// Inside the HomePage component

// ... existing code ...

const renderFilmProjects = () => (
  <SectionContainer>
    <SectionTitle
      subtitle="Showcasing my work in film and visual effects"
    >
      Film Projects
    </SectionTitle>

    <div className="space-y-6">
      {filmProjectsData.map((project, index) => (
        <ProjectCard
          key={index}
          {...project}
        />
      ))}
    </div>
  </SectionContainer>
);

const renderContent = () => {
  switch (activeSection) {
    case 'summary':
      return renderSummary();
    case 'skills':
      return renderSkills();
    case 'experience':
      return renderExperience();
    case 'personal':
      return renderPersonal();
    case 'film':
      return renderFilmProjects();
    default:
      return null;
  }
};

// ... rest of the code ...

// Define filmProjectsData
const filmProjectsData = [
  {
    title: "Proud Princess",
    description: "Lead compositor on the feature film Proud Princess.",
    duration: "September 2024 - October 2024",
    icon: Film,
    variant: 'featured',
    technologies: ["Nuke", "DaVinci Resolve", "Mocha Pro"],
    details: (
      <div className="space-y-4">
        <p>
          Worked as the lead compositor, overseeing the visual effects compositing for key sequences.
        </p>
        <ul className="list-disc pl-5">
          <li>Led a team of compositors</li>
          <li>Ensured visual consistency across shots</li>
          <li>Optimized the compositing pipeline</li>
        </ul>
      </div>
    ),
    media: [
      // Add your media files here
    ],
    mediaType: 'image', // or 'video'
  },
  // Add more projects as needed
];
