const SkillCard = ({ 
  title, 
  icon: Icon, 
  skills, 
  description,
  proficiency,
  projects,
  tools
}) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [ref, isInView] = useInView({ threshold: 0.2 });

  const progressBarRef = useRef(null);
  const progressValue = useMotionValue(0);
  const progressPercent = useTransform(progressValue, [0, 100], ['0%', '100%']);

  useEffect(() => {
    if (isInView && proficiency) {
      progressValue.set(proficiency);
    }
  }, [isInView, proficiency, progressValue]);

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
      transition={{ duration: 0.5 }}
      className="bg-white border border-gray-200 rounded-xl overflow-hidden"
    >
      <motion.div
        className="p-6 cursor-pointer"
        onClick={() => setIsExpanded(!isExpanded)}
      >
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            {Icon && (
              <div className="p-2 bg-gray-100 rounded-lg">
                <Icon size={24} className="text-gray-600" />
              </div>
            )}
            <h3 className="text-xl font-semibold text-gray-900">{title}</h3>
          </div>
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="text-gray-500"
          >
            {isExpanded ? (
              <Minimize size={20} />
            ) : (
              <Maximize size={20} />
            )}
          </motion.button>
        </div>

        <p className="mt-2 text-gray-600">{description}</p>

        {/* Proficiency */}
        {proficiency && (
          <div className="mt-4">
            <div className="flex justify-between items-center mb-2">
              <span className="text-sm text-gray-500">Proficiency</span>
              <span className="text-sm font-medium text-gray-700">{proficiency}%</span>
            </div>
            <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
              <motion.div
                ref={progressBarRef}
                className="h-full bg-gray-900"
                style={{ width: progressPercent }}
                transition={{ duration: 1, ease: "easeOut" }}
              />
            </div>
          </div>
        )}

        {/* Expanded Content */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3 }}
              className="mt-6 overflow-hidden"
            >
              {/* Skills Grid */}
              <div className="grid grid-cols-2 gap-4">
                {skills.map((skill, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.1 }}
                    className="flex items-center space-x-2"
                  >
                    <div className="w-2 h-2 bg-gray-900 rounded-full" />
                    <span className="text-gray-700">{skill}</span>
                  </motion.div>
                ))}
              </div>

              {/* Tools Section */}
              {tools && (
                <div className="mt-6">
                  <h4 className="text-sm font-medium text-gray-700 mb-3">
                    Tools & Software
                  </h4>
                  <div className="flex flex-wrap gap-2">
                    {tools.map((tool, index) => (
                      <span
                        key={index}
                        className="px-3 py-1 bg-gray-100 rounded-full text-sm text-gray-700"
                      >
                        {tool}
                      </span>
                    ))}
                  </div>
                </div>
              )}

              {/* Related Projects */}
              {projects && (
                <div className="mt-6">
                  <h4 className="text-sm font-medium text-gray-700 mb-3">
                    Related Projects
                  </h4>
                  <div className="grid gap-2">
                    {projects.map((project, index) => (
                      <motion.a
                        key={index}
                        href={project.link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="flex items-center space-x-2 text-gray-600 hover:text-gray-900"
                        whileHover={{ x: 5 }}
                      >
                        <ArrowRight size={16} />
                        <span>{project.name}</span>
                      </motion.a>
                    ))}
                  </div>
                </div>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </motion.div>
  );
};

