        {/* Glitch Art */}
        <ProjectCard
          title="Experimental Glitch Art"
          description="Digital art exploring data manipulation and visual glitches."
          icon={ImageIcon} // Correct usage
          technologies={["Processing", "Python", "Custom Tools"]}
          proficiency={85}
          skills={[
            "Custom Processing Scripts",
            "Real-time Glitch Effects",
            "Data Manipulation",
            "Generative Art",
            "Sound Reactive Visuals",
            "Mixed Media Integration"
          ]}
          tools={["Processing", "Python", "TouchDesigner"]}
          projects={[
            { name: "Glitch Series 1", link: "https://example.com/glitch-series-1" },
            { name: "Data Distortion", link: "https://example.com/data-distortion" }
          ]}
          media={glitchImages}
          mediaType="image"
          variant="featured"
          link="https://example.com/experimental-glitch-art"
        />

        {/* Analog Photography */}
        <ProjectCard
          title="Analog Photography"
          description="Film photography and darkroom experiments."
          icon={Camera}
          technologies={["Film", "Darkroom", "Alternative Process"]}
          proficiency={80}
          skills={[
            "Medium and Large Format Photography",
            "Alternative Development Processes",
            "Mixed Media Experiments",
            "Film Grain Manipulation",
            "Color Timelapse",
            "Cinematic Storytelling"
          ]}
          tools={["Film Cameras", "Darkroom Equipment", "Alternative Processes"]}
          projects={[
            { name: "Film Noir Series", link: "https://example.com/film-noir-series" },
            { name: "Color Timelapse", link: "https://example.com/color-timelapse" }
          ]}
          media={analogImages}
          mediaType="image"
          variant="default"
          link="https://example.com/analog-photography"
        />

        {/* VJ Performance Tools */}
        <ProjectCard
          title="VJ Performance Tools"
          description="Custom tools for live visual performance."
          icon={Play}
          technologies={["TouchDesigner", "Resolume Arena", "MIDI"]}
          proficiency={75}
          skills={[
            "Real-time Visual Effects",
            "Audio Reactive Visuals",
            "Custom MIDI Mappings",
            "Interactive Installations",
            "Performance Optimization",
            "Tool Development"
          ]}
          tools={["TouchDesigner", "Resolume Arena", "MIDI Controllers"]}
          projects={[
            { name: "Live VJ Toolkit", link: "https://example.com/live-vj-toolkit" },
            { name: "Interactive Installations", link: "https://example.com/interactive-installations" }
          ]}
          media={commercialImages}
          mediaType="image"
          variant="outline"
          link="https://example.com/vj-performance-tools"
        />

        {/* Experimental Animation */}
        <ProjectCard
          title="Motion Studies"
          description="Abstract animation experiments."
          icon={Clapperboard}
          technologies={["After Effects", "Cinema 4D", "Processing"]}
          proficiency={70}
          skills={[
            "Procedural Animation",
            "Generative Art Techniques",
            "Sound Visualization",
            "Abstract Storytelling",
            "Cinematic Techniques",
            "Visual Rhythm"
          ]}
          tools={["After Effects", "Cinema 4D", "Processing"]}
          projects={[
            { name: "Abstract Rhythms", link: "https://example.com/abstract-rhythms" },
            { name: "Visual Pulse", link: "https://example.com/visual-pulse" }
          ]}
          media={glitchImages}
          mediaType="image"
          videoUrl="https://player.vimeo.com/video/123456789"
          variant="default"
          link="https://example.com/motion-studies"
        />
      </div>
    </Section>
  );

  // Define content renderers based on active section
  const renderContent = () => {
    switch (activeSection) {
      case 'summary':
        return renderSummary();
      case 'film':
        return renderFilmProjects();
      case 'commercial':
        return renderCommercialWork();
      case 'experience':
        return (
          <Section
            title="Professional Experience"
            subtitle="Career highlights and achievements"
          >
            <div className="space-y-8">
              {experienceData.film.concat(experienceData.commercial)
                .sort((a, b) => new Date(b.duration.split(' - ')[0]) - new Date(a.duration.split(' - ')[0]))
                .map((exp, index) => (
                  <ProjectCard
                    key={index}
                    {...exp}
                    className="border-l-4 border-gray-800"
                  />
                ))}
            </div>
          </Section>
        );
      case 'skills':
        return renderSkills();
      case 'personal':
        return renderPersonal();
      case 'experimental':
        return (
          <Section
            title="Experimental Work"
            subtitle="Pushing boundaries and exploring new techniques"
          >
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {experienceData.personal
                .filter(project => project.category === 'Experimental')
                .map((project, index) => (
                  <ProjectCard
                    key={index}
                    {...project}
                    variant="featured"
                  />
                ))}
            </div>
          </Section>
        );
      default:
        return null;
    }
  };

  // Render loading screen if loading
  if (isLoading) {
    return (
      <LoadingSpinner />
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col md:flex-row">
      {/* Sidebar */}
      <motion.nav
        variants={animationVariants.slideInRight}
        initial="initial"
        animate="animate"
        exit="exit"
        className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r 
          border-gray-200 p-6 md:h-screen md:sticky md:top-0"
      >
        <div className="flex flex-col h-full">
          {/* Profile Section */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="mb-6"
          >
            <div className="relative w-20 h-20 mb-4">
              <img
                src={profilePicture}
                alt="Profile"
                className="rounded-full object-cover w-full h-full"
              />
              <motion.div
                className="absolute -inset-0.5 rounded-full bg-gradient-to-r 
                  from-black to-gray-600 opacity-0"
                whileHover={{ opacity: 0.2 }}
              />
            </div>
            <h2 className="text-2xl font-bold text-gray-900">Martin Tomek</h2>
            <p className="text-gray-500">VFX Artist & Motion Designer</p>
          </motion.div>

          {/* Contact Info */}
          <motion.div
            variants={animationVariants.container}
            initial="hidden"
            animate="show"
            className="mb-6 space-y-2"
          >
            <ContactInfo icon={MapPin} text="Prague, Czech Republic" href="#" />
            <ContactInfo
              icon={Mail}
              text="martintomek.vfx@gmail.com"
              href="mailto:martintomek.vfx@gmail.com"
            />
            <ContactInfo
              icon={Phone}
              text="+420 774 630 132"
              href="tel:+420774630132"
            />
            <ContactInfo
              icon={Github}
              text="github.com/Themolx"
              href="https://github.com/Themolx"
            />
            <ContactInfo
              icon={Linkedin}
              text="LinkedIn Profile"
              href="https://www.linkedin.com/in/martin-tomek"
            />
          </motion.div>

          {/* Navigation */}
          <Navigation
            activeSection={activeSection}
            setActiveSection={setActiveSection}
          />

          {/* Social Links */}
          <div className="mt-6">
            <div className="flex space-x-4">
              <motion.a
                href="https://github.com/Themolx"
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-600 hover:text-black transition-colors"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <Github size={20} />
                <span className="sr-only">GitHub</span>
              </motion.a>
              <motion.a
                href="https://www.linkedin.com/in/martin-tomek"
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-600 hover:text-black transition-colors"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <Linkedin size={20} />
                <span className="sr-only">LinkedIn</span>
              </motion.a>
              <motion.a
                href="mailto:martintomek.vfx@gmail.com"
                className="text-gray-600 hover:text-black transition-colors"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <Mail size={20} />
                <span className="sr-only">Email</span>
              </motion.a>
            </div>
          </div>

          {/* Footer */}
          <motion.div
            variants={animationVariants.item}
            initial="hidden"
            animate="show"
            transition={{ delay: 0.5 }}
            className="mt-auto pt-6 border-t border-gray-200"
          >
            <p className="text-sm text-gray-500">Available for new opportunities</p>
            <p className="text-sm text-gray-500 mt-1">
              Willing to relocate to Canary Islands
            </p>
            <motion.a
              href="#"
              className="flex items-center mt-4 text-gray-600 hover:text-black 
                transition-colors group"
              whileHover={{ x: 5 }}
            >
              <Download size={16} className="mr-2 group-hover:scale-110 transition-transform" />
              <span className="text-sm">Download Resume</span>
            </motion.a>
          </motion.div>
        </div>
      </motion.nav>

      {/* Main Content */}
      <main
        className="flex-1 p-4 md:p-8 overflow-y-auto"
      >
        <AnimatePresence mode="wait">
          {renderContent()}
        </AnimatePresence>
      </main>

      {/* Mobile Menu Button */}
      <motion.button
        className="md:hidden fixed bottom-8 left-8 p-3 bg-black text-white 
          rounded-full shadow-lg hover:bg-gray-800 z-40"
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={() => setIsMobileMenuOpen(true)}
      >
        <Menu size={24} />
      </motion.button>

      {/* Mobile Navigation */}
      <MobileNavigation
        isOpen={isMobileMenuOpen}
        setIsOpen={setIsMobileMenuOpen}
      >
        <Navigation
          activeSection={activeSection}
          setActiveSection={(section) => {
            setActiveSection(section);
            setIsMobileMenuOpen(false);
          }}
          isMobile
        />
      </MobileNavigation>

      {/* Scroll to Top Button */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            className="fixed bottom-8 right-8 p-3 bg-black text-white rounded-full 
              shadow-lg hover:bg-gray-800 z-40"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <ChevronRight className="transform -rotate-90" size={24} />
          </motion.button>
        )}
      </AnimatePresence>

      {/* Cursor Highlight Effect */}
      <CursorHighlight />
    </div>
  );
};

export default App;
