import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { getProductThumb, placeholderVideo } from '../../utils/placeholders';

function ProductCard({ product, badge }) {
  const [isHovered, setIsHovered] = useState(false);
  const thumbnail = product.thumbnail || getProductThumb(product.id);

  return (
    <motion.div
      className="group relative rounded-2xl overflow-hidden bg-[#111111]"
      whileHover={{ y: -5 }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
    >
      {/* Badge */}
      {badge && (
        <div className="absolute top-4 right-4 z-10">
          <span className="px-3 py-1 rounded-full text-xs font-medium bg-purple-600 text-white">
            {badge}
          </span>
        </div>
      )}

      <div className="aspect-video relative overflow-hidden">
        {isHovered ? (
          <div className="w-full h-full bg-[#1a1a1a] animate-pulse flex items-center justify-center">
            <span className="text-white/50">Hover Preview</span>
          </div>
        ) : (
          <img 
            src={thumbnail}
            alt={product.title}
            className="w-full h-full object-cover"
            loading="lazy"
          />
        )}
      </div>

      <div className="p-6">
        <h3 className="text-xl font-medium mb-2">{product.title}</h3>
        <p className="text-gray-400 text-sm mb-4">{product.description}</p>

        {/* Software compatibility */}
        <div className="flex flex-wrap gap-2 mb-4">
          {product.software?.map(sw => (
            <span key={sw} className="px-2 py-1 bg-white/5 rounded-full text-xs">
              {sw}
            </span>
          ))}
        </div>

        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <span className="text-2xl font-bold">${product.salePrice || product.price}</span>
            {product.salePrice && (
              <span className="text-sm text-gray-400 line-through">${product.price}</span>
            )}
          </div>
          <button className="bg-white text-black px-6 py-2 rounded-full 
                         font-medium hover:bg-gray-100 transition-colors">
            Add to Cart
          </button>
        </div>
      </div>
    </motion.div>
  );
}

export default ProductCard;
