// pages/index.js
import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
  Linkedin, // Added LinkedIn icon
  X
} from 'lucide-react';

// Glitch Art Images (using raw GitHub URLs)
const glitchImages = [
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/1.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/2.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/3.png',
];

/**
 * FilmProjectCard Component
 * Enhanced with Framer Motion animations
 */
const FilmProjectCard = ({
  id,
  title,
  subtitle,
  description,
  details,
  isSelected,
  onSelect,
  onClose,
}) => {
  return (
    <motion.div
      layoutId={`card-${id}`}
      className={`cursor-pointer bg-white border border-gray-200 rounded-xl p-6 ${
        isSelected ? 'fixed inset-4 md:inset-20 z-50 overflow-y-auto' : 'hover:shadow-xl'
      }`}
      onClick={() => !isSelected && onSelect(id)}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
    >
      <motion.div layoutId={`content-${id}`}>
        <motion.h3 
          layoutId={`title-${id}`}
          className="text-xl font-semibold text-gray-900 mb-1"
        >
          {title}
        </motion.h3>
        <motion.p 
          layoutId={`subtitle-${id}`}
          className="text-sm text-gray-500 mb-2"
        >
          {subtitle}
        </motion.p>
        <motion.p 
          layoutId={`description-${id}`}
          className="text-gray-600"
        >
          {description}
        </motion.p>

        {isSelected && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.2 }}
            className="mt-4"
          >
            {details}
            <motion.button
              className="mt-4 px-4 py-2 bg-black text-white rounded-lg hover:bg-gray-800 transition-colors"
              onClick={(e) => {
                e.stopPropagation();
                onClose();
              }}
            >
              <X size={20} />
            </motion.button>
          </motion.div>
        )}
      </motion.div>
    </motion.div>
  );
};

// Main component modifications
const HomePage = () => {
  const [activeSection, setActiveSection] = useState('summary');
  const [selectedProjectId, setSelectedProjectId] = useState(null);

  // Film projects data
  const filmProjects = [
    {
      id: 'proud-princess',
      title: 'Proud Princess',
      subtitle: 'PFX Studio | September 2024 - October 2024',
      description: 'Compositing work on the feature film.',
      details: (
        <div className="space-y-2">
          <h4 className="font-semibold mb-2">Project Details:</h4>
          <p className="text-gray-700 leading-relaxed">
            Collaborated with the VFX team to integrate visual effects seamlessly into
            live-action footage, ensuring high-quality deliverables under tight deadlines.
          </p>
        </div>
      ),
    },
    {
      id: 'rosa-dara',
      title: 'Rosa & Dara a jejich velké letní dobrodružství',
      subtitle: 'Incognito Studio | August 2021 - Present',
      description: 'Compositor and developer for a 3D feature film.',
      details: (
        <div className="space-y-2">
          <h4 className="font-semibold mb-2">Key Responsibilities:</h4>
          <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
            <li>Lead compositor for character integration scenes</li>
            <li>Developed custom Nuke tools for pipeline optimization</li>
            <li>Collaborated with 3D artists for seamless integration</li>
          </ul>
        </div>
      ),
    },
  ];

  // Animation variants for page transitions
  const pageTransition = {
    initial: { opacity: 0, y: 20 },
    animate: { opacity: 1, y: 0 },
    exit: { opacity: 0, y: -20 },
  };

  // Modified renderContent function - showing only the film projects section modification
  const renderContent = () => {
    switch (activeSection) {
      case 'film':
        return (
          <motion.div
            variants={pageTransition}
            initial="initial"
            animate="animate"
            exit="exit"
            className="space-y-6"
          >
            <h1 className="text-4xl font-bold text-gray-900 mb-4">Film Projects</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <AnimatePresence>
                {filmProjects.map((project) => (
                  <FilmProjectCard
                    key={project.id}
                    {...project}
                    isSelected={selectedProjectId === project.id}
                    onSelect={setSelectedProjectId}
                    onClose={() => setSelectedProjectId(null)}
                  />
                ))}
              </AnimatePresence>
            </div>
            {selectedProjectId && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="fixed inset-0 bg-black bg-opacity-50 z-40"
                onClick={() => setSelectedProjectId(null)}
              />
            )}
          </motion.div>
        );
      // Other cases remain the same...
    }
  };

  // NavItem component enhanced with animations
  const NavItem = ({ id, icon: Icon, label }) => (
    <motion.button
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
      onClick={() => setActiveSection(id)}
      className={`w-full flex items-center px-4 py-3 rounded-lg transition-colors duration-500 ease-in-out ${
        activeSection === id
          ? 'bg-black text-white'
          : 'text-gray-600 hover:text-black hover:bg-gray-100'
      }`}
    >
      <Icon size={20} className="min-w-[20px]" />
      <span className="ml-3 text-sm font-medium whitespace-nowrap">{label}</span>
    </motion.button>
  );

  // Rest of the component remains the same...

  return (
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="min-h-screen bg-gray-50 flex flex-col md:flex-row"
    >
      {/* Sidebar */}
      <motion.nav
        initial={{ x: -50, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ delay: 0.2 }}
        className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r border-gray-200 p-6"
      >
        {/* Sidebar content remains the same... */}
      </motion.nav>

      {/* Main Content */}
      <main className="flex-1 p-4 md:p-8 overflow-y-auto pb-[600px]">
        <AnimatePresence mode="wait">
          {renderContent()}
        </AnimatePresence>
      </main>
    </motion.div>
  );
};

export default HomePage;
