import React, { useState, useEffect, useRef } from "react";
import { motion, useAnimation, AnimatePresence } from "framer-motion";
import { useDrag } from "react-use-gesture";
import useSound from "use-sound";
import popSound from "../../assets/sounds/pop.mp3"; // Ensure you have a pop sound effect
import hoverSound from "../../assets/sounds/hover.mp3";

export const BlobDemo = () => {
  const [isActive, setIsActive] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const blobRef = useRef(null);
  const controls = useAnimation();

  // Sound effects
  const [playPop] = useSound(popSound, { volume: 0.5 });
  const [playHover] = useSound(hoverSound, { volume: 0.3 });

  // Handle draggable blobs
  const bind = useDrag(({ offset: [x, y] }) => {
    controls.start({
      x,
      y,
      transition: { type: "spring", stiffness: 300, damping: 30 }
    });
  });

  // Handle mouse move for particle trails
  const handleMouseMove = (e) => {
    const { clientX, clientY } = e;
    setMousePosition({ x: clientX, y: clientY });
  };

  // Dynamic background animation
  const backgroundVariants = {
    animate: {
      background: [
        "linear-gradient(45deg, #1D2B64, #F8CDDA)",
        "linear-gradient(135deg, #F8CDDA, #1D2B64)",
      ],
      transition: {
        duration: 10,
        repeat: Infinity,
        repeatType: "reverse"
      }
    }
  };

  return (
    <motion.div
      className="min-h-screen flex flex-wrap gap-8 justify-center items-center p-8"
      variants={backgroundVariants}
      animate="animate"
      onMouseMove={handleMouseMove}
    >
      {/* Psychedelic 3D Rotating Blob */}
      <motion.div
        {...bind()}
        className="w-64 h-64 relative cursor-grab"
        ref={blobRef}
        animate={controls}
        whileTap={{ cursor: "grabbing" }}
        onClick={() => {
          playPop();
          setIsActive(!isActive);
        }}
      >
        {/* Multiple Layers for 3D Effect */}
        {[...Array(6)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute inset-0 rounded-full mix-blend-overlay"
            style={{
              background: `radial-gradient(circle at ${30 +
                i * 10}%, rgba(${Math.floor(
                Math.random() * 255
              )}, ${Math.floor(Math.random() * 255)}, ${Math.floor(
                Math.random() * 255
              )}, 0.6) 0%, transparent 70%)`
            }}
            animate={{
              rotate: [0, 360],
              scale: [1, 1.2, 1],
            }}
            transition={{
              duration: 20 - i * 2,
              repeat: Infinity,
              ease: "linear",
            }}
          />
        ))}
        <span className="absolute inset-0 flex items-center justify-center text-white font-extrabold text-xl z-10">
          {isActive ? "Unleashed!" : "Click Me!"}
        </span>
      </motion.div>

      {/* Interactive Particle Blob */}
      <motion.div
        className="w-64 h-64 bg-gradient-to-tr from-teal-400 to-blue-500 rounded-full relative overflow-hidden"
        onMouseMove={(e) => {
          const { currentTarget, clientX, clientY } = e;
          const { left, top } = currentTarget.getBoundingClientRect();
          setMousePosition({
            x: clientX - left,
            y: clientY - top,
          });
        }}
        whileHover="hover"
        onHoverStart={() => playHover()}
      >
        {/* Particle Trails */}
        <AnimatePresence>
          <motion.div
            className="absolute w-3 h-3 bg-white rounded-full"
            initial={{ opacity: 0, x: mousePosition.x, y: mousePosition.y }}
            animate={{
              opacity: [1, 0],
              x: mousePosition.x + (Math.random() - 0.5) * 100,
              y: mousePosition.y + (Math.random() - 0.5) * 100,
              scale: [1, 0.5],
            }}
            exit={{ opacity: 0 }}
            transition={{
              duration: 1,
              ease: "easeOut",
            }}
            key={`${mousePosition.x}-${mousePosition.y}-${Date.now()}`}
          />
        </AnimatePresence>
        <span className="absolute inset-0 flex items-center justify-center text-white font-semibold text-lg z-10">
          Hover Over Me
        </span>
      </motion.div>

      {/* Draggable Morphing Chaos Button */}
      <motion.button
        className="relative w-64 h-64 bg-gradient-to-br from-green-400 to-yellow-500 rounded-full overflow-hidden cursor-pointer"
        {...bind()}
        animate={controls}
        whileTap={{ cursor: "grabbing" }}
        onClick={() => {
          playPop();
          setIsActive(!isActive);
        }}
      >
        {/* Morphing Shapes */}
        <motion.div
          className="absolute inset-0"
          animate={{
            borderRadius: isActive
              ? ["20% 80% 80% 20%", "80% 20% 20% 80%", "50% 50% 50% 50%"]
              : ["50% 50% 50% 50%", "20% 80% 80% 20%", "80% 20% 20% 80%"],
            rotate: isActive ? 720 : 0,
          }}
          transition={{
            duration: 5,
            repeat: Infinity,
            ease: "easeInOut",
          }}
          style={{
            background: "rgba(255, 255, 255, 0.2)",
          }}
        />

        {/* Particle Explosion */}
        <AnimatePresence>
          {isActive &&
            Array.from({ length: 20 }).map((_, i) => (
              <motion.div
                key={i}
                className="absolute w-2 h-2 bg-white rounded-full"
                initial={{ opacity: 1, x: "50%", y: "50%" }}
                animate={{
                  x: `${Math.random() * 200 - 100}%`,
                  y: `${Math.random() * 200 - 100}%`,
                  scale: 0,
                  opacity: 0,
                }}
                transition={{
                  duration: 1,
                  delay: i * 0.05,
                  ease: "easeOut",
                }}
              />
            ))}
        </AnimatePresence>

        {/* Text */}
        <motion.span
          className="absolute inset-0 flex items-center justify-center text-white font-bold text-lg z-10"
          animate={{
            scale: isActive ? [1, 1.5, 1] : 1,
          }}
          transition={{
            duration: 0.5,
            repeat: isActive ? Infinity : 0,
          }}
        >
          {isActive ? "Chaos Unleashed!" : "Unleash Chaos"}
        </motion.span>
      </motion.button>

      {/* Neon Ripple Effect */}
      <motion.div
        className="absolute inset-0 pointer-events-none"
        variants={{
          ripple: {
            scale: [1, 2, 1],
            opacity: [0.5, 0, 0.5],
          }
        }}
        animate="ripple"
        transition={{
          duration: 4,
          repeat: Infinity,
          ease: "easeInOut",
        }}
      >
        <div className="absolute top-0 left-0 w-full h-full border-4 border-neonblue rounded-full"></div>
      </motion.div>
    </motion.div>
  );
};
