import { motion, useAnimation } from 'framer-motion';
import { useState } from 'react';

export const Example = () => {
  const [activeDemo, setActiveDemo] = useState(null);
  const controls = useAnimation();

  const demos = [
    {
      title: "Chain Reaction ðŸ”„",
      color: "bg-gradient-to-r from-blue-500 to-teal-500",
      content: (
        <div className="grid grid-cols-4 gap-2 p-4">
          {[...Array(16)].map((_, i) => {
            const row = Math.floor(i / 4);
            const col = i % 4;

            return (
              <motion.div
                key={i}
                className="aspect-square bg-white/20 rounded-lg cursor-pointer"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={async () => {
                  // Trigger clicked cell
                  controls.start(`cell${i}`, {
                    scale: [1, 1.4, 1],
                    rotate: 360,
                    transition: { duration: 0.5 }
                  });

                  // Get neighboring cells
                  const neighbors = [];
                  if (row > 0) neighbors.push(i - 4); // top
                  if (row < 3) neighbors.push(i + 4); // bottom
                  if (col > 0) neighbors.push(i - 1); // left
                  if (col < 3) neighbors.push(i + 1); // right

                  // Animate neighbors with delay
                  neighbors.forEach((n, index) => {
                    controls.start(`cell${n}`, {
                      scale: [1, 1.2, 1],
                      rotate: 180,
                      transition: { 
                        duration: 0.3,
                        delay: 0.1 * (index + 1)
                      }
                    });
                  });
                }}
                animate={controls}
                custom={`cell${i}`}
              />
            );
          })}
        </div>
      )
    },
    {
      title: "Particle Field ðŸŒŸ",
      color: "bg-gradient-to-r from-purple-500 to-pink-500",
      content: (
        <div className="relative h-[300px] bg-black/20 rounded-lg overflow-hidden">
          {[...Array(30)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute w-2 h-2 bg-white/40 rounded-full"
              animate={{
                x: [0, Math.random() * 400 - 200],
                y: [0, Math.random() * 300 - 150],
                scale: [1, Math.random() + 0.5, 1],
                opacity: [0.4, 1, 0.4],
              }}
              transition={{
                duration: 2 + Math.random() * 2,
                repeat: Infinity,
                repeatType: "reverse",
                ease: "easeInOut"
              }}
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
              }}
              whileHover={{
                scale: 2,
                backgroundColor: "rgba(255,255,255,0.8)",
              }}
            />
          ))}
        </div>
      )
    }
    // ... other demos
  ];

  return (
    <div className="flex flex-col items-center justify-center min-h-[600px] p-8">
      <div className="grid grid-cols-2 gap-4 mb-8 w-full max-w-2xl">
        {demos.map((demo, index) => (
          <motion.button
            key={index}
            className={`${demo.color} p-6 rounded-xl text-white font-bold text-lg`}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => setActiveDemo(activeDemo === index ? null : index)}
          >
            {demo.title}
          </motion.button>
        ))}
      </div>

      <motion.div
        layout
        className="w-full max-w-2xl"
      >
        {activeDemo !== null && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className={`${demos[activeDemo].color} p-8 rounded-xl`}
          >
            {demos[activeDemo].content}
          </motion.div>
        )}
      </motion.div>
    </div>
  );
};
