import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import HeroSection from './components/home/HeroSection';
import FeaturedProducts from './components/home/FeaturedProducts';
import LatestBlogPosts from './components/home/LatestBlogPosts';
import NewsletterSignup from './components/home/NewsletterSignup';
import ProductCard from './components/shop/ProductCard';
import BlogPost from './components/blog/BlogPost';
import PortfolioItem from './components/portfolio/PortfolioItem';

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <Navbar />
        <main className="container mx-auto px-4 py-8">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/shop" element={<Shop />} />
            <Route path="/blog" element={<Blog />} />
            <Route path="/portfolio" element={<Portfolio />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

// Home component with featured sections
function Home() {
  return (
    <div className="space-y-12">
      <HeroSection />
      <FeaturedProducts />
      <LatestBlogPosts />
      <NewsletterSignup />
    </div>
  );
}

// Shop component for your digital products
function Shop() {
  const products = [
    {
      id: 1,
      title: "Cyberpunk Glitch Art Collection",
      price: 29.99,
      category: "Glitch Art",
      gumroadLink: "https://gumroad.com/your-product-1"
    },
    {
      id: 2,
      title: "Minimal Website Template",
      price: 49.99,
      category: "Templates",
      gumroadLink: "https://gumroad.com/your-product-2"
    },
    // Add more products...
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {products.map(product => (
        <ProductCard key={product.id} product={product} />
      ))}
    </div>
  );
}

// Blog component
function Blog() {
  const posts = [
    {
      id: 1,
      title: "Creating Glitch Art: A Beginner's Guide",
      excerpt: "Learn the basics of creating stunning glitch art...",
      date: "2024-03-20"
    },
    // Add more blog posts...
  ];

  return (
    <div className="max-w-4xl mx-auto space-y-8">
      {posts.map(post => (
        <BlogPost key={post.id} post={post} />
      ))}
    </div>
  );
}

// Portfolio component to showcase your work
function Portfolio() {
  const works = [
    {
      id: 1,
      title: "Neon Dreams",
      category: "Glitch Art",
      image: "/path/to/image.jpg"
    },
    // Add more portfolio items...
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
      {works.map(work => (
        <PortfolioItem key={work.id} work={work} />
      ))}
    </div>
  );
}

export default App;
