import React, { useState } from 'react';
import { Clapperboard, Code, Film, User } from 'lucide-react';

const App = () => {
  const [activeSection, setActiveSection] = useState('compositing');

  const NavItem = ({ id, icon: Icon, label }) => (
    <button
      onClick={() => setActiveSection(id)}
      className={`w-full flex items-center px-4 py-3 rounded-lg transition-all duration-200 hover:bg-gray-100 ${
        activeSection === id 
          ? 'bg-black text-white hover:bg-black/90' 
          : 'text-gray-600 hover:text-black'
      }`}
    >
      <Icon size={20} className="min-w-[20px]" />
      <span className="ml-3 text-sm font-medium whitespace-nowrap">{label}</span>
    </button>
  );

  const ProjectCard = ({ title, description }) => (
    <div className="bg-white border border-gray-200 rounded-xl p-6 hover:shadow-lg transition-shadow duration-200">
      <h3 className="text-xl font-semibold text-gray-900 mb-2">{title}</h3>
      <p className="text-gray-600">{description}</p>
    </div>
  );

  const sections = [
    { id: 'compositing', icon: Clapperboard, label: 'Compositing' },
    { id: 'technical', icon: Code, label: 'Technical' },
    { id: 'motion', icon: Film, label: 'Motion' },
    { id: 'personal', icon: User, label: 'Personal' }
  ];

  const renderContent = () => {
    switch (activeSection) {
      case 'compositing':
        return (
          <div className="space-y-8">
            <div>
              <h1 className="text-4xl font-bold text-gray-900 mb-4">Compositing</h1>
              <p className="text-xl text-gray-600 max-w-2xl">
                Specialized in creating seamless visual effects and integrations using Nuke. 
                Extensive experience in film production and advertising projects.
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              <ProjectCard 
                title="Feature Film VFX" 
                description="Led compositing team for major Hollywood production, delivering 200+ shots including complex environment extensions and character enhancements."
              />
              <ProjectCard 
                title="Commercial Portfolio" 
                description="Created high-end visual effects for leading brands including Nike, Apple, and Samsung, focusing on product integration and enhancement."
              />
              <ProjectCard 
                title="Music Video Effects" 
                description="Developed innovative visual effects solutions for award-winning music videos, including particle systems and digital environment creation."
              />
            </div>
          </div>
        );
      default:
        return (
          <div className="flex items-center justify-center h-[calc(100vh-4rem)]">
            <p className="text-gray-500 text-lg">Select a section from the menu</p>
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Sidebar */}
      <nav className="fixed top-0 left-0 h-full w-64 bg-white border-r border-gray-200 p-6">
        <div className="h-full flex flex-col">
          {/* Logo */}
          <div className="mb-8">
            <h2 className="text-2xl font-bold text-gray-900">MT</h2>
            <p className="text-sm text-gray-500 mt-1">Visual Effects Artist</p>
          </div>

          {/* Navigation */}
          <div className="space-y-2">
            {sections.map((section) => (
              <NavItem
                key={section.id}
                id={section.id}
                icon={section.icon}
                label={section.label}
              />
            ))}
          </div>

          {/* Footer */}
          <div className="mt-auto pt-6 border-t border-gray-200">
            <p className="text-sm text-gray-500">Â© 2024 MT Studio</p>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="ml-64 p-8">
        {renderContent()}
      </main>
    </div>
  );
};

export default App;
