import React from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import DownloadButton from '../components/blog/DownloadButton';

function BlogPost() {
  const { slug } = useParams();

  const blogPosts = {
    'nuke-advanced-grab-tool': {
      title: "ðŸš€ From Frustration to Innovation: The Birth of Nuke's Advanced Grab Tool",
      date: "November 9, 2024",
      author: "Martin Tomek",
      tags: ["Nuke", "Python", "Workflow", "Technical Guide"],
      content: [
        {
          type: 'intro',
          content: "Picture this: You're deep into a complex comp, your Node Graph looks like a spider's web after too much coffee, and you're playing that familiar game of 'drag and pray' with your nodes. We've all been there. That's exactly why I created the Advanced Grab Tool - because life's too short for messy node trees! ðŸ•¸ï¸"
        },
        {
          type: 'heading',
          content: "The Problem: Node Graph Chaos ðŸ˜±"
        },
        {
          type: 'paragraph',
          content: "Every Nuke artist knows the pain - you've got a perfect node tree, then a client request comes in. Suddenly you're playing Tetris with nodes, trying not to break connections while reorganizing your comp. It's like performing surgery while juggling!"
        },
        {
          type: 'heading',
          content: "Enter the Grab Tool âœ¨"
        },
        {
          type: 'feature-list',
          content: [
            {
              shortcut: "E",
              name: "Standard Grab",
              description: "Just the nodes you want, nothing else. Clean and simple."
            },
            {
              shortcut: "Ctrl+E",
              name: "Input Tree Grab",
              description: "Like telling your nodes 'bring your friends along!'"
            },
            {
              shortcut: "Alt+Ctrl+E",
              name: "Full Tree Grab",
              description: "The 'everyone's invited to the party' option"
            }
          ]
        },
        {
          type: 'heading',
          content: "The AI-Powered Development Story ðŸ¤–"
        },
        {
          type: 'paragraph',
          content: "Here's where it gets interesting - I didn't build this alone. I teamed up with Claude AI, and our conversations went something like this:"
        },
        {
          type: 'dialogue',
          content: [
            {
              speaker: "Me",
              text: "Hey Claude, how do I make nodes move like in Blender?"
            },
            {
              speaker: "Claude",
              text: "Let's start with PySide2 for event handling. Think of it like giving your nodes dancing lessons..."
            }
          ]
        },
        {
          type: 'heading',
          content: "The GitHub Journey ðŸ“ˆ"
        },
        {
          type: 'timeline',
          content: [
            {
              date: "Oct 2",
              title: "First Steps",
              description: "Basic movement implemented - nodes finally following orders!"
            },
            {
              date: "Oct 3",
              title: "Growing Up",
              description: "Added tree selection - because no node should move alone"
            },
            {
              date: "Oct 6",
              title: "The Big Update",
              description: "Undo support added - because we all make mistakes!"
            }
          ]
        },
        {
          type: 'heading',
          content: "Try It Yourself! ðŸŽ®"
        },
        {
          type: 'steps',
          content: [
            "1. Download from Nukepedia",
            "2. Drop it in your .nuke folder",
            "3. Add to menu.py",
            "4. Restart Nuke",
            "5. Press E and watch the magic happen!"
          ]
        },
        {
          type: 'cta',
          content: {
            nukepedia: {
              text: "Get It On Nukepedia",
              url: "https://www.nukepedia.com/python/nodegraph/nuke-grab-tool"
            },
            github: {
              text: "View On GitHub",
              url: "https://github.com/Themolx/NukeScripts"
            }
          }
        }
      ]
    }
  };

  const post = blogPosts[slug];

  const renderContent = (content) => {
    switch (content.type) {
      case 'paragraph':
        return <p className="text-gray-300 leading-relaxed mb-6">{content.content}</p>;

      case 'heading':
        return <h2 className="text-2xl font-bold text-white mt-12 mb-6">{content.content}</h2>;

      case 'download-section':
        return (
          <div className="flex flex-col sm:flex-row gap-4 my-8">
            <DownloadButton 
              url={content.content.nukepedia}
              text="Download from Nukepedia"
            />
            <DownloadButton 
              url={content.content.github}
              text="View on GitHub"
            />
          </div>
        );

      case 'video-demo':
        return (
          <div className="aspect-video w-full rounded-lg overflow-hidden my-8">
            <iframe
              src={`https://www.youtube.com/embed/${content.videoId}`}
              className="w-full h-full"
              allowFullScreen
            />
          </div>
        );

      case 'steps':
        return (
          <div className="space-y-6 my-8">
            {content.content.map((step, index) => (
              <div key={index} className="bg-white/5 rounded-lg p-6">
                <h3 className="text-xl font-semibold text-purple-400 mb-3">
                  {step.title}
                </h3>
                <p className="text-gray-300 whitespace-pre-line">
                  {step.description}
                </p>
              </div>
            ))}
          </div>
        );

      case 'code-example':
        return (
          <div className="my-8">
            <h3 className="text-xl font-semibold mb-4">{content.content.title}</h3>
            <pre className="bg-black/50 p-4 rounded-lg overflow-x-auto">
              <code className="text-sm text-gray-300">
                {content.content.code}
              </code>
            </pre>
          </div>
        );

      case 'try-yourself':
        return (
          <div className="bg-purple-900/20 rounded-lg p-6 my-8">
            <h3 className="text-xl font-semibold mb-4">{content.content.title}</h3>
            <ul className="list-decimal list-inside space-y-2 mb-6">
              {content.content.steps.map((step, index) => (
                <li key={index} className="text-gray-300">{step}</li>
              ))}
            </ul>
            <pre className="bg-black/50 p-4 rounded-lg overflow-x-auto">
              <code className="text-sm text-gray-300">
                {content.content.sampleCode}
              </code>
            </pre>
          </div>
        );

      default:
        return null;
    }
  };

  if (!post) {
    return <div className="container mx-auto px-4 py-24">Post not found</div>;
  }

  return (
    <motion.article
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="max-w-4xl mx-auto px-4 py-24"
    >
      <div className="mb-8">
        <span className="text-purple-400">{post.date}</span>
        <h1 className="text-4xl font-bold mt-2 mb-4">{post.title}</h1>
        <div className="flex items-center gap-2 text-gray-400">
          <span>By {post.author}</span>
          <span>â€¢</span>
          <div className="flex gap-2">
            {post.tags.map(tag => (
              <span key={tag} className="text-sm bg-white/10 px-3 py-1 rounded-full">
                {tag}
              </span>
            ))}
          </div>
        </div>
      </div>

      <div className="prose prose-invert max-w-none">
        {post.content.map((section, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            {renderContent(section)}
          </motion.div>
        ))}
      </div>
    </motion.article>
  );
}

export default BlogPost;
