// pages/index.js
import React, { useState, useRef, useEffect } from 'react';
import {
  Film,
  Briefcase,
  User,
  Code,
  Clapperboard,
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

/**
 * ProjectCard Component
 * Handles individual project display, hover effects, and media controls.
 */
const ProjectCard = ({
  id,
  title,
  description,
  duration,
  moreInfo,
  media,
  mediaType, // 'video' or 'image'
  alwaysExpanded = false, // Controls expansion behavior
  className = '', // Allows additional class names for styling
  selectedId,
  setSelectedId,
}) => {
  const isExpandable = !!setSelectedId;
  const isExpanded = selectedId === id || alwaysExpanded;
  const mediaRefs = useRef([]);

  // Pause all media when not expanded
  useEffect(() => {
    if (!isExpanded && !alwaysExpanded) {
      mediaRefs.current.forEach((mediaElement) => {
        if (mediaElement) {
          if (mediaType === 'video') {
            mediaElement.pause();
          }
        }
      });
    }
  }, [isExpanded, alwaysExpanded, mediaType]);

  const handleClick = () => {
    if (isExpandable) {
      setSelectedId(isExpanded ? null : id);
    }
  };

  const CardContainer = isExpandable ? motion.div : 'div';

  return (
    <CardContainer
      layoutId={isExpandable ? id : undefined}
      onClick={handleClick}
      className={`bg-white border border-gray-200 rounded-xl p-6 hover:shadow-xl transform transition-transform duration-500 ease-in-out ${className} ${
        isExpandable ? 'cursor-pointer' : ''
      }`}
    >
      <div className="flex items-center">
        <div>
          <h3 className="text-xl font-semibold text-gray-900 mb-1">{title}</h3>
          {duration && <p className="text-sm text-gray-500 mb-1">{duration}</p>}
        </div>
      </div>
      <p className="text-gray-600 whitespace-pre-line mt-2">{description}</p>

      {/* Additional Content */}
      {isExpanded && moreInfo && (
        isExpandable ? (
          <AnimatePresence>
            <motion.div
              className="mt-4"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              {/* Display media based on type */}
              {media && (
                <div className="mb-4">
                  {mediaType === 'video' ? (
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {media.map((mediaSrc, index) => (
                        <motion.video
                          key={index}
                          src={mediaSrc}
                          controls
                          className="w-full rounded-lg"
                          ref={(el) => (mediaRefs.current[index] = el)}
                          layoutId={`${id}-media-${index}`}
                        />
                      ))}
                    </div>
                  ) : mediaType === 'image' ? (
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {media.map((mediaSrc, index) => (
                        <motion.img
                          key={index}
                          src={mediaSrc}
                          alt={`${title} ${index + 1}`}
                          className="w-full h-auto rounded-lg object-cover"
                          layoutId={`${id}-media-${index}`}
                        />
                      ))}
                    </div>
                  ) : null}
                </div>
              )}
              {/* Display additional information */}
              {moreInfo}
            </motion.div>
          </AnimatePresence>
        ) : (
          <div className="mt-4">
            {/* Display media based on type */}
            {media && (
              <div className="mb-4">
                {mediaType === 'video' ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {media.map((mediaSrc, index) => (
                      <video
                        key={index}
                        src={mediaSrc}
                        controls
                        className="w-full rounded-lg"
                        ref={(el) => (mediaRefs.current[index] = el)}
                      />
                    ))}
                  </div>
                ) : mediaType === 'image' ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {media.map((mediaSrc, index) => (
                      <img
                        key={index}
                        src={mediaSrc}
                        alt={`${title} ${index + 1}`}
                        className="w-full h-auto rounded-lg object-cover"
                      />
                    ))}
                  </div>
                ) : null}
              </div>
            )}
            {/* Display additional information */}
            {moreInfo}
          </div>
        )
      )}
    </CardContainer>
  );
};

/**
 * Main App Component
 */
const HomePage = () => {
  const [activeSection, setActiveSection] = useState('film');
  const [selectedId, setSelectedId] = useState(null);

  // Navigation items
  const sections = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'film', icon: Film, label: 'Film Projects' },
    { id: 'experience', icon: Briefcase, label: 'Experience' },
    { id: 'skills', icon: Code, label: 'Skills' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' },
  ];

  /**
   * Renders content based on the active section.
   */
  const renderContent = () => {
    switch (activeSection) {
      case 'film':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">Film Projects</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {[
                {
                  id: 'proud-princess',
                  title: 'Proud Princess',
                  duration: 'PFX Studio | September 2024 - October 2024',
                  description: '- Compositing work on the feature film.',
                  moreInfo: (
                    <div className="space-y-2">
                      <h4 className="font-semibold mb-2">Project Details:</h4>
                      <p className="text-gray-700 leading-relaxed">
                        Collaborated with the VFX team to integrate visual effects seamlessly into
                        live-action footage, ensuring high-quality deliverables under tight deadlines.
                      </p>
                    </div>
                  ),
                  media: null,
                  mediaType: null,
                },
                {
                  id: 'rosa-dara',
                  title: 'Rosa & Dara a jejich velké letní dobrodružství',
                  duration: 'Incognito Studio | August 2021 - Present',
                  description: '- Compositor and developer for a 3D feature film.',
