import React, { useState } from 'react';
import { Clapperboard, Code, Film, User } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

function App() {
  const [activeSection, setActiveSection] = useState('compositing');

  const NavItem = ({ id, icon, label }) => (
    <button
      onClick={() => setActiveSection(id)}
      className={`nav-item p-4 rounded-lg transition-colors duration-300 ${
        activeSection === id ? 'bg-black text-white' : 'bg-transparent text-black/60'
      }`}
    >
      {icon}
      <span className="ml-2 font-medium">{label}</span>
    </button>
  );

  const ProjectCard = ({ title, description }) => (
    <motion.div 
      initial={{ opacity: 0, y: 20 }} 
      animate={{ opacity: 1, y: 0 }} 
      exit={{ opacity: 0, y: -20 }}
      className="section-card p-6 rounded-lg shadow-md bg-black hover:bg-black/80 transition-all"
    >
      <h2 className="text-2xl font-semibold mb-2 text-black">{title}</h2>
      <p className="text-black/70">{description}</p>
    </motion.div>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'compositing':
        return (
          <AnimatePresence>
            <motion.div 
              key="compositing" 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              exit={{ opacity: 0 }}
            >
              <h1 className="text-4xl font-bold mb-6 text-black">Compositing</h1>
              <p className="text-xl text-black/80 mb-12 max-w-2xl">
                Specialized in creating seamless visual effects and integrations using Nuke. 
                Extensive experience in film production and advertising projects.
              </p>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                <ProjectCard 
                  title="Project Alpha" 
                  description="Visual effects for major film production" 
                />
                <ProjectCard 
                  title="Commercial Work" 
                  description="Commercial projects and advertising" 
                />
                <ProjectCard 
                  title="Film Integration" 
                  description="Seamless visual effects integration" 
                />
              </div>
            </motion.div>
          </AnimatePresence>
        );
      // ... other sections similar to compositing
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-white text-black">
      <nav className="fixed top-0 left-0 w-20 h-screen bg-white border-r border-black/10">
        <div className="flex flex-col items-center h-full py-8">
          <div className="text-3xl font-bold mb-12 text-black">MT</div>
          <div className="flex flex-col gap-4">
            <NavItem id="compositing" icon={<Clapperboard size={20} />} label="Compositing" />
            <NavItem id="technical" icon={<Code size={20} />} label="Technical" />
            <NavItem id="motion" icon={<Film size={20} />} label="Motion" />
            <NavItem id="personal" icon={<User size={20} />} label="Personal" />
          </div>
        </div>
      </nav>
      <main className="ml-20 p-8">
        {renderContent()}
      </main>
    </div>
  );
}

export default App;
