import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
  Linkedin,
  X,
  ExternalLink,
  ChevronUp,
} from 'lucide-react';

// Glitch Art Images
const glitchImages = [
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/1.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/2.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/3.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/4.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/6.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/7.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/9.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/12.png',
];

// Analog Photography Images
const analogImages = [
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/1.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/2.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/3.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/4.jpg',
];

// Profile picture
const profilePicture =
  'https://avatars.githubusercontent.com/u/183303841?s=400&u=15ae10f7b65b15a10c0ddd7a9d12efbb608014f4&v=4';

// Animation variants
const fadeInUp = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -20 },
  transition: { duration: 0.5 },
};

const stagger = {
  animate: {
    transition: {
      staggerChildren: 0.1,
    },
  },
};

const sidebar = {
  initial: { x: -50, opacity: 0 },
  animate: { x: 0, opacity: 1, transition: { duration: 0.5, delay: 0.2 } },
};

const expandAnimation = {
  initial: { height: 0, opacity: 0 },
  animate: {
    height: 'auto',
    opacity: 1,
    transition: {
      height: {
        duration: 0.4,
        ease: [0.04, 0.62, 0.23, 0.98],
      },
      opacity: { duration: 0.25 },
    },
  },
  exit: {
    height: 0,
    opacity: 0,
    transition: {
      height: { duration: 0.3 },
      opacity: { duration: 0.2 },
    },
  },
};

// Section Title Component
const SectionTitle = ({ children }) => (
  <motion.h1
    className="text-4xl font-bold text-gray-900 mb-8"
    variants={fadeInUp}
    initial="initial"
    animate="animate"
    exit="exit"
  >
    {children}
  </motion.h1>
);

// Enhanced ProjectCard Component
const ProjectCard = ({
  title,
  description,
  duration,
  moreInfo,
  media,
  mediaType,
  alwaysExpanded = false,
  className = '',
  isSelected,
  onSelect,
  onClose,
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);

  useEffect(() => {
    if (!isHovered && !alwaysExpanded) {
      const timer = setTimeout(() => setIsExpanded(false), 300);
      return () => clearTimeout(timer);
    } else {
      setIsExpanded(true);
    }
  }, [isHovered, alwaysExpanded]);

  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      whileHover={!isSelected && { scale: 1.02 }}
      transition={{ duration: 0.3 }}
      className={`bg-white border border-gray-200 rounded-xl p-6 ${className} ${
        !alwaysExpanded && !isSelected && 'cursor-pointer hover:shadow-lg'
      }`}
      onClick={() => !alwaysExpanded && !isSelected && onSelect && onSelect()}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {isSelected ? (
        // Render expanded layout
        <motion.div layout className="flex flex-col">
          {/* Media at the top */}
          {media && media.length > 0 && (
            <div className="mb-6">
              {mediaType === 'image' ? (
                <img
                  src={media[0]}
                  alt={title}
                  className="w-full h-auto object-cover rounded-lg"
                />
              ) : (
                <video src={media[0]} controls className="w-full rounded-lg" />
              )}
            </div>
          )}
          {/* Title and Close button */}
          <div className="flex justify-between items-start">
            <motion.h3 layout className="text-3xl font-semibold text-gray-900">
              {title}
            </motion.h3>
            {onClose && (
              <motion.button
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                onClick={(e) => {
                  e.stopPropagation();
                  onClose();
                }}
                className="p-2 hover:bg-gray-100 rounded-full"
              >
                <X size={24} />
              </motion.button>
            )}
          </div>
          {/* Duration */}
          {duration && (
            <motion.p layout className="text-sm text-gray-500 mt-1">
              {duration}
            </motion.p>
          )}
          {/* Description */}
          <motion.p
            layout
            className="text-gray-700 mt-4 leading-relaxed"
          >
            {description}
          </motion.p>
          {/* More Info */}
          {moreInfo && <div className="mt-6">{moreInfo}</div>}
          {/* Media (if more than one) */}
          {media && media.length > 1 && (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
              {media.slice(1).map((item, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: index * 0.1 }}
                  className="rounded-lg overflow-hidden"
                >
                  {mediaType === 'image' ? (
                    <img
                      src={item}
                      alt={`${title} ${index + 2}`}
                      className="w-full h-auto object-cover transform hover:scale-105 transition-transform duration-300"
                    />
                  ) : (
                    <video src={item} controls className="w-full" />
                  )}
                </motion.div>
              ))}
            </div>
          )}
        </motion.div>
      ) : (
        // Render compact layout
        <motion.div layout className="flex flex-col">
          <div className="flex justify-between items-start">
            <motion.div layout>
              <motion.h3
                layout
                className="text-xl font-semibold text-gray-900"
              >
                {title}
              </motion.h3>
              {duration && (
                <motion.p layout className="text-sm text-gray-500 mt-1">
                  {duration}
                </motion.p>
              )}
            </motion.div>
          </div>
          <motion.p layout className="text-gray-600 mt-4">
            {description}
          </motion.p>
        </motion.div>
      )}
    </motion.div>
  );
};

// NavItem Component
const NavItem = ({ id, icon: Icon, label, isActive, onClick }) => (
  <motion.button
    whileHover={{ scale: 1.02, x: 5 }}
    whileTap={{ scale: 0.98 }}
    onClick={onClick}
    className={`w-full flex items-center px-4 py-3 rounded-lg transition-colors duration-300 ${
      isActive
        ? 'bg-black text-white'
        : 'text-gray-600 hover:text-black hover:bg-gray-100'
    }`}
  >
    <Icon size={20} className="min-w-[20px]" />
    <span className="ml-3 text-sm font-medium">{label}</span>
  </motion.button>
);

// ContactInfo Component
const ContactInfo = ({ icon: Icon, text, href }) => (
  <motion.a
    whileHover={{ scale: 1.05, x: 5 }}
    whileTap={{ scale: 0.95 }}
    href={href}
    target="_blank"
    rel="noopener noreferrer"
    className="flex items-center text-gray-600 hover:text-black transition-colors"
  >
    <Icon size={16} className="mr-2" />
    <span className="text-sm">{text}</span>
  </motion.a>
);

// Main HomePage Component
const HomePage = () => {
  const [activeSection, setActiveSection] = useState('summary');
  const [selectedProjectId, setSelectedProjectId] = useState(null);

  // Navigation sections
  const sections = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'film', icon: Film, label: 'Film Projects' },
    { id: 'experience', icon: Briefcase, label: 'Experience' },
    { id: 'skills', icon: Code, label: 'Skills' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' },
  ];

  // Film projects data
  const filmProjects = [
    {
      id: 'proud-princess',
      title: 'Proud Princess',
      duration: 'PFX Studio | September 2024 - October 2024',
      description: 'Lead compositor for this major feature film project.',
      moreInfo: (
        <div className="space-y-4">
          <section>
            <h4 className="font-semibold text-lg mb-2">Project Overview</h4>
            <p className="text-gray-700">
              Collaborated with the VFX team to integrate visual effects
              seamlessly into live-action footage, ensuring high-quality
              deliverables under tight deadlines.
            </p>
          </section>
          <section>
            <h4 className="font-semibold text-lg mb-2">Key Responsibilities</h4>
            <ul className="list-disc pl-5 space-y-2 text-gray-700">
              <li>Led compositing team for key sequences</li>
              <li>Developed and maintained shot consistency</li>
              <li>Optimized workflow for tight delivery schedule</li>
              <li>Collaborated with directors for creative decisions</li>
            </ul>
          </section>
        </div>
      ),
      media: [
        'https://via.placeholder.com/800x400?text=Proud+Princess',
      ],
      mediaType: 'image',
    },
    {
      id: 'rosa-dara',
      title: 'Rosa & Dara',
      duration: 'Incognito Studio | August 2021 - Present',
      description:
        'Lead compositor and technical director for 3D animated feature film.',
      moreInfo: (
        <div className="space-y-4">
          <section>
            <h4 className="font-semibold text-lg mb-2">Project Scope</h4>
            <p className="text-gray-700">
              Full-length 3D animated feature film requiring complex character
              integration and extensive pipeline development for efficient
              workflow.
            </p>
          </section>
          <section>
            <h4 className="font-semibold text-lg mb-2">Technical Achievements</h4>
            <ul className="list-disc pl-5 space-y-2 text-gray-700">
              <li>Developed custom Nuke tools for pipeline optimization</li>
              <li>Implemented automated quality control systems</li>
              <li>Created render farm integration solutions</li>
              <li>Established collaborative workflow procedures</li>
            </ul>
          </section>
        </div>
      ),
      media: [
        'https://via.placeholder.com/800x400?text=Rosa+%26+Dara',
      ],
      mediaType: 'image',
    },
  ];

  // Experience data
  const experienceData = [
    // ... (same as before)
  ];

  // Skills data
  const skillsData = [
    // ... (same as before)
  ];

  // Render main content based on active section
  const renderContent = () => {
    switch (activeSection) {
      case 'summary':
        return (
          <motion.div
            variants={stagger}
            initial="initial"
            animate="animate"
            exit="exit"
            className="space-y-8"
          >
            {/* ... (same as before) */}
          </motion.div>
        );

      case 'film':
        return (
          <motion.div
            variants={stagger}
            initial="initial"
            animate="animate"
            exit="exit"
            className="space-y-6"
          >
            <SectionTitle>Film Projects</SectionTitle>

            <motion.div
              variants={fadeInUp}
              className="grid grid-cols-1 md:grid-cols-2 gap-6"
            >
              <AnimatePresence>
                {filmProjects.map((project) => (
                  <motion.div
                    key={project.id}
                    layoutId={`project-${project.id}`}
                  >
                    <ProjectCard
                      {...project}
                      isSelected={selectedProjectId === project.id}
                      onSelect={() => setSelectedProjectId(project.id)}
                      onClose={() => setSelectedProjectId(null)}
                    />
                  </motion.div>
                ))}
              </AnimatePresence>
            </motion.div>

            <AnimatePresence>
              {selectedProjectId && (
                <>
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    className="fixed inset-0 bg-black/60 z-40"
                    onClick={() => setSelectedProjectId(null)}
                  />
                  <motion.div
                    layoutId={`project-${selectedProjectId}`}
                    className="fixed inset-4 md:inset-20 lg:inset-0 lg:top-16 lg:bottom-16 lg:left-1/4 lg:right-1/4 z-50 bg-white rounded-xl overflow-y-auto p-6 max-w-3xl mx-auto"
                  >
                    <ProjectCard
                      {...filmProjects.find(
                        (p) => p.id === selectedProjectId
                      )}
                      isSelected={true}
                      onClose={() => setSelectedProjectId(null)}
                    />
                  </motion.div>
                </>
              )}
            </AnimatePresence>
          </motion.div>
        );

      case 'skills':
        // ... (same as before)
        break;

      case 'experience':
        // ... (same as before)
        break;

      case 'personal':
        // ... (same as before)
        break;

      default:
        return null;
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="min-h-screen bg-gray-50 flex flex-col md:flex-row"
    >
      {/* Sidebar */}
      <motion.nav
        variants={sidebar}
        initial="initial"
        animate="animate"
        className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r border-gray-200 p-6"
      >
        <div className="flex flex-col h-full">
          {/* Profile Picture */}
          <motion.div
            className="mb-6 flex items-center"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <img
              src={profilePicture}
              alt="Profile"
              className="w-16 h-16 rounded-full object-cover mr-4"
            />
            <div>
              <h2 className="text-2xl font-bold text-gray-900">
                Martin Tomek
              </h2>
              <p className="text-sm text-gray-500 mt-1">
                VFX Artist & Motion Designer
              </p>
            </div>
          </motion.div>

          {/* Contact Info */}
          <motion.div
            className="mb-6 space-y-2"
            variants={stagger}
            initial="initial"
            animate="animate"
          >
            <ContactInfo icon={MapPin} text="Prague, Czech Republic" />
            <ContactInfo
              icon={Mail}
              text="martintomek.vfx@gmail.com"
              href="mailto:martintomek.vfx@gmail.com"
            />
            <ContactInfo
              icon={Phone}
              text="+420 774 630 132"
              href="tel:+420774630132"
            />
            <ContactInfo
              icon={Github}
              text="github.com/Themolx"
              href="https://github.com/Themolx"
            />
            <ContactInfo
              icon={Linkedin}
              text="LinkedIn Profile"
              href="https://www.linkedin.com/in/martin-tomek-1762b8196/"
            />
          </motion.div>

          {/* Navigation */}
          <motion.div
            className="space-y-2"
            variants={stagger}
            initial="initial"
            animate="animate"
          >
            {sections.map((section) => (
              <NavItem
                key={section.id}
                {...section}
                isActive={activeSection === section.id}
                onClick={() => setActiveSection(section.id)}
              />
            ))}
          </motion.div>

          {/* Footer */}
          <motion.div
            className="mt-auto pt-6 border-t border-gray-200"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
          >
            <p className="text-sm text-gray-500">
              Available for new opportunities
            </p>
            <p className="text-sm text-gray-500 mt-1">
              Willing to relocate to Canary Islands
            </p>
            <motion.a
              href="#"
              className="flex items-center mt-4 text-gray-600 hover:text-black transition-colors"
              whileHover={{ x: 5 }}
            >
              <Download size={16} className="mr-2" />
              <span className="text-sm">Download Resume</span>
            </motion.a>
          </motion.div>
        </div>
      </motion.nav>

      {/* Main Content */}
      <main className="flex-1 p-4 md:p-8 overflow-y-auto">
        <AnimatePresence mode="wait">{renderContent()}</AnimatePresence>
      </main>

      {/* Scroll to top button */}
      <AnimatePresence>
        {typeof window !== 'undefined' && window.pageYOffset > 300 && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            className="fixed bottom-8 right-8 p-3 bg-black text-white rounded-full shadow-lg hover:bg-gray-800 transition-colors"
          >
            <ChevronUp size={24} />
          </motion.button>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default HomePage;
