import React from 'react';
import { motion } from 'framer-motion';
import { BlobDemo } from '../demos/BlobDemo';
import { DragDemo } from '../demos/DragDemo';
import { Example } from '../demos/Example';

function BlogContent({ content }) {
  switch (content.type) {
    case 'intro':
      return (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="prose prose-invert max-w-none mb-8"
        >
          {content.content}
        </motion.div>
      );

    case 'section':
      return (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="mb-12"
        >
          <h2 className="text-2xl font-bold mb-6">{content.title}</h2>
          {typeof content.content === 'string' ? (
            <div className="prose prose-invert max-w-none">
              {content.content}
            </div>
          ) : content.content.tools ? (
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {content.content.tools.map((tool, index) => (
                <div key={index} className="bg-white/5 rounded-lg p-6">
                  <h3 className="text-xl font-bold mb-2">{tool.name}</h3>
                  <p className="text-gray-400">{tool.description}</p>
                </div>
              ))}
            </div>
          ) : content.content.features ? (
            <div className="space-y-8">
              {content.content.features.map((feature, index) => (
                <div key={index} className="bg-white/5 rounded-lg p-6">
                  <h3 className="text-xl font-bold mb-2">{feature.title}</h3>
                  <p className="text-gray-400 mb-4">{feature.description}</p>
                  {feature.code && (
                    <pre className="bg-black/50 p-4 rounded-lg overflow-x-auto">
                      <code>{feature.code}</code>
                    </pre>
                  )}
                </div>
              ))}
            </div>
          ) : null}
        </motion.div>
      );

    case 'conclusion':
      return (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="mt-12 border-t border-white/10 pt-12"
        >
          <div className="prose prose-invert max-w-none mb-8">
            {content.content.summary}
          </div>
          <div className="bg-white/5 rounded-lg p-6">
            <p className="text-xl mb-4">{content.content.callToAction.text}</p>
            <a
              href={content.content.callToAction.link}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-block bg-purple-600 hover:bg-purple-700 text-white px-6 py-3 rounded-lg transition-colors"
            >
              {content.content.callToAction.linkText}
            </a>
          </div>
        </motion.div>
      );

    default:
      return null;
  }
}

export default BlogContent;
