import React, { useState } from 'react';
import { motion } from 'framer-motion';
import ProductCard from '../components/shop/ProductCard';

function Shop() {
  const [activeCategory, setActiveCategory] = useState('all');

  const products = [
    {
      id: 'glitch-fx-v2',
      title: "GLITCH FX V2 - ANALOG DISTORTER",
      description: "Professional analog-created glitch effects pack with real CRT captures.",
      price: 99.99,
      salePrice: 59.99,
      category: "glitch",
      thumbnail: "/assets/Final_Photo_v1.png",
      badge: "Best Seller",
      software: ["After Effects", "Premiere Pro", "DaVinci Resolve"],
      link: "/shop/products/glitch-fx-v2"
    },
    {
      id: 'cyberpunk-toolkit',
      title: "CYBERPUNK TOOLKIT PRO",
      description: "Complete VFX toolkit for creating cyberpunk-style visual effects.",
      price: 79.99,
      salePrice: 49.99,
      category: "vfx",
      thumbnail: "/products/cyberpunk/thumbnail.jpg",
      badge: "New",
      software: ["After Effects", "Premiere Pro"],
      link: "/shop/products/cyberpunk-toolkit"
    },
    {
      id: 'neural-glitch',
      title: "NEURAL GLITCH AI",
      description: "AI-powered glitch effects generator with machine learning algorithms.",
      price: 149.99,
      salePrice: 89.99,
      category: "tools",
      thumbnail: "/products/neural-glitch/thumbnail.jpg",
      software: ["TouchDesigner", "After Effects"],
      link: "/shop/products/neural-glitch"
    }
  ];

  const categories = [
    { id: 'all', name: 'All Products' },
    { id: 'glitch', name: 'Glitch Effects' },
    { id: 'vfx', name: 'VFX Templates' },
    { id: 'tools', name: 'Custom Tools' }
  ];

  const filteredProducts = activeCategory === 'all' 
    ? products 
    : products.filter(product => product.category === activeCategory);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="min-h-screen py-24"
    >
      <div className="container mx-auto px-4">
        {/* Hero Section */}
        <div className="text-center mb-16">
          <h1 className="text-5xl font-bold mb-6">Digital Assets Shop</h1>
          <p className="text-xl text-gray-400 max-w-2xl mx-auto">
            Professional VFX assets, presets, and tools for creators
          </p>
        </div>

        {/* Categories */}
        <div className="flex flex-wrap justify-center gap-4 mb-12">
          {categories.map(category => (
            <button
              key={category.id}
              onClick={() => setActiveCategory(category.id)}
              className={`px-6 py-2 rounded-full transition-all ${
                activeCategory === category.id
                  ? 'bg-purple-600 text-white'
                  : 'bg-white/5 hover:bg-white/10'
              }`}
            >
              {category.name}
            </button>
          ))}
        </div>

        {/* Products Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredProducts.map(product => (
            <ProductCard 
              key={product.id} 
              product={product}
              badge={product.badge}
            />
          ))}
        </div>
      </div>
    </motion.div>
  );
}

export default Shop;
