import React from 'react';

function HeroSection() {
  return (
    <div className="bg-gradient-to-r from-purple-600 to-blue-600 text-white py-20">
      <div className="container mx-auto text-center">
        <h1 className="text-4xl md:text-6xl font-bold mb-6">Digital Art & Templates</h1>
        <p className="text-xl mb-8">Discover unique glitch art and web templates</p>
        <a href="/shop" className="bg-white text-purple-600 px-8 py-3 rounded-lg font-bold hover:bg-gray-100">
          Shop Now
        </a>
      </div>
    </div>
  );
}

export default HeroSection;
