// App.js

import React, { useState, useEffect, useRef, useCallback } from 'react';

import { motion, AnimatePresence, useAnimation, useMotionValue, useTransform } from 'framer-motion';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
  Linkedin,
  X,
  ExternalLink,
  ChevronRight,
  Play,
  ChevronUp,
  Menu,
  Maximize,
  Minimize
} from 'lucide-react';

// ====================
// Constants & Assets
// ====================

const glitchImages = [
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/1.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/2.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/3.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/4.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/6.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/7.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/9.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/12.png',
];

const analogImages = [
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/1.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/2.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/3.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/4.jpg',
];

// Commercial Images (assuming these are additional)
const commercialImages = [
  'https://via.placeholder.com/800x600.png?text=Commercial+1',
  'https://via.placeholder.com/800x600.png?text=Commercial+2',
  'https://via.placeholder.com/800x600.png?text=Commercial+3',
  'https://via.placeholder.com/800x600.png?text=Commercial+4',
  'https://via.placeholder.com/800x600.png?text=Commercial+5',
  'https://via.placeholder.com/800x600.png?text=Commercial+6',
  // Add more as needed
];

const profilePicture = 'https://avatars.githubusercontent.com/u/183303841?s=400&u=15ae10f7b65b15a10c0ddd7a9d12efbb608014f4&v=4';

// ====================
// Animation Variants
// ====================

const animationVariants = {
  slideInRight: {
    initial: { x: -100, opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: 100, opacity: 0 },
    transition: { duration: 0.5, ease: 'easeInOut' }
  },
  fadeInUp: {
    initial: { opacity: 0, y: 20 },
    animate: { opacity: 1, y: 0 },
    exit: { opacity: 0, y: -20 },
    transition: { duration: 0.5, ease: 'easeOut' }
  },
  container: {
    hidden: { opacity: 0 },
    show: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
        delayChildren: 0.3
      }
    }
  },
  item: {
    hidden: { opacity: 0, y: 20 },
    show: { opacity: 1, y: 0 },
    transition: { type: "spring", stiffness: 300, damping: 24 }
  }
};

// ====================
// Custom Hooks
// ====================

const useInView = (options = {}) => {
  const [isInView, setIsInView] = useState(false);
  const ref = useRef();

  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      setIsInView(entry.isIntersecting);
    }, { threshold: 0.1, ...options });

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, [options]);

  return [ref, isInView];
};

const useScrollDirection = () => {
  const [scrollDirection, setScrollDirection] = useState("up");
  const [prevOffset, setPrevOffset] = useState(0);

  useEffect(() => {
    const toggleScrollDirection = () => {
      const scrollY = window.pageYOffset;
      if (scrollY > prevOffset) {
        setScrollDirection("down");
      } else if (scrollY < prevOffset) {
        setScrollDirection("up");
      }
      setPrevOffset(scrollY);
    };

    window.addEventListener("scroll", toggleScrollDirection);
    return () => window.removeEventListener("scroll", toggleScrollDirection);
  }, [prevOffset]);

  return scrollDirection;
};

const useMousePosition = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  return mousePosition;
};

// ====================
// Utility Components
// ====================

const LoadingSpinner = () => (
  <div className="fixed inset-0 bg-white z-50 flex items-center justify-center">
    <motion.div
      className="w-16 h-16 border-4 border-gray-200 border-t-black rounded-full"
      animate={{ rotate: 360 }}
      transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
    />
  </div>
);

const CursorHighlight = () => {
  const { x, y } = useMousePosition();
  const size = 400;

  return (
    <div
      className="pointer-events-none fixed inset-0 z-30 transition-opacity duration-300"
      style={{
        background: `radial-gradient(circle ${size}px at ${x}px ${y}px, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0) 100%)`
      }}
    />
  );
};

// ====================
// Modal Components
// ====================

const ImageGalleryModal = ({ images, currentIndex, setCurrentIndex, onClose }) => {
  const controls = useAnimation();
  const dragConstraints = useRef(null);
  const dragX = useMotionValue(0);
  const dragProgress = useTransform(dragX, [-200, 200], [1, -1]);

  const handleDragEnd = useCallback(() => {
    const x = dragX.get();
    if (Math.abs(x) > 100) {
      const newIndex = x > 0
        ? (currentIndex - 1 + images.length) % images.length
        : (currentIndex + 1) % images.length;
      setCurrentIndex(newIndex);
    }
    dragX.set(0);
  }, [currentIndex, images.length, setCurrentIndex, dragX]);

  const handleKeyDown = useCallback((e) => {
    if (e.key === 'ArrowRight') {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    } else if (e.key === 'ArrowLeft') {
      setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
    } else if (e.key === 'Escape') {
      onClose();
    }
  }, [images.length, onClose, setCurrentIndex]);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/90 z-50 flex items-center justify-center"
      onClick={onClose}
    >
      <motion.div
        ref={dragConstraints}
        className="relative max-w-5xl w-full h-full flex items-center justify-center"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Navigation Controls */}
        <div className="absolute top-4 right-4 flex space-x-4">
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="p-2 bg-white/10 rounded-full text-white hover:bg-white/20"
            onClick={() => setCurrentIndex((prev) => (prev - 1 + images.length) % images.length)}
          >
            <ChevronRight className="w-6 h-6 rotate-180" />
          </motion.button>
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="p-2 bg-white/10 rounded-full text-white hover:bg-white/20"
            onClick={() => setCurrentIndex((prev) => (prev + 1) % images.length)}
          >
            <ChevronRight className="w-6 h-6" />
          </motion.button>
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="p-2 bg-white/10 rounded-full text-white hover:bg-white/20"
            onClick={onClose}
          >
            <X className="w-6 h-6" />
          </motion.button>
        </div>

        {/* Image Container */}
        <motion.div
          drag="x"
          dragConstraints={dragConstraints}
          dragElastic={0.1}
          onDragEnd={handleDragEnd}
          style={{ x: dragX }}
          className="relative w-full h-full flex items-center justify-center"
        >
          <motion.img
            key={currentIndex}
            src={images[currentIndex]}
            alt={`Gallery image ${currentIndex + 1}`}
            className="max-h-[80vh] max-w-[90vw] object-contain pointer-events-none"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.3 }}
          />
        </motion.div>

        {/* Image Counter */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex items-center space-x-2">
          <span className="text-white text-sm">
            {currentIndex + 1} / {images.length}
          </span>
          <div className="flex space-x-1">
            {images.map((_, index) => (
              <button
                key={index}
                className={`w-2 h-2 rounded-full transition-colors duration-200 ${
                  index === currentIndex ? 'bg-white' : 'bg-white/40'
                }`}
                onClick={() => setCurrentIndex(index)}
              />
            ))}
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

const VideoModal = ({ url, onClose, title }) => {
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/95 z-50 flex items-center justify-center"
      onClick={onClose}
    >
      <motion.div
        className="relative w-full max-w-4xl bg-black rounded-lg overflow-hidden"
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        onClick={(e) => e.stopPropagation()}
      >
        <div className="aspect-w-16 aspect-h-9">
          <iframe
            src={url}
            className="w-full h-full"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
            title={title}
          />
        </div>
        {title && (
          <div className="absolute bottom-0 left-0 right-0 p-4 bg-gradient-to-t from-black/80 to-transparent">
            <h3 className="text-xl font-bold text-white">{title}</h3>
          </div>
        )}
        <motion.button
          className="absolute top-4 right-4 text-white"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          onClick={onClose}
        >
          <X size={24} />
        </motion.button>
      </motion.div>
    </motion.div>
  );
};

// ====================
// Component: ContactInfo
// ====================

const ContactInfo = ({ icon: Icon, text, href, className = '' }) => (
  <motion.a
    whileHover={{ scale: 1.05 }}
    whileTap={{ scale: 0.95 }}
    href={href || '#'}
    target="_blank"
    rel="noopener noreferrer"
    className={`flex items-center text-gray-600 hover:text-black transition-colors ${className}`}
  >
    <Icon size={16} className="mr-2" />
    <span className="text-sm">{text}</span>
  </motion.a>
);

// ====================
// Component: Navigation
// ====================

const Navigation = ({ activeSection, setActiveSection, isMobile = false }) => {
  const navItems = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'film', icon: Film, label: 'Film Projects' },
    { id: 'commercial', icon: Briefcase, label: 'Commercial Work' },
    { id: 'experience', icon: Briefcase, label: 'Experience' },
    { id: 'skills', icon: Code, label: 'Skills' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' }
    // Removed 'experimental' as per instruction
  ];

  return (
    <nav className={`${isMobile ? 'flex flex-row justify-around' : 'space-y-2'} flex-1`}>
      {navItems.map((item) => (
        <motion.button
          key={item.id}
          onClick={() => setActiveSection(item.id)}
          className={`
            ${isMobile ? 'p-2' : 'w-full px-4 py-3'}
            flex items-center rounded-lg transition-all duration-300
            ${activeSection === item.id
              ? 'bg-black text-white'
              : 'text-gray-600 hover:bg-gray-100 hover:text-black'
            }
          `}
          whileHover={{ scale: isMobile ? 1.05 : 1 }} // Removed y animation
        >
          <item.icon size={20} className="min-w-[20px]" />
          {!isMobile && <span className="ml-3 text-sm font-medium">{item.label}</span>}
        </motion.button>
      ))}
    </nav>
  );
};

const ProjectCard = ({
  title,
  description,
  tags = [],
  duration,
  icon: Icon,
  media,
  videoUrl,
  expanded = false,
  className = '',
  onExpand,
  variant = 'default'
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [showVideo, setShowVideo] = useState(false);

  const cardVariants = {
    default: 'bg-white hover:shadow-lg',
    featured: 'bg-gradient-to-br from-gray-900 to-gray-800 text-white',
    outline: 'bg-white border-2 border-black hover:bg-gray-50'
  };

  return (
    <motion.div
      className={`
        rounded-xl overflow-hidden transition-all duration-300
        ${cardVariants[variant]}
        ${className}
      `}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      whileHover={{ y: -5 }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
    >
      <div className="p-6">
        {/* Header */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-3">
            {Icon && (
              <div className={`
                p-2 rounded-lg 
                ${variant === 'featured' ? 'bg-white/10' : 'bg-gray-100'}
              `}>
                <Icon size={24} className={
                  variant === 'featured' ? 'text-white' : 'text-gray-600'
                } />
              </div>
            )}
            <div>
              <h3 className={`text-xl font-semibold ${
                variant === 'featured' ? 'text-white' : 'text-gray-900'
              }`}>
                {title}
              </h3>
              {duration && (
                <p className={`text-sm ${
                  variant === 'featured' ? 'text-white/60' : 'text-gray-500'
                }`}>
                  {duration}
                </p>
              )}
            </div>
          </div>

          {/* Expand/Collapse Button */}
          {onExpand && (
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={() => onExpand(!expanded)}
              className={`p-1 rounded-full ${
                variant === 'featured' ? 'text-white/80' : 'text-gray-500'
              }`}
            >
              <ChevronUp 
                size={20} 
                className={expanded ? 'rotate-0' : 'rotate-180'}
              />
            </motion.button>
          )}
        </div>

        {/* Description */}
        <p className={`
          ${variant === 'featured' ? 'text-white/80' : 'text-gray-600'}
          ${expanded ? 'line-clamp-none' : 'line-clamp-2'}
        `}>
          {description}
        </p>

        {/* Tags */}
        {tags.length > 0 && (
          <div className="flex flex-wrap gap-2 mt-4">
            {tags.map((tag, index) => (
              <span
                key={index}
                className={`
                  px-3 py-1 rounded-full text-sm
                  ${variant === 'featured' 
                    ? 'bg-white/10 text-white' 
                    : 'bg-gray-100 text-gray-700'}
                `}
              >
                {tag}
              </span>
            ))}
          </div>
        )}

        {/* Media Section */}
        <AnimatePresence>
          {expanded && media && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              className="mt-6 grid grid-cols-2 gap-4"
            >
              {media.map((item, index) => (
                <motion.div
                  key={index}
                  className="relative group cursor-pointer rounded-lg overflow-hidden"
                  whileHover={{ scale: 1.05 }}
                >
                  <img
                    src={item}
                    alt={`${title} ${index + 1}`}
                    className="w-full h-48 object-cover"
                  />
                  <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 
                    transition-opacity duration-200 flex items-center justify-center">
                    <Maximize className="text-white" size={24} />
                  </div>
                </motion.div>
              ))}
            </motion.div>
          )}
        </AnimatePresence>

        {/* Video Preview */}
        {expanded && videoUrl && (
          <motion.div 
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="mt-4"
          >
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => setShowVideo(true)}
              className="w-full relative aspect-video rounded-lg overflow-hidden 
                bg-gray-900 flex items-center justify-center group"
            >
              <div className="absolute inset-0 bg-black/50 group-hover:bg-black/30 
                transition-colors duration-200" />
              <Play className="text-white w-16 h-16 group-hover:scale-110 
                transition-transform duration-200" />
            </motion.button>
          </motion.div>
        )}
      </div>
    </motion.div>
  );
};

// ====================
// Component: SkillCard
// ====================

const SkillCard = ({ 
  title, 
  icon: Icon, 
  skills, 
  description,
  tools,
  projects
}) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [ref, isInView] = useInView({ threshold: 0.2 });

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
      transition={{ duration: 0.5 }}
      className="bg-white border border-gray-200 rounded-xl overflow-hidden"
    >
      <motion.div
        className="p-6 flex flex-col"
      >
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            {Icon && (
              <div className="p-2 bg-gray-100 rounded-lg">
                <Icon size={24} className="text-gray-600" />
              </div>
            )}
            <h3 className="text-xl font-semibold text-gray-900">{title}</h3>
          </div>
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={() => setIsExpanded(!isExpanded)}
            className="text-gray-500"
          >
            {isExpanded ? (
              <Minimize size={20} />
            ) : (
              <Maximize size={20} />
            )}
          </motion.button>
        </div>

        <p className="mt-2 text-gray-600">{description}</p>

        {/* Software/Tools Bubbles - Always Visible */}
        {tools && (
          <div className="mt-4">
            <h4 className="text-sm font-medium text-gray-700 mb-2">Tools & Software</h4>
            <div className="flex flex-wrap gap-2">
              {tools.map((tool, index) => (
                <span
                  key={index}
                  className="px-3 py-1 bg-gray-100 rounded-full text-sm text-gray-700"
                >
                  {tool}
                </span>
              ))}
            </div>
          </div>
        )}

        {/* Expanded Content */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3 }}
              className="mt-6 overflow-hidden"
            >
              {/* Skills Grid */}
              <div className="grid grid-cols-1 gap-4">
                {skills.map((skill, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.1 }}
                    className="flex items-center space-x-2"
                  >
                    <div className="w-2 h-2 bg-gray-900 rounded-full" />
                    <span className="text-gray-700">{skill}</span>
                  </motion.div>
                ))}
              </div>

              {/* Related Projects */}
              {projects && (
                <div className="mt-6">
                  <h4 className="text-sm font-medium text-gray-700 mb-3">
                    Related Projects
                  </h4>
                  <div className="grid gap-2">
                    {projects.map((project, index) => (
                      <motion.a
                        key={index}
                        href={project.link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="flex items-center space-x-2 text-gray-600 hover:text-gray-900"
                        whileHover={{ scale: 1.05 }}
                      >
                        <ChevronRight size={16} />
                        <span>{project.name}</span>
                      </motion.a>
                    ))}
                  </div>
                </div>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </motion.div>
  );
};

// ====================
// Component: Section
// ====================

const Section = ({ title, subtitle, children, className = '' }) => {
  const [ref, isInView] = useInView({ threshold: 0.1 });

  return (
    <motion.section
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
      transition={{ duration: 0.5 }}
      className={`space-y-8 ${className}`}
    >
      <div className="space-y-2">
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
          className="text-4xl font-bold text-gray-900"
        >
          {title}
        </motion.h2>
        {subtitle && (
          <motion.p
            initial={{ opacity: 0, y: 20 }}
            animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
            transition={{ delay: 0.2 }}
            className="text-lg text-gray-600"
          >
            {subtitle}
          </motion.p>
        )}
      </div>
      {children}
    </motion.section>
  );
};

// ====================
// Component: MobileNavigation
// ====================

const MobileNavigation = ({ isOpen, setIsOpen, children }) => {
  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          className="fixed inset-0 z-50 bg-white"
        >
          <div className="p-4">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-xl font-bold">Navigation</h2>
              <motion.button
                whileTap={{ scale: 0.95 }}
                onClick={() => setIsOpen(false)}
                className="p-2"
              >
                <X size={24} />
              </motion.button>
            </div>
            {children}
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

