              ))}
            </motion.div>
          </Section>
        );

      case 'skills':
        return (
          <Section
            title="Skills & Expertise"
            subtitle="Technical proficiency and creative capabilities"
          >
            <div className="space-y-12">
              {/* Main Skills */}
              <div className="grid grid-cols-1 gap-6">
                {experienceData.skills.map((skill, index) => (
                  <SkillCard
                    key={index}
                    {...skill}
                  />
                ))}
              </div>

              {/* Software Proficiency */}
              <div>
                <h3 className="text-xl font-semibold text-gray-900 mb-6">Software Proficiency</h3>
                <div className="grid grid-cols-1 gap-6">
                  {experienceData.softwareProficiency.map((software, index) => (
                    software.category ? (
                      <div key={index} className="col-span-1">
                        <h4 className="text-lg font-semibold text-gray-700 border-b border-gray-200 pb-2 mb-4">
                          {software.category}
                        </h4>
                      </div>
                    ) : (
                      <motion.div
                        key={index}
                        variants={animationVariants.item}
                        initial={{ opacity: 0, x: -20 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        transition={{ delay: index * 0.1 }}
                        className="bg-white p-4 rounded-lg border border-gray-200"
                      >
                        <div className="flex justify-between items-center mb-2">
                          <span className="font-medium text-black">{software.name}</span>
                          <span className="text-sm text-gray-500">{software.proficiency}%</span>
                        </div>
                        <div className="h-2 bg-gray-100 rounded-full overflow-hidden">
                          <motion.div
                            className="h-full bg-black"
                            style={{ width: `${software.proficiency}%` }}
                            transition={{ duration: 1, ease: "easeOut" }}
                          />
                        </div>
                      </motion.div>
                    )
                  ))}
                </div>
              </div>
            </div>
          </Section>
        );

      case 'experience':
        return (
          <Section
            title="Professional Experience"
            subtitle="Career highlights and achievements"
          >
            <div className="space-y-8">
              {experienceData.experience.map((exp, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ delay: index * 0.1 }}
                  className="bg-white p-6 rounded-xl shadow-md border border-gray-200"
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-xl font-semibold text-gray-900">{exp.title}</h3>
                      <p className="text-sm text-gray-500">{exp.duration}</p>
                    </div>
                    {exp.link && (
                      <div className="flex space-x-2">
                        <a href={exp.link} target="_blank" rel="noopener noreferrer" className="text-indigo-600 hover:text-indigo-800 flex items-center space-x-1">
                          <span>Learn More</span>
                          <ExternalLink size={16} />
                        </a>
                      </div>
                    )}
                  </div>
                  <p className="mt-4 text-gray-700">{exp.description}</p>
                  {exp.moreInfo && (
                    <div className="mt-4 text-gray-600">
                      {exp.moreInfo}
                    </div>
                  )}
                </motion.div>
              ))}
            </div>
          </Section>
        );

      case 'personal':
        return (
          <Section
            title="Personal Projects"
            subtitle="Experimental work and creative explorations"
          >
            <div className="grid grid-cols-1 gap-8">
              {personalProjects.map((project, index) => (
                <ProjectCard
                  key={index}
                  {...project}
                  variant={project.variant || 'default'}
                  category="Personal"
                />
              ))}
            </div>
          </Section>
        );

      case 'commercial':
        return (
          <Section
            title="Commercial Work"
            subtitle="Advertising and branded content projects"
          >
            <motion.div
              variants={animationVariants.container}
              initial="hidden"
              animate="show"
              className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4"
            >
              {experienceData.commercial.map((project, index) => (
                <motion.div
                  key={index}
                  variants={animationVariants.item}
                  className="bg-white border border-gray-200 rounded-lg hover:shadow-lg transition-all duration-200 p-4"
                >
                  {/* Project Header */}
                  <div className="flex items-start justify-between mb-2">
                    <h3 className="text-base font-semibold text-gray-900">{project.title}</h3>
                    {project.icon && (
                      <project.icon className="w-4 h-4 text-gray-400 flex-shrink-0 ml-2" />
                    )}
                  </div>

                  {/* Duration */}
                  <p className="text-xs text-gray-500 mb-2">{project.duration}</p>

                  {/* Description - Limited to 2 lines */}
                  <p className="text-sm text-gray-600 line-clamp-2 mb-3">{project.description}</p>

                  {/* Technologies */}
                  {project.technologies && (
                    <div className="flex flex-wrap gap-1.5 mt-auto">
                      {project.technologies.map((tech, techIndex) => (
                        <span
                          key={techIndex}
                          className="px-2 py-0.5 bg-gray-100 text-gray-600 rounded-full text-xs whitespace-nowrap"
                        >
                          {tech}
                        </span>
                      ))}
                    </div>
                  )}
                </motion.div>
              ))}
            </motion.div>
          </Section>
        );

      default:
        return null;
    }
  };

  // Render loading screen if loading
  if (isLoading) {
    return (
      <LoadingSpinner />
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col md:flex-row">
      {/* Sidebar */}
      <motion.nav
        variants={animationVariants.slideInRight}
        initial="initial"
        animate="animate"
        exit="exit"
        className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r 
          border-gray-200 p-6 md:h-screen md:sticky md:top-0"
      >
        <div className="flex flex-col h-full">
          {/* Profile Section */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="mb-6"
          >
            <div className="relative w-20 h-20 mb-4">
              <img
                src={profilePicture}
                alt="Profile"
                className="rounded-full object-cover w-full h-full"
              />
              <motion.div
                className="absolute -inset-0.5 rounded-full bg-gradient-to-r 
                  from-black to-gray-600 opacity-0"
                whileHover={{ opacity: 0.2 }}
              />
            </div>
            <h2 className="text-2xl font-bold text-gray-900">Martin Tomek</h2>
            <p className="text-gray-500">VFX Artist & Motion Designer</p>
          </motion.div>

          {/* Contact Info */}
          <motion.div
            variants={animationVariants.container}
            initial="hidden"
            animate="show"
            className="mb-6 space-y-2"
          >
            <ContactInfo icon={MapPin} text="Prague, Czech Republic" href="#" />
            <ContactInfo
              icon={Mail}
              text="martintomek.vfx@gmail.com"
              href="mailto:martintomek.vfx@gmail.com"
            />
            <ContactInfo
              icon={Phone}
              text="+420 774 630 132"
              href="tel:+420774630132"
            />
            <ContactInfo
              icon={Github}
              text="github.com/Themolx"
              href="https://github.com/Themolx"
            />
            <ContactInfo
              icon={Linkedin}
              text="LinkedIn Profile"
              href="https://www.linkedin.com/in/martin-tomek"
            />
          </motion.div>

          {/* Navigation */}
          <Navigation
            activeSection={activeSection}
            setActiveSection={setActiveSection}
          />

          {/* Social Links */}
          <div className="mt-6">
            <div className="flex space-x-4">
              <motion.a
                href="https://github.com/Themolx"
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-600 hover:text-black transition-colors"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <Github size={20} />
                <span className="sr-only">GitHub</span>
              </motion.a>
              <motion.a
                href="https://www.linkedin.com/in/martin-tomek"
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-600 hover:text-black transition-colors"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <Linkedin size={20} />
                <span className="sr-only">LinkedIn</span>
              </motion.a>
              <motion.a
                href="mailto:martintomek.vfx@gmail.com"
                className="text-gray-600 hover:text-black transition-colors"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                <Mail size={20} />
                <span className="sr-only">Email</span>
              </motion.a>
            </div>
          </div>

          {/* Footer */}
          <motion.div
            variants={animationVariants.item}
            initial="hidden"
            animate="show"
            transition={{ delay: 0.5 }}
            className="mt-auto pt-6 border-t border-gray-200"
          >
            <p className="text-sm text-gray-500">Available for new opportunities</p>
            <p className="text-sm text-gray-500 mt-1">
              Willing to relocate to Canary Islands
            </p>
            <motion.a
              href="#"
              className="flex items-center mt-4 text-gray-600 hover:text-black 
                transition-colors group"
              whileHover={{ scale: 1.05 }} // Simplified hover
            >
              <Download size={16} className="mr-2 group-hover:scale-110 transition-transform" />
              <span className="text-sm">Download Resume</span>
            </motion.a>
          </motion.div>
        </div>
      </motion.nav>

      {/* Main Content */}
      <main
        className="flex-1 p-4 md:p-8 overflow-y-auto"
      >
        <AnimatePresence mode="wait">
          {renderContent()}
        </AnimatePresence>
      </main>

      {/* Mobile Menu Button */}
      <motion.button
        className="md:hidden fixed bottom-8 left-8 p-3 bg-black text-white 
          rounded-full shadow-lg hover:bg-gray-800 z-40"
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={() => setIsMobileMenuOpen(true)}
      >
        <Menu size={24} />
      </motion.button>

      {/* Mobile Navigation */}
      <MobileNavigation
        isOpen={isMobileMenuOpen}
        setIsOpen={setIsMobileMenuOpen}
      >
        <Navigation
          activeSection={activeSection}
          setActiveSection={(section) => {
            setActiveSection(section);
            setIsMobileMenuOpen(false);
          }}
          isMobile
        />
      </MobileNavigation>

      {/* Scroll to Top Button */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            className="fixed bottom-8 right-8 p-3 bg-black text-white rounded-full 
              shadow-lg hover:bg-gray-800 z-40"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <ChevronUp size={24} />
          </motion.button>
        )}
      </AnimatePresence>

      {/* Cursor Highlight Effect */}
      <CursorHighlight />
    </div>
  );
};

