import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
  Linkedin,
  X,
  ExternalLink,
  ChevronRight,
  Camera,
  Palette,
  Terminal
} from 'lucide-react';

// Animation variants
const fadeInUp = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -20 },
  transition: { duration: 0.5 }
};

const stagger = {
  animate: {
    transition: {
      staggerChildren: 0.1
    }
  }
};

const sidebar = {
  initial: { x: -50, opacity: 0 },
  animate: { x: 0, opacity: 1, transition: { duration: 0.5, delay: 0.2 } }
};

/**
 * Section Title Component
 */
const SectionTitle = ({ children }) => (
  <motion.h1 
    className="text-4xl font-bold text-gray-900 mb-8"
    variants={fadeInUp}
    initial="initial"
    animate="animate"
    exit="exit"
  >
    {children}
  </motion.h1>
);

/**
 * Skills Card Component
 */
const SkillCard = ({ title, icon: Icon, skills, description }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <motion.div
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
      className="bg-white rounded-xl p-6 shadow-sm hover:shadow-xl transition-shadow"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <motion.div className="flex items-center mb-4">
        <Icon size={24} className="mr-3 text-gray-700" />
        <h3 className="text-xl font-semibold">{title}</h3>
      </motion.div>
      <motion.ul
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        className="space-y-2"
      >
        {skills.map((skill, index) => (
          <motion.li
            key={index}
            initial={{ x: -10, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: index * 0.1 }}
            className="flex items-center text-gray-700"
          >
            <ChevronRight size={16} className="mr-2" />
            {skill}
          </motion.li>
        ))}
      </motion.ul>
      <AnimatePresence>
        {isHovered && (
          <motion.p
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 10 }}
            className="mt-4 text-gray-600 text-sm"
          >
            {description}
          </motion.p>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

/**
 * Project Card Component with Framer Motion
 */
const ProjectCard = ({
  id,
  title,
  subtitle,
  description,
  details,
  media,
  isSelected,
  onSelect,
  onClose,
}) => {
  return (
    <motion.div
      layoutId={`card-${id}`}
      className={`cursor-pointer bg-white rounded-xl ${
        isSelected 
          ? 'fixed inset-4 md:inset-20 z-50 overflow-y-auto' 
          : 'hover:shadow-xl border border-gray-200'
      }`}
      onClick={() => !isSelected && onSelect(id)}
    >
      <motion.div 
        className="p-6"
        layoutId={`content-${id}`}
      >
        <motion.div 
          className="flex justify-between items-start"
          layoutId={`header-${id}`}
        >
          <div>
            <motion.h3 
              className="text-xl font-semibold text-gray-900"
              layoutId={`title-${id}`}
            >
              {title}
            </motion.h3>
            <motion.p 
              className="text-sm text-gray-500 mt-1"
              layoutId={`subtitle-${id}`}
            >
              {subtitle}
            </motion.p>
          </div>
          {isSelected && (
            <motion.button
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              onClick={(e) => {
                e.stopPropagation();
                onClose();
              }}
              className="p-2 hover:bg-gray-100 rounded-full"
            >
              <X size={20} />
            </motion.button>
          )}
        </motion.div>

        <motion.p 
          className="text-gray-600 mt-4"
          layoutId={`description-${id}`}
        >
          {description}
        </motion.p>

        <AnimatePresence>
          {isSelected && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 20 }}
              transition={{ delay: 0.2 }}
              className="mt-6 space-y-6"
            >
              {details}
              {media && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {media.map((item, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.3 + index * 0.1 }}
                      className="rounded-lg overflow-hidden"
                    >
                      {item.type === 'image' ? (
                        <img 
                          src={item.url} 
                          alt={item.alt} 
                          className="w-full h-auto"
                        />
                      ) : (
                        <video
                          src={item.url}
                          controls
                          className="w-full"
                        />
                      )}
                    </motion.div>
                  ))}
                </div>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </motion.div>
  );
};

/**
 * NavItem Component with Animation
 */
const NavItem = ({ id, icon: Icon, label, isActive, onClick }) => (
  <motion.button
    whileHover={{ scale: 1.02, x: 5 }}
    whileTap={{ scale: 0.98 }}
    onClick={onClick}
    className={`w-full flex items-center px-4 py-3 rounded-lg transition-colors duration-300 ${
      isActive
        ? 'bg-black text-white'
        : 'text-gray-600 hover:text-black hover:bg-gray-100'
    }`}
  >
    <Icon size={20} className="min-w-[20px]" />
    <span className="ml-3 text-sm font-medium">{label}</span>
  </motion.button>
);

/**
 * ContactInfo Component with Animation
 */
const ContactInfo = ({ icon: Icon, text, href }) => (
  <motion.a
    whileHover={{ scale: 1.05, x: 5 }}
    whileTap={{ scale: 0.95 }}
    href={href}
    target="_blank"
    rel="noopener noreferrer"
    className="flex items-center text-gray-600 hover:text-black transition-colors"
  >
    <Icon size={16} className="mr-2" />
    <span className="text-sm">{text}</span>
  </motion.a>
);

/**
 * Main HomePage Component
 */
const HomePage = () => {
  const [activeSection, setActiveSection] = useState('summary');
  const [selectedProjectId, setSelectedProjectId] = useState(null);

  // Project data
  const filmProjects = [
    {
      id: 'proud-princess',
      title: 'Proud Princess',
      subtitle: 'PFX Studio | September 2024 - October 2024',
      description: 'Lead compositor for this major feature film project.',
      details: (
        <div className="space-y-4">
          <section>
            <h4 className="font-semibold text-lg mb-2">Project Overview</h4>
            <p className="text-gray-700">
              Collaborated with the VFX team to integrate visual effects seamlessly 
              into live-action footage, ensuring high-quality deliverables under tight deadlines.
            </p>
          </section>
          <section>
            <h4 className="font-semibold text-lg mb-2">Key Responsibilities</h4>
            <ul className="list-disc pl-5 space-y-2 text-gray-700">
              <li>Led compositing team for key sequences</li>
              <li>Developed and maintained shot consistency</li>
              <li>Optimized workflow for tight delivery schedule</li>
              <li>Collaborated with directors for creative decisions</li>
            </ul>
          </section>
        </div>
      ),
      media: [
        {
          type: 'image',
          url: '/api/placeholder/600/400',
          alt: 'Proud Princess VFX Shot 1'
        },
        {
          type: 'image',
          url: '/api/placeholder/600/400',
          alt: 'Proud Princess VFX Shot 2'
        }
      ]
    },
    {
      id: 'rosa-dara',
      title: 'Rosa & Dara',
      subtitle: 'Incognito Studio | August 2021 - Present',
      description: 'Lead compositor and technical director for 3D animated feature film.',
      details: (
        <div className="space-y-4">
          <section>
            <h4 className="font-semibold text-lg mb-2">Project Scope</h4>
            <p className="text-gray-700">
              Full-length 3D animated feature film requiring complex character integration
              and extensive pipeline development for efficient workflow.
            </p>
          </section>
          <section>
            <h4 className="font-semibold text-lg mb-2">Technical Achievements</h4>
            <ul className="list-disc pl-5 space-y-2 text-gray-700">
              <li>Developed custom Nuke tools for pipeline optimization</li>
