import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
  Linkedin,
  X,
  ExternalLink,
  ChevronRight,
  Camera,
  Palette,
  Terminal,
  Settings,
  Monitor
} from 'lucide-react';

