// Enhanced ProjectCard Component
const ProjectCard = ({
  title,
  description,
  duration,
  moreInfo,
  media,
  mediaType,
  alwaysExpanded = false,
  className = '',
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);
  const mediaRefs = useRef([]);

  useEffect(() => {
    if (!isHovered && !alwaysExpanded) {
      const timer = setTimeout(() => setIsExpanded(false), 300);
      return () => clearTimeout(timer);
    } else {
      setIsExpanded(true);
    }
  }, [isHovered, alwaysExpanded]);

  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      whileHover={{ scale: 1.02 }}
      transition={{ duration: 0.3 }}
      className={`bg-white border border-gray-200 rounded-xl p-6 ${className}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <motion.div layout className="flex flex-col">
        <motion.h3 
          layout
          className="text-xl font-semibold text-gray-900 mb-1"
        >
          {title}
        </motion.h3>

        {duration && (
          <motion.p 
            layout
            className="text-sm text-gray-500 mb-1"
          >
            {duration}
          </motion.p>
        )}

        <motion.p 
          layout
          className="text-gray-600 mt-2"
        >
          {description}
        </motion.p>

        <AnimatePresence>
          {(isExpanded || alwaysExpanded) && (moreInfo || media) && (
            <motion.div
              variants={expandAnimation}
              initial="initial"
              animate="animate"
              exit="exit"
              className="overflow-hidden"
            >
              <motion.div 
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
                className="mt-4"
              >
                {media && (
                  <div className="mb-4 grid grid-cols-1 md:grid-cols-2 gap-4">
                    {media.map((item, index) => (
                      <motion.div
                        key={index}
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ delay: index * 0.1 }}
                        className="rounded-lg overflow-hidden"
                      >
                        {mediaType === 'image' ? (
                          <img 
                            src={item}
                            alt={`${title} ${index + 1}`}
                            className="w-full h-auto object-cover transform hover:scale-105 transition-transform duration-300"
                          />
                        ) : (
                          <video
                            ref={el => mediaRefs.current[index] = el}
                            src={item}
                            controls
                            className="w-full"
                          />
                        )}
                      </motion.div>
                    ))}
                  </div>
                )}
                {moreInfo}
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </motion.div>
  );
};

