              )}
              {studio && (
                <p className={`text-sm mt-0.5 ${
                  variant === 'featured' ? 'text-white/60' : 'text-gray-500'
                }`}>
                  {studio}
                </p>
              )}
            </div>
          </div>

          {/* Only show expand/collapse for non-commercial cards */}
          {canExpand && (
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={(e) => {
                e.stopPropagation();
