export const aeCustomPanelPost = {
    slug: 'ae-custom-panel',
    title: "Building a Custom Panel for After Effects with AI",
    date: "March 29, 2024",
    author: "Martin Tomek",
    tags: ["After Effects", "Automation", "VFX", "Workflow", "Discord"],
    content: [
        {
            type: 'intro',
            content: `As a Compositing TD, I found myself repeatedly doing the same tasks in After Effects.
                This is the story of how I automated my workflow with the help of AI.`
        },
        {
            type: 'section',
            title: "From Frustration to Automation",
            content: `I was spending too much time on repetitive tasks:
                â€¢ Tracking week numbers
                â€¢ Exporting files with specific naming conventions
                â€¢ Sending Discord notifications

                What started as a simple idea evolved into a custom panel that automated all of these tasks,
                turning 30-minute processes into one-click operations.`
        },
        {
            type: 'section',
            title: "Key Features",
            content: {
                features: [
                    {
                        title: "Week Number Tracking",
                        description: "Automatically calculates and maintains project week numbers using JavaScript's Date object. This ensures consistent tracking across all deliverables.",
                        code: `const getWeekNumber = () => {
  const date = new Date();
  const firstDayOfYear = new Date(date.getFullYear(), 0, 1);
  const pastDaysOfYear = (date - firstDayOfYear) / 86400000;
  return Math.ceil((pastDaysOfYear + firstDayOfYear.getDay() + 1) / 7);
};`
                    },
                    {
                        title: "Automated File Export",
                        description: "Handles file exports with standardized naming conventions, eliminating manual naming errors.",
                        code: `const exportFile = (composition, format, weekNumber) => {
  const exportSettings = { format };
  const exportName = \`Project_Week_\${weekNumber}_\${composition.name}\`;
  // After Effects export logic
};`
                    },
                    {
                        title: "Discord Integration",
                        description: "Automatically sends notifications to team members through Discord when new exports are ready.",
                        code: `const sendDiscordMessage = (message) => {
  fetch('https://discord.com/api/webhooks/your-webhook-url', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ content: message }),
  });
};`
                    }
                ]
            }
        },
        {
            type: 'section',
            title: "Development Timeline",
            content: {
                stages: [
                    {
                        date: "January 10, 2024",
                        title: "Project Inception",
                        description: "Identified key automation needs and began planning the custom panel."
                    },
                    {
                        date: "February 5, 2024",
