export const blogPosts = {
  'ai-powered-nuke-tools': {
    title: "ü§ñ My Journey: Creating Nuke Tools with AI",
    date: "March 28, 2024",
    author: "Martin Tomek",
    tags: ["Nuke", "Python", "AI", "Automation", "Workflow"],
    content: [
      {
        type: 'personal-intro',
        content: "When I first started using AI to help me create tools for Nuke, I had no idea how much it would transform my workflow. What began with simple scripts grew into a collection of powerful tools that I use daily. Here's the story of how AI helped me automate and enhance my Nuke workflow, one script at a time."
      },
      {
        type: 'journey-start',
        content: {
          title: "The Beginning: Simple Automation üå±",
          text: "My first experiments with AI-assisted coding were straightforward - loading sequences and organizing light passes. I discovered that AI assistants like Claude and ChatGPT could understand Nuke's Python API surprisingly well. Each successful script gave me confidence to tackle more complex challenges."
        }
      },
      {
        type: 'tools-showcase',
        content: {
          title: "The Tools That Changed My Workflow üõ†Ô∏è",
          tools: [
            {
              name: "Light Shuffler",
              description: "My first major success - a tool that automatically organizes light passes. AI helped me understand how to manipulate Nuke's shuffle nodes effectively.",
              achievement: "Reduced setup time for light passes by 70%"
            },
            {
              name: "Denoise Comp Generator",
              description: "AI helped me automate the creation of denoising compositions, handling all the standard settings automatically.",
              achievement: "Standardized denoising workflow across projects"
            },
            {
              name: "ZDefocus Checker",
              description: "One of my more complex tools - it analyzes depth of field settings across multiple shots.",
              achievement: "Catches inconsistencies before they become problems in production"
            }
          ]
        }
      },
      {
        type: 'dialogue-section',
        content: {
          title: "Learning Through Collaboration with AI üí°",
          conversation: {
            me: "I need a way to automatically check and organize my Reduce Noise nodes - they're scattered all over my comp.",
            claude: "We can create a script that identifies these nodes and wraps them in colored backdrops. Here's how we can traverse the node graph...",
            result: "This led to ReduceNoiseBackdrop.py - now I can instantly spot all my heavy processing nodes!"
          }
        }
      },
      {
        type: 'development-insights',
        content: {
          title: "Key Learnings üìö",
          points: [
            "AI excels at explaining Nuke's node graph manipulation",
