import { motion } from 'framer-motion';
import { useState } from 'react';

export const Example = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="flex items-center justify-center">
      <motion.div
        className="bg-purple-500 rounded-2xl p-8 cursor-pointer"
        layout
        onClick={() => setIsOpen(!isOpen)}
        initial={{ borderRadius: 50 }}
        animate={{
          width: isOpen ? 400 : 200,
          height: isOpen ? 400 : 200,
          borderRadius: isOpen ? "30px" : "50px"
        }}
        transition={{
          layout: { duration: 0.3, type: "spring" }
        }}
      >
        <motion.div
          layout
          className="text-white font-bold"
        >
          {isOpen ? "Click to shrink" : "Click to expand"}
        </motion.div>

        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.3 }}
            className="text-white mt-4"
          >
            <p>This is an example of Framer Motion's layout animations.</p>
            <p className="mt-4">The container smoothly animates between states!</p>
          </motion.div>
        )}
      </motion.div>
    </div>
  );
};
