import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
  Linkedin,
  X,
  ExternalLink,
  ChevronUp,
} from 'lucide-react';

// Profile picture
const profilePicture =
  'https://avatars.githubusercontent.com/u/183303841?s=400&u=15ae10f7b65b15a10c0ddd7a9d12efbb608014f4&v=4';

// Animation variants
const fadeInUp = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -20 },
  transition: { duration: 0.5 },
};

const stagger = {
  animate: {
    transition: {
      staggerChildren: 0.1,
    },
  },
};

const sidebar = {
  initial: { x: -50, opacity: 0 },
  animate: { x: 0, opacity: 1, transition: { duration: 0.5, delay: 0.2 } },
};

const modalAnimation = {
  hidden: { opacity: 0, scale: 0.95 },
  visible: { opacity: 1, scale: 1 },
  exit: { opacity: 0, scale: 0.95 },
};

// Section Title Component
const SectionTitle = ({ children }) => (
  <motion.h1
    className="text-4xl font-bold text-gray-900 mb-8"
    variants={fadeInUp}
    initial="initial"
    animate="animate"
    exit="exit"
  >
    {children}
  </motion.h1>
);

// Enhanced ProjectCard Component
const ProjectCard = ({
  title,
  description,
  duration,
  moreInfo,
  media,
  mediaType,
  alwaysExpanded = false,
  className = '',
  isSelected,
  onSelect,
  onClose,
  isPersonal = false,
}) => {
  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      whileHover={!isSelected && { scale: 1.02 }}
      transition={{ duration: 0.3 }}
      className={`bg-white border border-gray-200 rounded-xl p-6 ${className} ${
        !alwaysExpanded && !isSelected && 'cursor-pointer hover:shadow-lg'
      }`}
      onClick={() => !alwaysExpanded && !isSelected && onSelect && onSelect()}
    >
      {isSelected ? (
        // Render expanded layout
        <motion.div layout className="flex flex-col">
          {/* Media at the top */}
          {media && media.length > 0 && (
            <div className="mb-6">
              {mediaType === 'image' ? (
                <img
                  src={media[0]}
                  alt={title}
                  className="w-full h-auto object-cover rounded-lg"
                />
              ) : (
                <video src={media[0]} controls className="w-full rounded-lg" />
              )}
            </div>
          )}
          {/* Title and Close button */}
          <div className="flex justify-between items-start">
            <motion.h3 layout className="text-3xl font-semibold text-gray-900">
              {title}
            </motion.h3>
            {onClose && (
              <motion.button
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                onClick={(e) => {
                  e.stopPropagation();
                  onClose();
                }}
                className="p-2 hover:bg-gray-100 rounded-full"
              >
                <X size={24} />
              </motion.button>
            )}
          </div>
          {/* Duration */}
          {duration && (
            <motion.p layout className="text-sm text-gray-500 mt-1">
              {duration}
            </motion.p>
          )}
          {/* Description */}
          <motion.p layout className="text-gray-700 mt-4 leading-relaxed">
            {description}
          </motion.p>
          {/* More Info */}
          {moreInfo && <div className="mt-6">{moreInfo}</div>}
          {/* Media (if more than one) */}
          {media && media.length > 1 && (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
              {media.slice(1).map((item, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: index * 0.1 }}
                  className="rounded-lg overflow-hidden"
                >
                  {mediaType === 'image' ? (
                    <img
                      src={item}
                      alt={`${title} ${index + 2}`}
                      className="w-full h-auto object-cover transform hover:scale-105 transition-transform duration-300"
                    />
                  ) : (
                    <video src={item} controls className="w-full" />
                  )}
                </motion.div>
              ))}
            </div>
          )}
        </motion.div>
      ) : (
        // Render compact layout
        <motion.div layout className="flex flex-col">
          {media && media.length > 0 && (
            <img
              src={media[0]}
              alt={title}
              className={`w-full h-48 object-cover rounded-lg mb-4 ${
                isPersonal ? 'hover:opacity-80 transition-opacity duration-300' : ''
              }`}
            />
          )}
          <div className="flex justify-between items-start">
            <motion.div layout>
              <motion.h3 className="text-xl font-semibold text-gray-900">
                {title}
              </motion.h3>
              {duration && (
                <motion.p className="text-sm text-gray-500 mt-1">
                  {duration}
                </motion.p>
              )}
            </motion.div>
            {!isPersonal && (
              <motion.button
                onClick={(e) => {
                  e.stopPropagation();
                  onSelect && onSelect();
                }}
                className="p-2 hover:bg-gray-100 rounded-full"
              >
                <ChevronUp size={20} />
              </motion.button>
            )}
          </div>
          <motion.p className="text-gray-600 mt-4">
            {description}
          </motion.p>
        </motion.div>
      )}
    </motion.div>
  );
};

// NavItem Component
const NavItem = ({ id, icon: Icon, label, isActive, onClick }) => (
  <motion.button
    whileHover={{ scale: 1.02, x: 5 }}
    whileTap={{ scale: 0.98 }}
    onClick={onClick}
    className={`w-full flex items-center px-4 py-3 rounded-lg transition-colors duration-300 ${
      isActive
        ? 'bg-black text-white'
        : 'text-gray-600 hover:text-black hover:bg-gray-100'
    }`}
  >
    <Icon size={20} className="min-w-[20px]" />
    <span className="ml-3 text-sm font-medium">{label}</span>
  </motion.button>
);

// ContactInfo Component
const ContactInfo = ({ icon: Icon, text, href }) => (
  <motion.a
    whileHover={{ scale: 1.05, x: 5 }}
    whileTap={{ scale: 0.95 }}
    href={href}
    target="_blank"
    rel="noopener noreferrer"
    className="flex items-center text-gray-600 hover:text-black transition-colors"
  >
    <Icon size={16} className="mr-2" />
    <span className="text-sm">{text}</span>
  </motion.a>
);

// Image Lightbox Component
const ImageLightbox = ({ images, selectedIndex, onClose, onPrev, onNext }) => {
  const currentImage = images[selectedIndex];

  return (
    <AnimatePresence>
      {currentImage && (
        <>
          <motion.div
            className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={onClose}
          />
          <motion.div
            className="fixed inset-0 z-50 flex items-center justify-center p-4"
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.95 }}
            onClick={(e) => e.stopPropagation()}
          >
            <div className="relative">
              <img src={currentImage} alt={`Personal Project ${selectedIndex + 1}`} className="max-w-full max-h-screen rounded-lg" />
              <button
                onClick={onClose}
                className="absolute top-4 right-4 p-2 bg-white rounded-full hover:bg-gray-200 transition"
              >
                <X size={20} />
              </button>
              {selectedIndex > 0 && (
                <button
                  onClick={onPrev}
                  className="absolute top-1/2 left-4 transform -translate-y-1/2 p-2 bg-white rounded-full hover:bg-gray-200 transition"
                >
                  <ChevronUp size={20} style={{ transform: 'rotate(-90deg)' }} />
                </button>
              )}
              {selectedIndex < images.length - 1 && (
                <button
                  onClick={onNext}
                  className="absolute top-1/2 right-4 transform -translate-y-1/2 p-2 bg-white rounded-full hover:bg-gray-200 transition"
                >
                  <ChevronUp size={20} style={{ transform: 'rotate(90deg)' }} />
                </button>
              )}
            </div>
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
};

// Main HomePage Component
const HomePage = () => {
  const [activeSection, setActiveSection] = useState('summary');
  const [selectedProjectId, setSelectedProjectId] = useState(null);
  const [lightbox, setLightbox] = useState({
    isOpen: false,
    images: [],
    currentIndex: 0,
  });

  // Navigation sections
  const sections = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'film', icon: Film, label: 'Film Projects' },
    { id: 'experience', icon: Briefcase, label: 'Experience' },
    { id: 'skills', icon: Code, label: 'Skills' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' },
  ];

  // Film projects data
  const filmProjects = [
    {
      id: 'proud-princess',
      title: 'Proud Princess',
      duration: 'PFX Studio | September 2024 - October 2024',
      description: 'Lead compositor for this major feature film project.',
      moreInfo: (
        <div className="space-y-4">
          <section>
            <h4 className="font-semibold text-lg mb-2">Project Overview</h4>
            <p className="text-gray-700">
              Collaborated with the VFX team to integrate visual effects seamlessly into live-action footage, ensuring high-quality deliverables under tight deadlines.
            </p>
          </section>
          <section>
            <h4 className="font-semibold text-lg mb-2">Key Responsibilities</h4>
            <ul className="list-disc pl-5 space-y-2 text-gray-700">
              <li>Led compositing team for key sequences</li>
              <li>Developed and maintained shot consistency</li>
              <li>Optimized workflow for tight delivery schedule</li>
              <li>Collaborated with directors for creative decisions</li>
            </ul>
          </section>
        </div>
      ),
      media: [
        'https://via.placeholder.com/800x400?text=Proud+Princess',
      ],
      mediaType: 'image',
    },
    {
      id: 'rosa-dara',
      title: 'Rosa & Dara',
      duration: 'Incognito Studio | August 2021 - Present',
      description: 'Lead compositor and technical director for 3D animated feature film.',
      moreInfo: (
        <div className="space-y-4">
          <section>
            <h4 className="font-semibold text-lg mb-2">Project Scope</h4>
            <p className="text-gray-700">
              Full-length 3D animated feature film requiring complex character integration and extensive pipeline development for efficient workflow.
            </p>
          </section>
          <section>
            <h4 className="font-semibold text-lg mb-2">Technical Achievements</h4>
            <ul className="list-disc pl-5 space-y-2 text-gray-700">
              <li>Developed custom Nuke tools for pipeline optimization</li>
              <li>Implemented automated quality control systems</li>
              <li>Created render farm integration solutions</li>
              <li>Established collaborative workflow procedures</li>
            </ul>
          </section>
        </div>
      ),
      media: [
        'https://via.placeholder.com/800x400?text=Rosa+%26+Dara',
      ],
      mediaType: 'image',
    },
  ];

  // Personal projects data
  const personalProjects = [
    {
      id: 'glitch-art',
      title: 'Glitch Art',
      description: 'Experimental glitch art created through various digital manipulation techniques.',
      media: [
        'https://via.placeholder.com/800x600?text=Glitch+Art+1',
        'https://via.placeholder.com/800x600?text=Glitch+Art+2',
        'https://via.placeholder.com/800x600?text=Glitch+Art+3',
      ],
      mediaType: 'image',
    },
    {
      id: 'experimental-animation',
      title: 'Experimental Animation',
      description: 'Creating unique narratives through experimental animation techniques.',
      media: [
        'https://via.placeholder.com/800x600?text=Experimental+Animation+1',
        'https://via.placeholder.com/800x600?text=Experimental+Animation+2',
      ],
      mediaType: 'image',
    },
    {
      id: 'mixed-media',
      title: 'Mixed Media',
      description: 'Fusion of analog and digital media to create compelling visual stories.',
      media: [
        'https://via.placeholder.com/800x600?text=Mixed+Media+1',
        'https://via.placeholder.com/800x600?text=Mixed+Media+2',
      ],
      mediaType: 'image',
    },
    {
      id: 'analog-photography',
      title: 'Analog Photography',
      description: 'Capturing moments through analog lenses and developing alternative film processes.',
      media: [
        'https://via.placeholder.com/800x600?text=Analog+Photography+1',
        'https://via.placeholder.com/800x600?text=Analog+Photography+2',
      ],
      mediaType: 'image',
    },
  ];

  // Experience data
  const experienceData = [
    {
      title: 'Mid-Level Compositor',
      duration: 'PFX Studio | September 2024 - October 2024',
      description: 'Lead compositing work on the feature film Proud Princess.',
      moreInfo: (
        <div className="space-y-4">
          <h4 className="font-semibold mb-2">Key Achievements:</h4>
          <ul className="list-disc pl-5 space-y-1 text-gray-700">
            <li>Led compositing team for key sequences</li>
            <li>Established shot consistency guidelines</li>
            <li>Optimized delivery pipeline</li>
          </ul>
        </div>
      ),
    },
    {
      title: 'Nuke Compositor & VFX Supervisor',
      duration: 'Incognito Studio | August 2021 - Present',
      description: 'Provided VFX services for various high-profile clients.',
      moreInfo: (
        <div className="space-y-2">
          <h4 className="font-semibold mb-2">Clients:</h4>
          <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed grid grid-cols-2 gap-2">
            <li>Panasonic Japan</li>
            <li>Moneta Bank</li>
            <li>McDonald's</li>
            <li>Ketoknoflik</li>
            <li>PSS</li>
            <li>T-Mobile</li>
            <li>O2</li>
            <li>7energy</li>
            <li>Billa</li>
            <li>Epet</li>
          </ul>
          <p className="text-gray-700 leading-relaxed mt-4">
            VFX Supervisor for the Creditas project.
          </p>
          <p className="text-gray-700 leading-relaxed">
            Working on "Rosa & Dara a jejich velk letn dobrodrustv", a 3D feature film.
          </p>
        </div>
      ),
    },
    {
      title: 'Freelance Motion Graphic Designer',
      duration: 'VIG Production | October 2023 - Present',
      description: 'Created motion graphics for various client projects.',
    },
    {
      title: 'Freelance Nuke Compositor',
      duration: 'Let it Roll Festival | July 2023 - August 2023',
      description: 'Co-created a 5-minute opening sequence using Nuke and Nuke Studio.',
    },
  ];

  // Skills data
  const skillsData = [
    {
      category: 'Compositing',
      skills: ['Nuke (3+ years)', 'After Effects (8+ years)', 'DaVinci Resolve', 'Mocha Pro'],
    },
    {
      category: 'Technical Direction',
      skills: ['Python Scripting', 'Pipeline Development', 'Workflow Optimization', 'Custom Tool Creation'],
    },
    {
      category: 'Motion Design',
      skills: ['Adobe Creative Suite', 'Cinema 4D', 'Motion Graphics', 'Animation Principles'],
    },
    {
      category: 'Creative Development',
      skills: ['VJing (Resolume Arena)', 'TouchDesigner', 'Experimental Animation', 'Glitch Art'],
    },
  ];

  // Render main content based on active section
  const renderContent = () => {
    switch (activeSection) {
      case 'summary':
        return (
          <motion.div
            variants={stagger}
            initial="initial"
            animate="animate"
            exit="exit"
            className="space-y-8"
          >
            <SectionTitle>
              Hello, I'm <span className="text-indigo-600">Martin Tomek</span>
            </SectionTitle>

            <motion.div
              variants={fadeInUp}
              className="prose prose-lg max-w-none"
            >
              <p className="text-lg text-gray-700 leading-relaxed">
                I'm a <strong>Mid-Level Compositor</strong>, <strong>Technical Director (TD)</strong>, and{' '}
                <strong>Motion Designer</strong> based in <strong>Prague, Czech Republic</strong>, with a passion for visual storytelling. My current work spans both Incognito Studio and PFX Studio, where I've contributed to major projects including <em>"Proud Princess"</em> and <em>"Rosa & Dara a jejich velk letn dobrodrustv"</em>, a 3D feature film blending 2D and 3D animation.
              </p>
              <p className="text-lg text-gray-700 leading-relaxed">
                My professional experience includes VFX compositing for clients such as Panasonic Japan, Moneta Bank, and McDonald's, as well as VFX supervision for the Creditas project. Additionally, I have been involved in the Let it Roll Festival, compositing a 5-minute opening sequence. As a freelance Motion Graphic Designer, I work with VIG Production, creating dynamic visual content for various client projects.
              </p>
              <p className="text-lg text-gray-700 leading-relaxed">
                I specialize in Nuke compositing and pipeline development, with additional experience in motion graphics, leveraging tools like After Effects, DaVinci Resolve, Adobe Creative Suite, and TouchDesigner. I am also skilled in on-set VFX supervision and enjoy creating tailored scripts and tools for VFX production efficiency. Outside of work, I explore experimental animation, glitch art, and the fusion of analog and digital media, as well as VJing using Resolume Arena and TouchDesigner.
              </p>
              <p className="text-lg text-gray-700 leading-relaxed">
                I'm always eager to learn and grow, having attended conferences like FMX and Anifilm. I'm open to new opportunities and willing to relocate to the Canary Islands. If you're looking for someone who combines technical skills with creative vision, I'd love to connect.
              </p>
              <p className="text-lg text-gray-700 leading-relaxed">
                <strong>Contact:</strong> martintomek.vfx@gmail.com | +420 774 630 132
              </p>
              <p className="text-lg text-gray-700 leading-relaxed">
                I'm happy to share my password-protected Vimeo showreel upon request.
              </p>
              <p className="text-lg text-gray-700 leading-relaxed">
                Looking forward to potential collaborations!
              </p>
            </motion.div>
          </motion.div>
        );

      case 'film':
        return (
          <motion.div
            variants={stagger}
            initial="initial"
            animate="animate"
            exit="exit"
            className="space-y-6"
          >
            <SectionTitle>Film Projects</SectionTitle>

            <motion.div
              variants={fadeInUp}
              className="grid grid-cols-1 md:grid-cols-2 gap-6"
            >
              <AnimatePresence>
                {filmProjects.map((project) => (
                  <motion.div
                    key={project.id}
                    layoutId={`project-${project.id}`}
                  >
                    <ProjectCard
                      {...project}
                      isSelected={selectedProjectId === project.id}
                      onSelect={() => setSelectedProjectId(project.id)}
                      onClose={() => setSelectedProjectId(null)}
                    />
                  </motion.div>
                ))}
              </AnimatePresence>
            </motion.div>

            <AnimatePresence>
              {selectedProjectId && (
                <>
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    className="fixed inset-0 bg-black/60 z-40"
                    onClick={() => setSelectedProjectId(null)}
                  />
                  <motion.div
                    layoutId={`project-${selectedProjectId}`}
                    className="fixed inset-4 md:inset-20 lg:inset-0 lg:top-16 lg:bottom-16 lg:left-1/4 lg:right-1/4 z-50 bg-white rounded-xl overflow-y-auto p-6 max-w-3xl mx-auto"
                  >
                    <ProjectCard
                      {...filmProjects.find((p) => p.id === selectedProjectId)}
                      isSelected={true}
                      onClose={() => setSelectedProjectId(null)}
                    />
                  </motion.div>
                </>
              )}
            </AnimatePresence>
          </motion.div>
        );

      case 'experience':
        return (
          <motion.div
            variants={stagger}
            initial="initial"
            animate="animate"
            exit="exit"
            className="space-y-8"
          >
            <SectionTitle>Professional Experience</SectionTitle>

            <motion.div
              variants={fadeInUp}
              className="space-y-6"
            >
              {experienceData.map((exp, index) => (
                <ProjectCard
                  key={index}
                  {...exp}
                />
              ))}

              <ProjectCard
                title="Education"
                description="The University of Creative Communication, Prague - Degree in VFX and Animation"
              />
            </motion.div>
          </motion.div>
        );

      case 'skills':
        return (
          <motion.div
            variants={stagger}
            initial="initial"
            animate="animate"
            exit="exit"
            className="space-y-8"
          >
            <SectionTitle>Skills & Expertise</SectionTitle>

            <motion.div
              variants={fadeInUp}
              className="grid grid-cols-1 md:grid-cols-2 gap-6"
            >
              {skillsData.map((skill, index) => (
                <motion.div
                  key={index}
                  className="bg-white border border-gray-200 rounded-xl p-6"
                >
                  <h3 className="text-xl font-semibold text-gray-900 mb-4">{skill.category}</h3>
                  <ul className="list-disc pl-5 space-y-2 text-gray-700">
                    {skill.skills.map((s, idx) => (
                      <li key={idx}>{s}</li>
                    ))}
                  </ul>
                </motion.div>
              ))}
            </motion.div>
          </motion.div>
        );

      case 'personal':
        return (
          <motion.div
            variants={stagger}
            initial="initial"
            animate="animate"
            exit="exit"
            className="space-y-8"
          >
            <SectionTitle>Personal Projects</SectionTitle>

            <motion.div
              variants={fadeInUp}
              className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
            >
              {personalProjects.map((project, index) => (
                <motion.div
                  key={project.id}
                  className="relative"
                >
                  <ProjectCard
                    {...project}
                    isPersonal={true}
                    onSelect={() => {
                      setLightbox({
                        isOpen: true,
                        images: project.media,
                        currentIndex: 0,
                      });
