                </motion.div>
              ))}
            </motion.div>

            {/* Image Lightbox */}
            <ImageLightbox
              images={lightbox.images}
              selectedIndex={lightbox.currentIndex}
              onClose={() => setLightbox({ ...lightbox, isOpen: false })}
              onPrev={() =>
                setLightbox((prev) => ({
                  ...prev,
                  currentIndex: prev.currentIndex - 1,
                }))
              }
              onNext={() =>
                setLightbox((prev) => ({
                  ...prev,
                  currentIndex: prev.currentIndex + 1,
                }))
              }
            />
          </motion.div>
        );

      default:
        return null;
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="min-h-screen bg-gray-50 flex flex-col md:flex-row"
    >
      {/* Sidebar */}
      <motion.nav
        variants={sidebar}
        initial="initial"
        animate="animate"
        className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r border-gray-200 p-6 flex-shrink-0"
      >
        <div className="flex flex-col h-full">
          {/* Profile Picture */}
          <motion.div
            className="mb-6 flex items-center"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <img
              src={profilePicture}
