/**
 * ProjectCard Component
 * Handles individual project display, hover effects, and media controls.
 */
const ProjectCard = ({
  title,
  description,
  duration,
  moreInfo,
  media,
  mediaType, // 'video' or 'image'
  alwaysExpanded = false, // Controls expansion behavior
  className = '', // Allows additional class names for styling
  thumbnail, // Thumbnail image or video
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const mediaRefs = useRef([]);

  // Pause all media except the one being played
  const handlePlay = (currentIndex) => {
    mediaRefs.current.forEach((mediaElement, index) => {
      if (mediaElement && index !== currentIndex) {
        if (mediaType === 'video') {
          mediaElement.pause();
        }
      }
    });
  };

  // Pause all media when hover ends (if not always expanded)
  useEffect(() => {
    if (!isHovered && !alwaysExpanded) {
      mediaRefs.current.forEach((mediaElement) => {
        if (mediaElement) {
          if (mediaType === 'video') {
            mediaElement.pause();
          }
        }
      });
    }
  }, [isHovered, alwaysExpanded, mediaType]);

  // Determine whether to show additional content
  const showAdditionalContent = alwaysExpanded || isHovered;

  // For Skills page hover effect
  const [showDetails, setShowDetails] = useState(false);

  return (
    <div
      className={`bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-xl p-6 hover:shadow-xl transition-shadow duration-300 ${className}`}
      onMouseEnter={() => {
        setIsHovered(true);
        setShowDetails(true);
      }}
      onMouseLeave={() => {
        setIsHovered(false);
        setShowDetails(false);
      }}
    >
      <div className="flex items-center">
        {thumbnail && (
          <div className="mr-4">
            <img
              src={thumbnail}
              alt={`${title} Thumbnail`}
              className="w-16 h-16 rounded-lg object-cover"
            />
          </div>
        )}
        <div>
          <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-1">{title}</h3>
          {duration && <p className="text-sm text-gray-500 mb-1">{duration}</p>}
        </div>
      </div>
      <p className="text-gray-600 dark:text-gray-300 whitespace-pre-line mt-2">{description}</p>

      {/* Additional Content with smoother expand */}
      {(moreInfo || media) && (
        <div
          className={`mt-4 transition-all duration-500 ease-in-out overflow-hidden ${
            showAdditionalContent ? 'max-h-screen opacity-100' : 'max-h-0 opacity-0'
          }`}
        >
          {/* Display media based on type */}
          {media && (
            <div className="mb-4">
              {mediaType === 'video' ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {media.map((mediaSrc, index) => (
                    <video
                      key={index}
                      src={mediaSrc}
                      controls
                      className="w-full rounded-lg"
                      ref={(el) => (mediaRefs.current[index] = el)}
                      onPlay={() => handlePlay(index)}
                    />
                  ))}
                </div>
              ) : mediaType === 'image' ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {media.map((imageSrc, index) => (
                    <img
                      key={index}
                      src={imageSrc}
                      alt={`${title} ${index + 1}`}
                      className="w-full rounded-lg object-cover"
                    />
                  ))}
                </div>
              ) : null}
            </div>
          )}
          {/* Display additional information */}
          {moreInfo}
        </div>
      )}

      {/* Skills page hover details */}
      {showDetails && !moreInfo && (
        <div className="mt-4">
          <p className="text-gray-700 dark:text-gray-300 leading-relaxed">
            {/* Add any additional details you want to show on hover */}
            Experience and proficiency details can be added here.
          </p>
        </div>
      )}
    </div>
  );
};

