export const aeCustomPanelPost = {
    slug: 'ae-custom-panel',
    title: "Hello World",
    date: "March 29, 2024",
    author: "Martin Tomek",
    tags: ["Test"],
    content: [
      {
        type: 'personal-intro',
        content: "Hello World! Welcome to my first blog post about AI and technology."
      },
      {
        type: 'journey-start',
        content: {
          title: "Discovering Claude AI ðŸ¤–",
          text: "Claude is an advanced AI assistant created by Anthropic. It excels at understanding context, writing code, and engaging in meaningful conversations. With its broad knowledge base and ability to handle complex tasks, Claude represents a significant step forward in AI technology."
        }
      },
      {
        type: 'development-timeline',
        content: {
          title: "Key Capabilities ðŸ“ˆ",
          stages: [
            {
              title: "Code Generation",
              description: "One of the most impressive aspects of Claude is its ability to understand and generate code across multiple programming languages. From Python to JavaScript, Claude can help debug issues, suggest improvements, and explain complex programming concepts in an accessible way."
            },
            {
              title: "Versatile Problem Solving",
              description: "Beyond coding, Claude demonstrates strong capabilities in analysis, writing, and problem-solving. It can help with tasks ranging from data analysis to creative writing, making it a versatile tool for various professional and personal needs."
            }
          ]
        }
      },
      {
        type: 'dialogue-section',
        content: {
          title: "Learning Through Conversation ðŸ’¡",
          conversation: {
            me: "Hey Claude, can you help me understand how to structure a React component?",
            claude: "Of course! Let's break it down into simple steps. Think of a React component like a building block...",
            result: "Through this conversational approach, complex concepts become much clearer and more approachable."
          }
        }
      },
      {
        type: 'github-journey',
        content: {
          title: "Version Control and Collaboration ðŸ“š",
          text: "Using GitHub has transformed how I manage my code and collaborate with others. It's not just about storing code - it's about building a better workflow.",
