import React, { useState } from 'react';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
} from 'lucide-react';

// Import your images/videos (Replace with your actual file paths)
import GlitchArtVideo from './assets/Glitch/glitch_art_video.mp4';

const App = () => {
  const [activeSection, setActiveSection] = useState('summary');
  const [hoveredCard, setHoveredCard] = useState(null);

  const NavItem = ({ id, icon: Icon, label }) => (
    <button
      onClick={() => setActiveSection(id)}
      className={`w-full flex items-center px-4 py-3 rounded-lg transition-all duration-200 hover:bg-gray-100 ${
        activeSection === id
          ? 'bg-black text-white hover:bg-black/90'
          : 'text-gray-600 hover:text-black'
      }`}
    >
      <Icon size={20} className="min-w-[20px]" />
      <span className="ml-3 text-sm font-medium whitespace-nowrap">
        {label}
      </span>
    </button>
  );

  const ProjectCard = ({ title, description, duration, moreInfo, media }) => (
    <div
      className="relative bg-white border border-gray-200 rounded-xl p-6 hover:shadow-lg transition-all duration-200 cursor-pointer"
      onMouseEnter={() => setHoveredCard(title)}
      onMouseLeave={() => setHoveredCard(null)}
    >
      <div className="flex justify-between items-start">
        <div>
          <h3 className="text-xl font-semibold text-gray-900 mb-2">
            {title}
          </h3>
          {duration && (
            <p className="text-sm text-gray-500 mb-3">{duration}</p>
          )}
        </div>
      </div>
      <p className="text-gray-600 whitespace-pre-line">{description}</p>

      {/* Modal Content */}
      {hoveredCard === title && moreInfo && (
        <div className="absolute inset-0 bg-white bg-opacity-95 flex items-center justify-center p-4 rounded-xl shadow-lg animate-fadeIn">
          <div className="relative max-w-md">
            {media && (
              <div className="mb-4">
                {/* Display image or video */}
                {media.type === 'video' ? (
                  <video
                    src={media.src}
                    controls
                    className="w-full rounded-lg"
                  />
                ) : (
                  <img
                    src={media.src}
                    alt={title}
                    className="w-full rounded-lg"
                  />
                )}
              </div>
            )}
            {moreInfo}
          </div>
        </div>
      )}
    </div>
  );

  const sections = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'film', icon: Film, label: 'Film Projects' },
    { id: 'experience', icon: Briefcase, label: 'Experience' },
    { id: 'skills', icon: Code, label: 'Skills' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' },
  ];

  const ContactInfo = ({ icon: Icon, text, href }) => (
    <a
      href={href}
      className="flex items-center text-gray-600 hover:text-black transition-colors"
      target="_blank"
      rel="noopener noreferrer"
    >
      <Icon size={16} className="mr-2" />
      <span className="text-sm">{text}</span>
    </a>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'summary':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Hello, I'm Martin Tomek
            </h1>
            <p className="text-lg text-gray-700 max-w-3xl leading-relaxed">
              I'm a Mid-Level Compositor and Technical Director (TD) based in Prague, Czech Republic, with a passion for visual storytelling and a keen eye for detail. With over three years of experience in compositing, pipeline development, and motion graphics, I specialize in using Nuke for creating seamless visual effects and developing tailored VFX tools and scripts.
            </p>
            <p className="text-lg text-gray-700 max-w-3xl leading-relaxed">
              Currently, I work with Incognito Studio and PFX Studio, contributing to major projects like <em>"Proud Princess"</em> and <em>"Rosa & Dara a jejich velké letní dobrodružství"</em>, a 3D feature film that blends 2D and 3D animation. My expertise extends to on-set VFX supervision and integrating analog and digital media, including VJing with Resolume Arena and TouchDesigner.
            </p>
            <p className="text-lg text-gray-700 max-w-3xl leading-relaxed">
              I'm always eager to embrace new challenges and opportunities, and I'm willing to relocate, with a particular interest in the Canary Islands.
            </p>
          </div>
        );
      case 'film':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Film Projects
            </h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title='"Proud Princess"'
                duration="PFX Studio | October 2023 - Present"
                description="Compositing work on the feature film."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">Project Details:</h4>
                    <p className="text-gray-700 leading-relaxed">
                      Collaborated with the VFX team to integrate visual effects seamlessly into live-action footage, ensuring high-quality deliverables under tight deadlines.
                    </p>
                  </div>
                }
                media={{
                  type: 'image',
                  src: 'path_to_your_image.jpg', // Replace with your image path
                }}
              />
              <ProjectCard
                title='"Rosa & Dara a jejich velké letní dobrodružství"'
                duration="Incognito Studio | August 2021 - Present"
                description="Compositor and developer for a 3D feature film."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">Key Responsibilities:</h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
                      <li>Lead compositor for character integration scenes.</li>
                      <li>Developed custom Nuke tools for pipeline optimization.</li>
                      <li>Collaborated with 3D artists for seamless integration.</li>
                    </ul>
                  </div>
                }
                media={{
                  type: 'image',
                  src: 'path_to_another_image.jpg', // Replace with your image path
                }}
              />
            </div>
          </div>
        );
      case 'experience':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Professional Experience
            </h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title="Mid Compositor & Technical Director"
                duration="Incognito Studio | August 2021 - Present"
                description="VFX compositing and development work for high-profile clients."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">Clients:</h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
                      <li>Panasonic Japan</li>
                      <li>Moneta Bank</li>
                      <li>McDonald's</li>
                      <li>VFX Supervisor for the Creditas project</li>
                    </ul>
                    <h4 className="font-semibold mb-2">Achievements:</h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
                      <li>Implemented compositing workflows reducing render times by 30%.</li>
                      <li>Developed custom scripts for pipeline optimization.</li>
                    </ul>
                  </div>
                }
                media={{
                  type: 'image',
                  src: 'path_to_experience_image.jpg', // Replace with your image path
                }}
              />
              {/* Add more ProjectCards as needed */}
            </div>
          </div>
        );
      case 'skills':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">Skills</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title="Software Proficiency"
                description={`• Nuke
• After Effects
• DaVinci Resolve
• Adobe Creative Suite
• Resolume Arena
• TouchDesigner`}
              />
              <ProjectCard
                title="Pipeline & Workflow"
                description={`• OpenPype
• Ayon
• On-set VFX Supervision
• 2D and 3D Animation Integration
• VJing`}
              />
              <ProjectCard
                title="Technical Direction"
                description="Developed tailored scripts and tools for pipeline optimization in VFX production, enhancing efficiency and productivity."
              />
            </div>
          </div>
        );
      case 'personal':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Personal Projects
            </h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title="Experimental Animation & Glitch Art"
                description="Creating experimental animations and glitch art by integrating analog and digital media."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">Activities:</h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
                      <li>Analog photography and alternative film development</li>
                      <li>VJing using Resolume Arena and TouchDesigner</li>
                      <li>Mixed media art projects</li>
                    </ul>
                  </div>
                }
                media={{
                  type: 'video',
                  src: GlitchArtVideo, // Use your imported video
                }}
              />
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* Sidebar */}
      <nav className="w-64 bg-white border-r border-gray-200 p-6 hidden md:block">
        <div className="h-full flex flex-col">
          {/* Logo */}
          <div className="mb-8">
            <h2 className="text-2xl font-bold text-gray-900">Martin Tomek</h2>
            <p className="text-sm text-gray-500 mt-1">
              Mid-Level Compositor & TD
            </p>
          </div>

          {/* Contact Info */}
          <div className="mb-6 space-y-2">
            <ContactInfo icon={MapPin} text="Prague, Czech Republic" />
            <ContactInfo
              icon={Mail}
              text="martintomek.vfx@gmail.com"
              href="mailto:martintomek.vfx@gmail.com"
            />
            <ContactInfo
              icon={Phone}
              text="+420 774 630 132"
              href="tel:+420774630132"
            />
          </div>

          {/* Navigation */}
          <div className="space-y-2">
            {sections.map((section) => (
              <NavItem
                key={section.id}
                id={section.id}
                icon={section.icon}
                label={section.label}
              />
            ))}
          </div>

          {/* Footer */}
          <div className="mt-auto pt-6 border-t border-gray-200">
            <p className="text-sm text-gray-500">
              Available for new opportunities
            </p>
            <p className="text-sm text-gray-500 mt-1">
              Willing to relocate to Canary Islands
            </p>
            <a
              href="#"
              className="flex items-center mt-4 text-gray-600 hover:text-black transition-colors"
            >
              <Download size={16} className="mr-2" />
              <span className="text-sm">Download Resume</span>
            </a>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="flex-1 p-8 overflow-y-auto">{renderContent()}</main>
    </div>
  );
};

export default App;
