export const aeCustomPanelPost = {
    slug: 'ae-custom-panel',
    title: "Hello World",
    date: "March 29, 2024",
    author: "Martin Tomek",
    tags: ["Test"],
    content: [
      {
        type: 'personal-intro',
        content: "Hello World! Welcome to my first blog post about AI and technology."
      },
      {
        type: 'journey-start',
        content: {
          title: "Discovering Framer Motion ðŸŽ¨",
          text: "Let's explore some practical examples of Framer Motion animations that you can use in your projects. Each example demonstrates a different aspect of animation."
        }
      },
      {
        type: 'development-timeline',
        content: {
          title: "Animation Examples ðŸ“ˆ",
          stages: [
            {
              date: "Basic Example",
              title: "Page Transitions",
              description: `
const PageTransition = () => {
  return (
    <motion.div
      initial={{ opacity: 0, x: -20 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, x: 20 }}
      transition={{ duration: 0.3 }}
    >
      {children}
    </motion.div>
  );
};`
            },
            {
              date: "Interactive",
              title: "Hover Effects",
              description: `
const HoverCard = () => {
  return (
    <motion.div
      whileHover={{ 
        scale: 1.05,
        boxShadow: "0px 5px 15px rgba(0,0,0,0.3)"
      }}
      transition={{ type: "spring", stiffness: 300 }}
    >
      Card Content
    </motion.div>
  );
};`
            },
            {
              date: "Advanced",
              title: "Scroll Animations",
              description: `
const ScrollReveal = () => {
  return (
    <motion.div
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.5 }}
    >
      Revealed Content
    </motion.div>
  );
};`
            }
          ]
        }
      },
      {
        type: 'journey-start',
        content: {
          title: "Best Practices ðŸ’¡",
