// App.js

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { motion, AnimatePresence, useAnimation, useMotionValue, useTransform } from 'framer-motion';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
  Linkedin,
  X,
  ExternalLink,
  ChevronRight,
  Play,
  ChevronUp,
  Menu,
  Maximize,
  Minimize,
  // Additional icons
  Camera,
  Video,
  Monitor,
  Palette,
  Brush,
  Laptop,
  Terminal,
  Settings,
  Tool,
  Layers,
  Image,
  FileVideo,
  Tv,
  Projector,
  Lightbulb,
  Sparkles,
  Wand2,
  Rocket,
  Star,
  Award,
  Trophy,
  Medal,
  Certificate,
  Zap,
  Eye,
  PenTool,
  Box,
  Boxes,
  Component,
  Layout,
  Scissors,
  Workflow,
  Gauge,
  Sliders,
  Pause,
} from 'lucide-react';
import Player from '@vimeo/player';

// ====================
// Constants & Assets
