const ImageGalleryModal = ({ images, currentIndex, setCurrentIndex, onClose }) => {
  const controls = useAnimation();
  const dragConstraints = useRef(null);
  const dragX = useMotionValue(0);
  const dragProgress = useTransform(dragX, [-200, 200], [1, -1]);

  const handleDragEnd = useCallback(() => {
    const x = dragX.get();
    if (Math.abs(x) > 100) {
      const newIndex = x > 0
        ? (currentIndex - 1 + images.length) % images.length
        : (currentIndex + 1) % images.length;
      setCurrentIndex(newIndex);
    }
    dragX.set(0);
  }, [currentIndex, images.length, setCurrentIndex, dragX]);

  const handleKeyDown = useCallback((e) => {
    if (e.key === 'ArrowRight') {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    } else if (e.key === 'ArrowLeft') {
      setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
    } else if (e.key === 'Escape') {
      onClose();
    }
  }, [images.length, onClose, setCurrentIndex]);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  const handleBackgroundClick = (e) => {
    // Only close if clicking the background overlay
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ 
        opacity: 1, 
        scale: 1,
        transition: {
          type: "spring",
          stiffness: 300,
          damping: 25
        }
      }}
      exit={{ 
        opacity: 0, 
        scale: 0.95,
        transition: {
          type: "spring",
          stiffness: 300,
          damping: 25
        }
      }}
      className="fixed inset-0 bg-black/90 z-50 flex items-center justify-center"
      onClick={handleBackgroundClick}  // Changed from onClick={onClose}
    >
      <motion.div
        ref={dragConstraints}
        className="relative max-w-5xl w-full h-full flex items-center justify-center"
        onClick={(e) => e.stopPropagation()}  // Prevent clicks from bubbling up
      >
        {/* Navigation Controls */}
        <div className="absolute top-4 right-4 flex space-x-4 z-50">
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="p-2 bg-white/10 rounded-full text-white hover:bg-white/20"
            onClick={(e) => {
              e.stopPropagation();  // Add this to prevent bubbling
              setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
            }}
          >
            <ChevronRight className="w-6 h-6 rotate-180" />
          </motion.button>
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="p-2 bg-white/10 rounded-full text-white hover:bg-white/20"
            onClick={(e) => {
              e.stopPropagation();  // Add this to prevent bubbling
              setCurrentIndex((prev) => (prev + 1) % images.length);
            }}
          >
            <ChevronRight className="w-6 h-6" />
          </motion.button>
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="p-2 bg-white/10 rounded-full text-white hover:bg-white/20"
            onClick={(e) => {
              e.stopPropagation();  // Add this to prevent bubbling
              onClose();
            }}
          >
            <X className="w-6 h-6" />
          </motion.button>
        </div>

        {/* Image Container */}
        <motion.div
          drag="x"
          dragConstraints={dragConstraints}
          dragElastic={0.1}
          onDragEnd={handleDragEnd}
          style={{ x: dragX }}
          className="relative w-full h-full flex items-center justify-center"
        >
          <motion.img
            key={currentIndex}
            src={images[currentIndex]}
            alt={`Gallery image ${currentIndex + 1}`}
            className="max-h-[80vh] max-w-[90vw] object-contain pointer-events-none"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.3 }}
          />
        </motion.div>

        {/* Image Counter */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex items-center space-x-2">
          <span className="text-white text-sm">
            {currentIndex + 1} / {images.length}
          </span>
          <div className="flex space-x-1">
            {images.map((_, index) => (
              <button
                key={index}
                className={`w-2 h-2 rounded-full transition-colors duration-200 ${
                  index === currentIndex ? 'bg-white' : 'bg-white/40'
                }`}
                onClick={() => setCurrentIndex(index)}
              />
            ))}
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

