          initial={{ opacity: 0, y: 20 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
          className="text-4xl font-bold text-gray-900"
        >
          {title}
        </motion.h2>
        {subtitle && (
          <motion.p
            initial={{ opacity: 0, y: 20 }}
            animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
            transition={{ delay: 0.2 }}
            className="text-lg text-gray-600"
          >
            {subtitle}
          </motion.p>
        )}
      </div>
      {children}
    </motion.section>
  );
};

// ====================
// Component: MobileNavigation
// ====================

const MobileNavigation = ({ isOpen, setIsOpen, children }) => {
  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          className="fixed inset-0 z-50 bg-white"
        >
          <div className="p-4">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-xl font-bold">Navigation</h2>
              <motion.button
                whileTap={{ scale: 0.95 }}
                onClick={() => setIsOpen(false)}
                className="p-2"
              >
                <X size={24} />
              </motion.button>
            </div>
            {children}
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

// ====================
// Main App Component
// ====================

const App = () => {
  const [activeSection, setActiveSection] = useState('summary');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1500);

    return () => clearTimeout(timer);
  }, []);

  const renderContent = () => {
    switch (activeSection) {
      case 'personal':
        return (
          <Section
            title="Personal Projects"
            subtitle="Experimental work and creative explorations"
          >
            <div className="grid grid-cols-1 gap-8">
              {personalProjects.map((project, index) => (
                <ProjectCard
                  key={index}
                  {...project}
                  variant={project.variant || 'default'}
                  category="Personal"
                />
              ))}
            </div>
          </Section>
        );
      default:
        return null;
    }
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col md:flex-row">
      <motion.nav
        variants={animationVariants.slideInRight}
        initial="initial"
        animate="animate"
        exit="exit"
        className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r border-gray-200 p-6 md:h-screen md:sticky md:top-0"
      >
        <div className="flex flex-col h-full">
          {/* Profile Section */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2, duration: 0.3 }}
            className="mb-6"
          >
            <div className="relative w-20 h-20 mb-4 group">
              <img
                src={profilePicture}
                alt="Profile"
                className="rounded-full object-cover w-full h-full"
              />
              <div
                className="absolute -inset-0.5 rounded-full bg-gradient-to-r 
                  from-black to-gray-600 opacity-0 group-hover:opacity-20 
                  transition-opacity duration-200"
              />
            </div>
            <h2 className="text-2xl font-bold text-gray-900">Martin Tomek</h2>
            <p className="text-gray-500">VFX Artist & Motion Designer</p>
          </motion.div>

          {/* Navigation */}
          <Navigation
            activeSection={activeSection}
            setActiveSection={setActiveSection}
          />
        </div>
      </motion.nav>

      <main className="flex-1 p-4 md:p-8 overflow-y-auto">
        <AnimatePresence mode="wait">
          {renderContent()}
        </AnimatePresence>
      </main>
    </div>
  );
};

export default App;
