      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <ProjectCard
          title="Experimental Glitch Art"
          description="Digital art exploring data manipulation and visual glitches"
          icon={ImageIcon}
          technologies={["Processing", "Python", "Custom Tools"]}
          media={glitchImages}
          mediaType="image"
          details={
            <div className="space-y-4">
              <p>
                A series of experimental works exploring the intersection of digital
                errors and artistic expression through custom data manipulation
                techniques.
              </p>
              <ul className="list-disc pl-5">
                <li>Developed custom processing algorithms</li>
                <li>Explored various data manipulation techniques</li>
                <li>Created real-time glitch effects</li>
              </ul>
            </div>
          }
        />

        <ProjectCard
          title="Analog Photography"
          description="Film photography and darkroom experimentation"
          icon={Camera}
          technologies={["Film", "Darkroom", "Alternative Process"]}
          media={analogImages}
          mediaType="image"
          details={
            <div className="space-y-4">
              <p>
                Exploration of traditional photography techniques combined with
                experimental processing methods.
              </p>
              <ul className="list-disc pl-5">
                <li>Medium and large format photography</li>
                <li>Alternative development processes</li>
                <li>Mixed media experiments</li>
              </ul>
            </div>
          }
        />

        <ProjectCard
          title="VJ Performance Tools"
          description="Custom tools for live visual performance"
          icon={Play}
          technologies={["TouchDesigner", "Resolume Arena", "MIDI"]}
          variant="featured"
          details={
            <div className="space-y-4">
              <p>
                Development of custom tools and workflows for live visual
                performance and interactive installations.
              </p>
              <ul className="list-disc pl-5">
                <li>Real-time visual effects</li>
                <li>Audio-reactive visuals</li>
                <li>Custom MIDI control mappings</li>
              </ul>
            </div>
          }
        />
      </div>
    </SectionContainer>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'summary':
        return renderSummary();
      case 'skills':
        return renderSkills();
      case 'experience':
        return renderExperience();
      case 'personal':
        return renderPersonal();
      default:
        return null;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col md:flex-row">
      {/* Sidebar */}
      <motion.nav
        variants={slideInRight}
        initial="initial"
        animate="animate"
        className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r border-gray-200 p-6"
      >
        <div className="flex flex-col h-full">
          {/* Profile Section */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="mb-6"
          >
            <img
              src={profilePicture}
              alt="Profile"
              className="w-16 h-16 rounded-full object-cover mb-4"
            />
            <h2 className="text-2xl font-bold text-gray-900">Martin Tomek</h2>
            <p className="text-gray-500">VFX Artist & Motion Designer</p>
          </motion.div>

          {/* Contact Info */}
          <motion.div
            variants={stagger}
            initial="initial"
            animate="animate"
            className="mb-6 space-y-2"
          >
            <ContactInfo icon={MapPin} text="Prague, Czech Republic" />
            <ContactInfo
              icon={Mail}
              text="martintomek.vfx@gmail.com"
              href="mailto:martintomek.vfx@gmail.com"
            />
            <ContactInfo
              icon={Phone}
              text="+420 774 630 132"
              href="tel:+420774630132"
            />
          </motion.div>

          {/* Navigation */}
          <Navigation
            activeSection={activeSection}
            setActiveSection={setActiveSection}
          />

          {/* Social Links */}
          <SocialLinks className="mt-6" />

          {/* Footer */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
            className="mt-auto pt-6 border-t border-gray-200"
          >
            <p className="text-sm text-gray-500">Available for new opportunities</p>
            <p className="text-sm text-gray-500 mt-1">
              Willing to relocate to Canary Islands
            </p>
            <motion.a
              href="#"
              className="flex items-center mt-4 text-gray-600 hover:text-black transition-colors"
              whileHover={{ x: 5 }}
            >
              <Download size={16} className="mr-2" />
              <span className="text-sm">Download Resume</span>
            </motion.a>
          </motion.div>
        </div>
      </motion.nav>

      {/* Main Content */}
      <main 
        ref={mainRef}
        className="flex-1 p-4 md:p-8 overflow-y-auto"
      >
        <AnimatePresence mode="wait">
          {renderContent()}
        </AnimatePresence>
      </main>

      {/* Scroll to top button */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            className="fixed bottom-8 right-8 p-3 bg-black text-white rounded-full 
              shadow-lg hover:bg-gray-800 transition-colors"
          >
            <ChevronRight className="transform -rotate-90" size={24} />
          </motion.button>
        )}
      </AnimatePresence>

      {/* Mobile Menu Button */}
      <motion.button
        className="md:hidden fixed bottom-8 left-8 p-3 bg-black text-white 
          rounded-full shadow-lg hover:bg-gray-800 transition-colors"
        onClick={() => setIsMobileMenuOpen(true)}
      >
        <Menu size={24} />
      </motion.button>

      {/* Mobile Navigation */}
      <MobileNavigation
        isOpen={isMobileMenuOpen}
        setIsOpen={setIsMobileMenuOpen}
      >
        <Navigation
          activeSection={activeSection}
          setActiveSection={(section) => {
            setActiveSection(section);
            setIsMobileMenuOpen(false);
          }}
          isMobile
        />
      </MobileNavigation>

      {/* Cursor Highlight Effect */}
      <CursorHighlight />
    </div>
  );
};

export default HomePage;
