// App.js
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
  Linkedin,
  X,
  ExternalLink,
  ChevronRight,
  Camera,
  Palette,
  Terminal,
  Monitor,
  Maximize,
  Minimize,
  Menu,
  Play,
  ChevronUp
} from 'lucide-react';

// Import UI components from shadcn/ui
import {
  Card,
  CardHeader,
  CardTitle,
  CardContent,
} from '@/components/ui/card';

// Glitch Art Images
const glitchImages = [
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/1.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/2.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/3.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/4.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/6.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/7.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/9.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/12.png',
];

// Analog Photography Images
const analogImages = [
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/1.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/2.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/3.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/4.jpg',
];

// Profile picture
const profilePicture = 'https://avatars.githubusercontent.com/u/183303841?s=400&u=15ae10f7b65b15a10c0ddd7a9d12efbb608014f4&v=4';

// Animation variants
const fadeInUp = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -20 },
  transition: { duration: 0.5 }
};

const stagger = {
  animate: {
    transition: {
      staggerChildren: 0.1
    }
  }
};

const sidebar = {
  initial: { x: -50, opacity: 0 },
  animate: { x: 0, opacity: 1, transition: { duration: 0.5, delay: 0.2 } }
};

// Section Title Component
const SectionTitle = ({ children }) => (
  <motion.h1 
    className="text-4xl font-bold text-gray-900 mb-8"
    variants={fadeInUp}
    initial="initial"
    animate="animate"
    exit="exit"
  >
    {children}
  </motion.h1>
);

// Skills Data Structure
const skillsData = {
  compositing: {
    title: "Compositing",
    description: "Advanced compositing with expertise in node-based and layer-based workflows.",
    skills: [
      "Advanced Rotoscoping",
      "Complex Node Setups",
      "Color Grading",
      "Shot Cleanup",
      "3D Integration",
      "Digital Matte Painting"
    ],
    tools: [
      "Nuke",
      "After Effects",
      "DaVinci Resolve",
      "Mocha Pro",
      "Silhouette"
    ],
    projects: [
      { name: "Proud Princess", link: "#" },
      { name: "Panasonic Campaign", link: "#" }
    ],
    icon: Monitor
  },
  technicalDirection: {
    title: "Technical Direction",
    description: "Development of efficient pipelines and automation tools for VFX production.",
    skills: [
      "Pipeline Architecture Design",
      "Custom Tool Development",
      "Workflow Automation",
      "Technical Documentation",
      "Render Farm Management",
      "Quality Control Systems"
    ],
    tools: [
      "Python",
      "Nuke Scripting",
      "Perforce",
      "JIRA",
      "Git"
    ],
    projects: [
      { name: "Rosa & Dara Pipeline", link: "#" },
      { name: "Automated QC Tool", link: "#" }
    ],
    icon: Terminal
  },
  motionDesign: {
    title: "Motion Design",
    description: "Creating engaging motion graphics and animations for various media.",
    skills: [
      "2D and 3D Animation",
      "Brand Identity Animation",
      "Title Sequences",
      "Animated Infographics",
      "Visual Storytelling",
      "Motion Typography"
    ],
    tools: [
      "Adobe After Effects",
      "Cinema 4D",
      "Adobe Premiere Pro",
      "Apple Motion"
    ],
    projects: [
      { name: "Corporate Branding", link: "#" },
      { name: "Animated Infographics", link: "#" }
    ],
    icon: Clapperboard
  }
};

// Skill Card Component
const SkillCard = ({ 
  title, 
  description, 
  skills = [], 
  tools = [], 
  projects = [], 
  icon: Icon 
}) => {
  const [isExpanded, setIsExpanded] = useState(false);

  return (
    <Card className="w-full">
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            {Icon && (
              <div className="p-2 bg-gray-100 rounded-lg">
                <Icon className="h-6 w-6 text-gray-600" />
              </div>
            )}
            <CardTitle>{title}</CardTitle>
          </div>
          <button
            onClick={() => setIsExpanded(!isExpanded)}
            className="text-gray-500 hover:text-gray-700"
          >
            {isExpanded ? <Minimize className="h-5 w-5" /> : <Maximize className="h-5 w-5" />}
          </button>
        </div>
      </CardHeader>
      <CardContent>
        <p className="text-gray-600">{description}</p>

        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3 }}
              className="mt-4 space-y-4"
            >
              {/* Skills */}
              <div>
                <h4 className="text-sm font-medium text-gray-900 mb-2">Core Skills</h4>
                <div className="grid grid-cols-2 gap-2">
                  {skills.map((skill, index) => (
                    <div 
                      key={index}
                      className="text-sm text-gray-600 flex items-center space-x-2"
                    >
                      <div className="w-1.5 h-1.5 bg-gray-900 rounded-full" />
                      <span>{skill}</span>
                    </div>
                  ))}
                </div>
              </div>

              {/* Tools */}
              <div>
                <h4 className="text-sm font-medium text-gray-900 mb-2">Tools & Software</h4>
                <div className="flex flex-wrap gap-2">
                  {tools.map((tool, index) => (
                    <span
                      key={index}
                      className="px-3 py-1 bg-gray-100 rounded-full text-sm text-gray-700"
                    >
                      {tool}
                    </span>
                  ))}
                </div>
              </div>

              {/* Projects */}
              {projects.length > 0 && (
                <div>
                  <h4 className="text-sm font-medium text-gray-900 mb-2">Related Projects</h4>
                  <div className="space-y-2">
                    {projects.map((project, index) => (
                      <a
                        key={index}
                        href={project.link}
                        className="flex items-center text-gray-600 hover:text-gray-900"
                      >
                        <ChevronRight className="h-4 w-4 mr-1" />
                        <span className="text-sm">{project.name}</span>
                      </a>
                    ))}
                  </div>
                </div>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </CardContent>
    </Card>
  );
};

// Skills Section Component
const SkillsSection = () => {
  return (
    <motion.div
      variants={stagger}
      initial="initial"
      animate="animate"
      exit="exit"
      className="space-y-8"
    >
      <SectionTitle>Skills & Expertise</SectionTitle>

      <motion.div className="grid grid-cols-1 gap-6">
        {Object.values(skillsData).map((skill, index) => (
          <motion.div
            key={index}
            variants={fadeInUp}
            className="w-full"
          >
            <SkillCard {...skill} />
          </motion.div>
        ))}
      </motion.div>

      {/* Software Proficiency Section */}
      <motion.div
        variants={fadeInUp}
        className="mt-12"
      >
        <h3 className="text-xl font-semibold text-gray-900 mb-6">Software Proficiency</h3>
        <div className="grid grid-cols-1 gap-4">
          {[
            { name: "Nuke", proficiency: 90 },
            { name: "After Effects", proficiency: 85 },
            { name: "DaVinci Resolve", proficiency: 80 },
            { name: "Python", proficiency: 75 },
            { name: "TouchDesigner", proficiency: 70 },
            { name: "Resolume Arena", proficiency: 65 }
          ].map((software, index) => (
            <div
              key={index}
              className="bg-white p-4 rounded-lg border border-gray-200"
            >
              <div className="flex justify-between items-center mb-2">
                <span className="font-medium">{software.name}</span>
                <span className="text-sm text-gray-500">{software.proficiency}%</span>
              </div>
              <div className="h-2 bg-gray-100 rounded-full overflow-hidden">
                <motion.div
                  className="h-full bg-black"
                  initial={{ width: 0 }}
                  animate={{ width: `${software.proficiency}%` }}
                  transition={{ duration: 1, delay: index * 0.1 }}
                />
              </div>
            </div>
          ))}
        </div>
      </motion.div>
    </motion.div>
  );
};
// Main App Component
const App = () => {
  const [activeSection, setActiveSection] = useState('summary');
  const [showScroll, setShowScroll] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Handle scroll behavior
  useEffect(() => {
    const handleScroll = () => {
      setShowScroll(window.pageYOffset > 300);
    };

    window.addEventListener('scroll', handleScroll);
    document.title = "Martin Tomek";

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Navigation sections
  const sections = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'film', icon: Film, label: 'Film Projects' },
    { id: 'commercial', icon: Briefcase, label: 'Commercial Work' },
    { id: 'experience', icon: Code, label: 'Experience' },
    { id: 'skills', icon: Terminal, label: 'Skills' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' }
  ];

  // NavItem Component
  const NavItem = ({ id, icon: Icon, label, isActive, onClick }) => (
    <motion.button
      whileTap={{ scale: 0.98 }}
      onClick={onClick}
      className={`
        w-full flex items-center px-4 py-3 rounded-lg transition-colors duration-300
        ${isActive 
          ? 'bg-black text-white' 
          : 'text-gray-600 hover:text-black hover:bg-gray-100'
        }
      `}
    >
      <Icon size={20} className="min-w-[20px]" />
      <span className="ml-3 text-sm font-medium">{label}</span>
    </motion.button>
  );

  // ContactInfo Component
  const ContactInfo = ({ icon: Icon, text, href }) => (
    <motion.a
      whileTap={{ scale: 0.98 }}
      href={href}
      target="_blank"
      rel="noopener noreferrer"
