import React, { useState } from "react";
import { motion } from "framer-motion";

export const BlobDemo = () => {
  const [isActive, setIsActive] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Add mouse move handler
  const handleMouseMove = (e) => {
    const { currentTarget, clientX, clientY } = e;
    const { left, top } = currentTarget.getBoundingClientRect();
    setMousePosition({
      x: clientX - left,
      y: clientY - top
    });
  };

  return (
    <div className="flex flex-wrap gap-8 justify-center p-8">
      {/* Basic Blob - Updated with hover glow effect */}
      <motion.div
        className="w-64 h-64 bg-gradient-to-r from-purple-500 to-pink-500 rounded-[42%_58%_70%_30%] flex items-center justify-center shadow-lg hover:shadow-purple-500/50"
        animate={{
          borderRadius: [
            "42% 58% 70% 30%",
            "30% 60% 70% 40%",
            "60% 40% 30% 70%",
            "42% 58% 70% 30%"
          ],
          boxShadow: [
            "0 0 20px rgba(168, 85, 247, 0.4)",
            "0 0 40px rgba(168, 85, 247, 0.2)",
            "0 0 20px rgba(168, 85, 247, 0.4)",
          ]
        }}
        // ... rest of Basic Blob remains same ...
      >
        <span className="text-white font-bold text-lg">Glowing Blob</span>
      </motion.div>

      {/* Interactive Blob - Updated with mouse tracking */}
      <motion.div
        className="w-64 h-64 bg-gradient-to-r from-purple-500 to-pink-500 rounded-[42%_58%_70%_30%] flex items-center justify-center relative overflow-hidden"
        onMouseMove={handleMouseMove}
        animate={{
          borderRadius: [
            "42% 58% 70% 30%",
            "30% 60% 70% 40%",
            "60% 40% 30% 70%",
            "42% 58% 70% 30%"
          ]
        }}
        whileHover={{
          scale: 1.1,
          rotate: 15,
          transition: { duration: 0.3 }
        }}
      >
        <motion.div
          className="absolute w-20 h-20 bg-white/30 rounded-full blur-xl"
          animate={{
            x: mousePosition.x - 40,
            y: mousePosition.y - 40,
          }}
          transition={{ type: "spring", damping: 10 }}
        />
        <motion.span 
          className="text-white font-bold text-lg z-10"
          whileHover={{ scale: 1.2 }}
        >
          Move Mouse Here
        </motion.span>
      </motion.div>

      {/* Morphing Button - Updated with particle effects */}
      <motion.button
        className="relative w-64 h-64 bg-gradient-to-r from-purple-500 to-pink-500 overflow-hidden"
        animate={{
          borderRadius: isActive
            ? ["42% 58% 70% 30%", "35% 65% 60% 40%", "50% 50% 50% 50%"]
            : ["42% 58% 70% 30%", "30% 60% 70% 40%", "60% 40% 30% 70%"],
          rotate: isActive ? 360 : 0,
          scale: isActive ? 1.2 : 1
        }}
        onClick={() => setIsActive(!isActive)}
        // ... rest of button animation props remain same ...
      >
        {/* Add floating particles */}
        {isActive && Array.from({ length: 8 }).map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-2 h-2 bg-white rounded-full"
            animate={{
              x: [0, (i % 2 ? 100 : -100) * Math.random()],
              y: [-20, -120],
              opacity: [1, 0],
              scale: [1, 0.5]
            }}
            transition={{
              duration: 1,
              repeat: Infinity,
              delay: i * 0.1
            }}
            style={{
              left: `${50 + (Math.random() - 0.5) * 40}%`,
              top: "50%"
            }}
          />
        ))}

        <motion.div
          className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-500 mix-blend-overlay"
          animate={{
            opacity: isActive ? 1 : 0,
            background: isActive 
              ? "linear-gradient(45deg, #3b82f6, #8b5cf6)"
              : "linear-gradient(45deg, #ec4899, #8b5cf6)"
          }}
          transition={{ duration: 0.3 }}
        />
        {/* ... rest of button content remains same ... */}
      </motion.button>
    </div>
  );
};
