// ====================
// Component: ProjectCard
// ====================

const ProjectCard = ({
  title,
  description,
  duration,
  details,
  technologies,
  media,
  mediaType,
  link,
  className = '',
  icon: Icon,
  variant = 'default',
  videoUrl,
  category,
  studio, // New prop for studio information
  alwaysExpanded = false, // New prop with default value
  isExpandable = true // New prop to control expandability
}) => {
  // Initialize isExpanded based on alwaysExpanded
  const [isExpanded, setIsExpanded] = useState(alwaysExpanded);
  const [showGallery, setShowGallery] = useState(false);
  const [showVideo, setShowVideo] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [ref, isInView] = useInView({ threshold: 0.2 });
  const controls = useAnimation();

  useEffect(() => {
    if (isInView) {
      controls.start({ opacity: 1, y: 0 });
    }
  }, [isInView, controls]);

  const variants = {
    default: {
      background: 'bg-white',
      text: 'text-gray-900',
      hover: 'hover:shadow-lg hover:border-gray-300'
    },
    featured: {
      background: 'bg-gradient-to-br from-gray-900 to-gray-800',
      text: 'text-white',
      hover: 'hover:shadow-xl hover:shadow-gray-900/20'
    },
    outline: {
      background: 'bg-white',
      text: 'text-gray-900',
      hover: 'hover:border-black'
    }
  };

  const currentVariant = variants[variant];

  // Determine if the card can be expanded
  const canExpand = isExpandable && !alwaysExpanded;

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      animate={controls}
      transition={{ duration: 0.3 }}
      className={`
        rounded-xl overflow-hidden border transition-all duration-300
        ${currentVariant.background} ${currentVariant.hover} ${className}
        ${canExpand ? 'cursor-pointer' : ''}
      `}
      onClick={() => {
        if (canExpand) { // Only toggle if expandable
          setIsExpanded(!isExpanded);
        }
      }}
    >
      <div className="p-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            {Icon && (
              <div className={`p-2 rounded-lg ${
                variant === 'featured' ? 'bg-white/10' : 'bg-gray-100'
              }`}>
                <Icon 
                  size={24} 
                  className={variant === 'featured' ? 'text-white' : 'text-gray-600'} 
                />
              </div>
            )}
            <div>
              <h3 className={`text-xl font-semibold ${currentVariant.text}`}>
                {title}
              </h3>
              {duration && (
                <p className={`text-sm mt-1 ${
                  variant === 'featured' ? 'text-white/60' : 'text-gray-500'
                }`}>
                  {duration}
                </p>
              )}
              {studio && (
                <p className={`text-sm mt-0.5 ${
                  variant === 'featured' ? 'text-white/60' : 'text-gray-500'
                }`}>
                  {studio}
                </p>
              )}
            </div>
          </div>

          {/* Toggle Button */}
          {canExpand && (
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={(e) => {
                e.stopPropagation();
                setIsExpanded(!isExpanded);
              }}
              className={variant === 'featured' ? 'text-white/80' : 'text-gray-500'}
            >
              {isExpanded ? (
                <Minimize size={20} />
              ) : (
                <Maximize size={20} />
              )}
            </motion.button>
          )}
        </div>

        {/* Main Content */}
        <div className={`mt-4 ${variant === 'featured' ? 'text-white/80' : 'text-gray-600'}`}>
          {description}
        </div>

        {/* Technologies */}
        {technologies && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.2 }}
            className="flex flex-wrap gap-2 mt-4"
          >
            {technologies.map((tech, index) => (
              <span
                key={index}
                className={`px-3 py-1 rounded-full text-sm ${
                  variant === 'featured'
                    ? 'bg-white/10 text-white'
                    : 'bg-gray-100 text-gray-700'
                }`}
              >
                {tech}
              </span>
            ))}
          </motion.div>
        )}

        {/* Expanded Content */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3 }}
              className="mt-6 overflow-hidden"
            >
              {/* Details */}
              {details && (
                <div className={`prose prose-sm max-w-none ${variant === 'featured' ? 'text-white/80' : 'text-gray-700'}`}>
                  {details}
                </div>
              )}

              {/* Media Grid */}
              {media && mediaType === 'image' && (
                <motion.div 
                  variants={animationVariants.container}
                  initial="hidden"
                  animate="show"
                  className="grid grid-cols-2 gap-4 mt-4"
                >
                  {media.map((src, index) => (
                    <motion.div
                      key={index}
                      variants={animationVariants.item}
                      whileHover={{ scale: 1.05 }}
                      className="relative group cursor-pointer"
                      onClick={(e) => {
                        e.stopPropagation();
                        setCurrentImageIndex(index);
                        setShowGallery(true);
                      }}
                    >
                      <img
                        src={src}
                        alt={`${title} ${index + 1}`}
                        className="w-full h-48 object-cover rounded-lg"
                      />
                      <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 
                        transition-opacity duration-200 rounded-lg flex items-center justify-center">
                        <Maximize className="text-white" size={24} />
                      </div>
                    </motion.div>
                  ))}
                </motion.div>
              )}

              {/* Video Preview */}
              {videoUrl && (
                <div className="mt-4">
                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    className="w-full relative aspect-video rounded-lg overflow-hidden 
                      bg-gray-900 flex items-center justify-center group"
                    onClick={(e) => {
                      e.stopPropagation();
                      setShowVideo(true);
                    }}
                  >
                    <div className="absolute inset-0 bg-black/50 group-hover:bg-black/30 
                      transition-colors duration-200" />
                    <Play className="text-white w-16 h-16 group-hover:scale-110 
                      transition-transform duration-200" />
                  </motion.button>
                </div>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </div>

      {/* Modals */}
      <AnimatePresence>
        {showGallery && (
          <ImageGalleryModal
            images={media}
            currentIndex={currentImageIndex}
            setCurrentIndex={setCurrentImageIndex}
            onClose={() => setShowGallery(false)}
          />
        )}
        {showVideo && (
          <VideoModal
            url={videoUrl}
            title={title}
            onClose={() => setShowVideo(false)}
          />
        )}
      </AnimatePresence>
    </motion.div>
  );
};
