import React from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';

function BlogPost() {
  const { slug } = useParams();

  const blogPosts = {
    'nuke-advanced-grab-tool': {
      title: "Transform Your Compositing Workflow with Nuke Advanced Grab Tool v3.8",
      date: "November 9, 2024",
      author: "Martin Tomek",
      tags: ["Nuke", "Python", "Workflow", "Technical Guide"],
      videoId: "your-video-id-6",
      content: [
        {
          type: 'paragraph',
          content: "In the fast-paced realm of visual effects and compositing, efficiency and precision are not just advantages—they're necessities. As projects grow in complexity, managing the Node Graph in Nuke can become a daunting task. Today, we're introducing the Nuke Advanced Grab Tool v3.8, a transformative script designed to elevate your node manipulation capabilities."
        },
        {
          type: 'heading',
          content: "What is the Advanced Grab Tool?"
        },
        {
          type: 'paragraph',
          content: "The Advanced Grab Tool is a Python script that enhances Nuke's node movement behavior, offering greater control and efficiency in your workflow. Inspired by Blender's Grab Tool, this script allows you to move nodes, input trees, or entire connected node trees with unprecedented ease and precision."
        },
        {
          type: 'heading',
          content: "Key Features"
        },
        {
          type: 'paragraph',
          content: "• Standard Grab (E): Move selected nodes\n• Input Tree Grab (Ctrl+Alt+E): Move selected nodes and upstream dependencies\n• Full Tree Grab (Ctrl+E): Move entire connected node tree\n• Axis Locking: Lock movement to X or Y axis\n• Zoom-Level Handling: Consistent movement regardless of zoom level\n• Movement Freezing: Hold middle mouse button or Alt + Left-click to freeze\n• Undo Support: Fully integrated with Nuke's undo system"
        },
        {
          type: 'heading',
          content: "Real-World Applications"
        },
        {
          type: 'paragraph',
          content: "Imagine working on a complex compositing project with hundreds of nodes. You need to reorganize your keying setup while maintaining all the connections to your grain, grade, and output nodes. With the Advanced Grab Tool, simply select your key nodes, use Input Tree Grab, and move everything together—no more tedious manual reorganization or risk of breaking connections."
        },
        {
          type: 'heading',
          content: "Installation Guide"
        },
        {
          type: 'paragraph',
          content: "1. Download NukeGrabTool.py from our GitHub repository\n2. Place in your .nuke directory (~/nuke/ on Linux/Mac, %userprofile%/.nuke/ on Windows)\n3. Add 'import NukeGrabTool' to your menu.py\n4. Restart Nuke to activate the tool"
        },
        {
          type: 'heading',
          content: "Using the Tool"
        },
        {
          type: 'paragraph',
          content: "1. Select your nodes in the Node Graph\n2. Press 'E' for standard grab mode\n3. Move your nodes with the mouse\n4. Press 'Z' or 'Y' to lock movement to an axis\n5. Click or press 'E' again to confirm placement\n6. Press 'Esc' to cancel the operation"
        },
        {
          type: 'heading',
          content: "System Requirements"
        },
        {
          type: 'paragraph',
          content: "• Nuke 12.1 or newer (required for PySide2 compatibility)\n• Compatible with Windows, macOS, and Linux\n• Minimal system overhead—works smoothly even with complex node trees"
        },
        {
          type: 'heading',
          content: "Pro Tips"
        },
        {
          type: 'paragraph',
          content: "• Use Full Tree Grab (Ctrl+E) when reorganizing entire effect chains\n• Combine with axis locking for perfect vertical or horizontal alignment\n• Customize shortcuts in menu.py to match your workflow\n• Keep your node trees organized by color-coding different sections"
        },
        {
          type: 'heading',
          content: "Future Development"
        },
        {
          type: 'paragraph',
          content: "We're actively working on new features including:\n• Snap-to-grid functionality\n• Custom alignment guides\n• Enhanced multi-selection tools\n• Integration with popular node presets"
        },
        {
          type: 'heading',
          content: "Community Support"
        },
        {
          type: 'paragraph',
          content: "Join our growing community of compositing artists using the Advanced Grab Tool. Share your experiences, suggest improvements, or contribute to the development on our GitHub repository. Your feedback helps shape the future of this tool."
        },
        {
          type: 'heading',
          content: "Get Started"
        },
        {
          type: 'paragraph',
          content: "Transform your Nuke workflow today. Download the Advanced Grab Tool from our GitHub repository and join the community of artists who are already benefiting from enhanced node manipulation capabilities.\n\nHappy compositing!"
        }
      ]
    }
  };

  const post = blogPosts[slug];

  if (!post) {
    return <div className="container mx-auto px-4 py-24">Post not found</div>;
  }

  return (
    <motion.article
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="max-w-4xl mx-auto px-4 py-24"
    >
      <div className="mb-8">
        <span className="text-purple-400">{post.date}</span>
        <h1 className="text-4xl font-bold mt-2 mb-4">{post.title}</h1>
        <div className="flex items-center gap-2 text-gray-400">
          <span>By {post.author}</span>
          <span>•</span>
          <div className="flex gap-2">
            {post.tags.map(tag => (
              <span key={tag} className="text-sm bg-white/10 px-3 py-1 rounded-full">
                {tag}
              </span>
            ))}
          </div>
        </div>
      </div>

      <div className="relative mb-12 aspect-video">
        <div className="absolute inset-0 border-2 border-purple-500/20 rounded-lg overflow-hidden">
          <iframe
            width="100%"
            height="100%"
            src={`https://www.youtube.com/embed/${post.videoId}`}
            title={post.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
            className="absolute inset-0 w-full h-full"
          ></iframe>
        </div>
      </div>

      <div className="prose prose-invert prose-purple max-w-none">
        {post.content.map((section, index) => {
          switch (section.type) {
            case 'paragraph':
              return <p key={index} className="text-gray-300 leading-relaxed mb-6">{section.content}</p>;
            case 'heading':
              return <h2 key={index} className="text-2xl font-bold mt-8 mb-4">{section.content}</h2>;
            default:
              return null;
          }
        })}
      </div>
    </motion.article>
  );
}

export default BlogPost;
