// pages/index.js
import React, { useState, useRef, useEffect } from 'react';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

// Glitch Art Images (using raw GitHub URLs)
const glitchImages = [
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/1.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/2.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/3.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/4.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/6.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/7.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/9.png',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/Glitch/12.png',
];

// Analog Photography Images (updated to use raw GitHub URLs)
const analogImages = [
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/1.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/2.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/3.jpg',
  'https://raw.githubusercontent.com/Themolx/WEB/0b3bbdc1f4fead85162a2cf826e439b9621dd400/assets/foto/4.jpg',
];

// Your profile picture
const profilePicture =
  'https://avatars.githubusercontent.com/u/183303841?s=400&u=15ae10f7b65b15a10c0ddd7a9d12efbb608014f4&v=4';

/**
 * ProjectCard Component
 * Handles individual project display, hover effects, and media controls.
 */
const ProjectCard = ({
  title,
  description,
  duration,
  moreInfo,
  media,
  mediaType, // 'video' or 'image'
  alwaysExpanded = false, // Controls expansion behavior
  className = '', // Allows additional class names for styling
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const mediaRefs = useRef([]);

  // Pause all media except the one being played
  const handlePlay = (currentIndex) => {
    mediaRefs.current.forEach((mediaElement, index) => {
      if (mediaElement && index !== currentIndex) {
        if (mediaType === 'video') {
          mediaElement.pause();
        }
      }
    });
  };

  // Pause all media when hover ends (if not always expanded)
  useEffect(() => {
    if (!isHovered && !alwaysExpanded) {
      mediaRefs.current.forEach((mediaElement) => {
        if (mediaElement) {
          if (mediaType === 'video') {
            mediaElement.pause();
          }
        }
      });
    }
  }, [isHovered, alwaysExpanded, mediaType]);

  // Determine whether to show additional content
  const showAdditionalContent = alwaysExpanded || isHovered;

  // For Skills page hover effect
  const [showDetails, setShowDetails] = useState(false);

  return (
    <motion.div
      className={`bg-white border border-gray-200 rounded-xl p-6 hover:shadow-xl transition-shadow duration-300 ${className}`}
      onMouseEnter={() => {
        setIsHovered(true);
        setShowDetails(true);
      }}
      onMouseLeave={() => {
        setIsHovered(false);
        setShowDetails(false);
      }}
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, ease: 'easeOut' }}
      whileHover={{ scale: 1.02, boxShadow: '0px 10px 30px rgba(0,0,0,0.1)' }}
    >
      <div className="flex items-center">
        <div>
          <h3 className="text-xl font-semibold text-gray-900 mb-1">{title}</h3>
          {duration && <p className="text-sm text-gray-500 mb-1">{duration}</p>}
        </div>
      </div>
      <p className="text-gray-600 whitespace-pre-line mt-2">{description}</p>

      {/* Additional Content */}
      {showAdditionalContent && moreInfo && (
        <div className="mt-4">
          {/* Display media based on type */}
          {media && (
            <div className="mb-4">
              {mediaType === 'video' ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {media.map((mediaSrc, index) => (
                    <motion.video
                      key={index}
                      src={mediaSrc}
                      controls
                      className="w-full rounded-lg"
                      ref={(el) => (mediaRefs.current[index] = el)}
                      onPlay={() => handlePlay(index)}
                      initial={{ opacity: 0, y: 50 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ duration: 0.5, delay: index * 0.1 }}
                      whileHover={{ scale: 1.02 }}
                    />
                  ))}
                </div>
              ) : mediaType === 'image' ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {media.map((mediaSrc, index) => (
                    <motion.img
                      key={index}
                      src={mediaSrc}
                      alt={`${title} ${index + 1}`}
                      className="w-full h-auto rounded-lg object-cover"
                      initial={{ opacity: 0, scale: 0.95 }}
                      animate={{ opacity: 1, scale: 1 }}
                      transition={{ duration: 0.5, delay: index * 0.1 }}
                      whileHover={{ scale: 1.05 }}
                    />
                  ))}
                </div>
              ) : null}
            </div>
          )}
          {/* Display additional information */}
          {moreInfo}
        </div>
      )}

      {/* Skills page hover details */}
      {showDetails && !moreInfo && (
        <div className="mt-4">
          <p className="text-gray-700 leading-relaxed">
            Experience and proficiency details can be added here.
          </p>
        </div>
      )}
    </motion.div>
  );
};

/**
 * Main App Component
 */
const HomePage = () => {
  const [activeSection, setActiveSection] = useState('summary');

  // Update the page title on component mount
  useEffect(() => {
    document.title = 'Martin Tomek - Portfolio';
  }, []);

  // Navigation items
  const sections = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'film', icon: Film, label: 'Film Projects' },
    { id: 'experience', icon: Briefcase, label: 'Experience' },
    { id: 'skills', icon: Code, label: 'Skills' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' },
  ];

  /**
   * NavItem Component
   * Handles individual navigation button styling and functionality.
   */
  const NavItem = ({ id, icon: Icon, label }) => (
    <motion.button
      onClick={() => setActiveSection(id)}
      className={`w-full flex items-center px-4 py-3 rounded-lg transition-all duration-200 ${
        activeSection === id
          ? 'bg-black text-white'
          : 'text-gray-600 hover:bg-gray-100 hover:text-black'
      }`}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      animate={{
        backgroundColor: activeSection === id ? '#000000' : '#FFFFFF',
        color: activeSection === id ? '#FFFFFF' : '#4B5563',
      }}
      transition={{ duration: 0.3 }}
    >
      <Icon size={20} className="min-w-[20px]" />
      <span className="ml-3 text-sm font-medium whitespace-nowrap">{label}</span>
    </motion.button>
  );

  /**
   * ContactInfo Component
   * Displays contact information with icons.
   */
  const ContactInfo = ({ icon: Icon, text, href }) => (
    <a
      href={href}
      className="flex items-center text-gray-600 hover:text-black transition-colors"
      target="_blank"
      rel="noopener noreferrer"
    >
      <Icon size={16} className="mr-2" />
      <span className="text-sm">{text}</span>
    </a>
  );

  /**
   * Renders content based on the active section.
   */
  const renderContent = () => {
    return (
      <AnimatePresence exitBeforeEnter>
        {activeSection === 'summary' && (
          <motion.div
            key="summary"
            className="space-y-6"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -50 }}
            transition={{ duration: 0.3 }}
          >
            {/* Showreel Video */}
            <div className="mb-8">
              <motion.div
                className="relative pb-[56.25%] h-0 overflow-hidden rounded-xl shadow-lg"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.5, delay: 0.2 }}
              >
                <iframe
                  src="https://player.vimeo.com/video/1016090207"
                  frameBorder="0"
                  allow="autoplay; fullscreen; picture-in-picture"
                  allowFullScreen
                  className="absolute top-0 left-0 w-full h-full"
                ></iframe>
              </motion.div>
            </div>
            <motion.h1
              className="text-4xl font-bold text-gray-900 mb-4"
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.3 }}
            >
              Hello, I'm <span className="text-indigo-600">Martin Tomek</span>
            </motion.h1>
            <motion.p
              className="text-lg text-gray-700 max-w-3xl leading-relaxed"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, delay: 0.4 }}
            >
              I'm a <strong>Mid-Level Compositor</strong> and{' '}
              <strong>VFX Artist</strong> based in{' '}
              <strong>Prague, Czech Republic</strong>, with a passion for{' '}
              <strong>visual storytelling</strong> and a keen eye for detail. With over{' '}
              <strong>three years of experience</strong> in compositing and motion graphics, I
              specialize in using <strong>Nuke</strong> and <strong>After Effects</strong> to
              create seamless visual effects.
            </motion.p>
            <motion.p
              className="text-lg text-gray-700 max-w-3xl leading-relaxed"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, delay: 0.5 }}
            >
              I'm always eager to embrace new challenges and opportunities, and I'm willing to
              relocate, with a particular interest in the <strong>Canary Islands</strong>.
            </motion.p>
            {/* Contact Me Button */}
            <motion.button
              onClick={() => {
                window.location.href = 'mailto:martintomek.vfx@gmail.com';
              }}
              className="mt-6 px-6 py-3 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 transition-colors"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, delay: 0.6 }}
            >
              Contact Me
            </motion.button>
          </motion.div>
        )}
        {/* Repeat similar structures for other sections ('film', 'experience', etc.) */}
        {activeSection === 'film' && (
          <motion.div
            key="film"
            className="space-y-6"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -50 }}
            transition={{ duration: 0.3 }}
          >
            <h1 className="text-4xl font-bold text-gray-900 mb-4">Film Projects</h1>
            <motion.div
              className="grid grid-cols-1 md:grid-cols-2 gap-6"
              initial="hidden"
              animate="visible"
              variants={{
                visible: {
                  transition: {
                    staggerChildren: 0.2,
                  },
                },
                hidden: {},
              }}
            >
              <ProjectCard
                title="Proud Princess"
                duration="PFX Studio | September 2024 - October 2024"
                description="- Compositing work on the feature film."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">Project Details:</h4>
                    <p className="text-gray-700 leading-relaxed">
                      Collaborated with the VFX team to integrate visual effects seamlessly into
                      live-action footage, ensuring high-quality deliverables under tight deadlines.
                    </p>
                  </div>
                }
                media={null}
                mediaType={null}
              />
              <ProjectCard
                title="Rosa & Dara a jejich velké letní dobrodružství"
                duration="Incognito Studio | August 2021 - Present"
                description="- Compositor and developer for a 3D feature film."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">Key Responsibilities:</h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
                      <li>Lead compositor for character integration scenes.</li>
                      <li>Developed custom Nuke tools for pipeline optimization.</li>
                      <li>Collaborated with 3D artists for seamless integration.</li>
                    </ul>
                  </div>
                }
                media={null}
                mediaType={null}
              />
            </motion.div>
          </motion.div>
        )}
        {/* Add similar blocks for 'experience', 'skills', and 'personal' */}
      </AnimatePresence>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col md:flex-row">
      {/* Sidebar */}
      <motion.nav
        initial={{ x: -250 }}
        animate={{ x: 0 }}
        transition={{ type: 'spring', stiffness: 100, damping: 20 }}
        className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r border-gray-200 p-6"
      >
        <div className="flex flex-col md:h-full">
          {/* Profile Picture */}
          <div className="mb-6 flex items-center">
            <motion.img
              src={profilePicture}
              alt="Profile"
              className="w-16 h-16 rounded-full object-cover mr-4"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, delay: 0.1 }}
            />
            <div>
              <motion.h2
                className="text-2xl font-bold text-gray-900"
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.2 }}
              >
                Martin Tomek
              </motion.h2>
              <motion.p
                className="text-sm text-gray-500 mt-1"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.5, delay: 0.3 }}
              >
                Motion Designer & VFX Artist
              </motion.p>
            </div>
          </div>

          {/* Contact Info */}
          <div className="mb-6 space-y-2">
            <ContactInfo icon={MapPin} text="Prague, Czech Republic" href="#" />
            <ContactInfo
              icon={Mail}
              text="martintomek.vfx@gmail.com"
              href="mailto:martintomek.vfx@gmail.com"
            />
            <ContactInfo icon={Phone} text="+420 774 630 132" href="tel:+420774630132" />
            <ContactInfo icon={Github} text="github.com/Themolx" href="https://github.com/Themolx" />
          </div>

          {/* Navigation */}
          <div className="space-y-2">
            {sections.map((section) => (
              <NavItem
                key={section.id}
                id={section.id}
                icon={section.icon}
                label={section.label}
              />
            ))}
          </div>

          {/* Footer */}
          <div className="mt-auto pt-6 border-t border-gray-200">
            <motion.p
              className="text-sm text-gray-500"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, delay: 0.4 }}
            >
              Available for new opportunities
            </motion.p>
            <motion.p
              className="text-sm text-gray-500 mt-1"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, delay: 0.5 }}
            >
              Willing to relocate to Canary Islands
            </motion.p>
            <motion.a
              href="#"
              className="flex items-center mt-4 text-gray-600 hover:text-black transition-colors"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.6 }}
              whileHover={{ scale: 1.05 }}
            >
              <Download size={16} className="mr-2" />
              <span className="text-sm">Download Resume</span>
            </motion.a>
          </div>
        </div>
      </motion.nav>

      {/* Main Content */}
      <main className="flex-1 p-4 md:p-8 overflow-y-auto pb-[600px]">
        {renderContent()}
      </main>
    </div>
  );
};

export default HomePage;
