// Enhanced ProjectCard Component
const ProjectCard = ({
  title,
  description,
  duration,
  details,
  technologies,
  media,
  mediaType,
  link,
  className = '',
  icon: Icon
}) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [showGallery, setShowGallery] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [ref, isInView] = useInView({ threshold: 0.2 });

  const handleCardClick = (e) => {
    e.preventDefault();
    if (link) {
      window.open(link, '_blank');
    } else {
      setIsExpanded(!isExpanded);
    }
  };

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
      whileHover={{ y: -5 }}
      transition={{ duration: 0.5 }}
      className={`bg-white border border-gray-200 rounded-xl overflow-hidden cursor-pointer ${className}`}
      onClick={handleCardClick}
    >
      <div className="p-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            {Icon && <Icon size={24} className="text-gray-600" />}
            <h3 className="text-xl font-semibold text-gray-900">{title}</h3>
          </div>
          {link ? (
            <ExternalLink size={20} className="text-gray-500" />
          ) : (
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={(e) => {
                e.stopPropagation();
                setIsExpanded(!isExpanded);
              }}
            >
              {isExpanded ? (
                <Minus size={20} className="text-gray-500" />
              ) : (
                <Plus size={20} className="text-gray-500" />
              )}
            </motion.button>
          )}
        </div>

        {duration && (
          <p className="text-sm text-gray-500 mt-2">{duration}</p>
        )}

        <p className="text-gray-600 mt-4">{description}</p>

        {technologies && (
          <div className="flex flex-wrap gap-2 mt-4">
            {technologies.map((tech, index) => (
              <span
                key={index}
                className="px-3 py-1 bg-gray-100 rounded-full text-sm text-gray-700"
              >
                {tech}
              </span>
            ))}
          </div>
        )}

        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              className="mt-6 space-y-4"
            >
              {details && (
                <div className="prose prose-sm max-w-none text-gray-700">
                  {details}
                </div>
              )}

              {media && mediaType === 'image' && (
                <div className="grid grid-cols-2 gap-4">
                  {media.map((src, index) => (
                    <motion.img
                      key={index}
                      src={src}
                      alt={`${title} ${index + 1}`}
                      className="w-full h-48 object-cover rounded-lg cursor-pointer"
                      whileHover={{ scale: 1.05 }}
                      onClick={(e) => {
                        e.stopPropagation();
                        setCurrentImageIndex(index);
                        setShowGallery(true);
                      }}
                    />
                  ))}
                </div>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </div>

      <AnimatePresence>
        {showGallery && (
          <ImageGalleryModal
            images={media}
            currentIndex={currentImageIndex}
            setCurrentIndex={setCurrentImageIndex}
            onClose={() => setShowGallery(false)}
          />
        )}
      </AnimatePresence>
    </motion.div>
  );
};

