// App.js
import React, { useState, useRef } from 'react';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
} from 'lucide-react';
import { useEffect } from 'react';

// Placeholder video URL for your showreel
const showreelVideo = 'https://www.w3schools.com/html/mov_bbb.mp4'; // Replace with your showreel URL

// Placeholder videos for personal projects
const placeholderVideos = [
  'https://www.w3schools.com/html/mov_bbb.mp4',
  'https://www.w3schools.com/html/movie.mp4',
  'https://www.w3schools.com/html/mov_bbb.mp4',
  'https://www.w3schools.com/html/movie.mp4',
];

const App = () => {
  const [activeSection, setActiveSection] = useState('summary');

  // For updating the page title
  useEffect(() => {
    document.title = 'Martin Tomek - Portfolio';
  }, []);

  const NavItem = ({ id, icon: Icon, label }) => (
    <button
      onClick={() => setActiveSection(id)}
      className={`w-full flex items-center px-4 py-3 rounded-lg transition-all duration-200 hover:bg-gray-100 ${
        activeSection === id
          ? 'bg-black text-white hover:bg-black/90'
          : 'text-gray-600 hover:text-black'
      }`}
    >
      <Icon size={20} className="min-w-[20px]" />
      <span className="ml-3 text-sm font-medium whitespace-nowrap">
        {label}
      </span>
    </button>
  );

  // Removed videoRefs and handlePlay from App component

  const ProjectCard = ({
    title,
    description,
    duration,
    moreInfo,
    media,
  }) => {
    // Move videoRefs and handlePlay inside ProjectCard
    const videoRefs = useRef([]);

    const handlePlay = (index) => {
      videoRefs.current.forEach((video, idx) => {
        if (video && idx !== index) {
          video.pause();
        }
      });
    };

    return (
      <div className="bg-white border border-gray-200 rounded-xl p-6 hover:shadow-xl transition-all duration-300 group">
        <div>
          <h3 className="text-xl font-semibold text-gray-900 mb-2">
            {title}
          </h3>
          {duration && (
            <p className="text-sm text-gray-500 mb-3">{duration}</p>
          )}
        </div>
        <p className="text-gray-600 whitespace-pre-line">{description}</p>

        {/* Additional Content */}
        {moreInfo && (
          <div className="overflow-hidden max-h-0 group-hover:max-h-[2000px] transition-all duration-500 ease-in-out mt-4">
            {media && (
              <div className="mb-4">
                {/* Display multiple videos */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {media.map((videoSrc, index) => (
                    <video
                      key={index}
                      src={videoSrc}
                      controls
                      className="w-full rounded-lg"
                      ref={(el) => (videoRefs.current[index] = el)}
                      onPlay={() => handlePlay(index)}
                    />
                  ))}
                </div>
              </div>
            )}
            {moreInfo}
          </div>
        )}
      </div>
    );
  };

  const sections = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'film', icon: Film, label: 'Film Projects' },
    { id: 'experience', icon: Briefcase, label: 'Experience' },
    { id: 'skills', icon: Code, label: 'Skills' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' },
  ];

  const ContactInfo = ({ icon: Icon, text, href }) => (
    <a
      href={href}
      className="flex items-center text-gray-600 hover:text-black transition-colors"
      target="_blank"
      rel="noopener noreferrer"
    >
      <Icon size={16} className="mr-2" />
      <span className="text-sm">{text}</span>
    </a>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'summary':
        return (
          <div className="space-y-6">
            {/* Showreel Video */}
            <div className="mb-8">
              <video
                src={showreelVideo}
                controls
                autoPlay
                muted
                loop
                className="w-full rounded-xl shadow-lg"
              />
            </div>
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Hello, I'm{' '}
              <span className="text-indigo-600">Martin Tomek</span>
            </h1>
            <p className="text-lg text-gray-700 max-w-3xl leading-relaxed">
              I'm a <strong>Mid-Level Compositor</strong> and{' '}
              <strong>Technical Director (TD)</strong> based in{' '}
              <strong>Prague, Czech Republic</strong>, with a passion for{' '}
              <strong>visual storytelling</strong> and a keen eye for
              detail. With over <strong>three years of experience</strong>{' '}
              in compositing, pipeline development, and motion graphics, I
              specialize in using <strong>Nuke</strong> for creating
              seamless visual effects and developing tailored VFX tools and
              scripts.
            </p>
            <p className="text-lg text-gray-700 max-w-3xl leading-relaxed">
              Currently, I work with <strong>Incognito Studio</strong> and{' '}
              <strong>PFX Studio</strong>, contributing to major projects
              like <em>"Proud Princess"</em> and{' '}
              <em>"Rosa & Dara a jejich velké letní dobrodružství"</em>, a
              3D feature film that blends 2D and 3D animation. My expertise
              extends to on-set VFX supervision and integrating analog and
              digital media, including <strong>VJing</strong> with{' '}
              <strong>Resolume Arena</strong> and{' '}
              <strong>TouchDesigner</strong>.
            </p>
            <p className="text-lg text-gray-700 max-w-3xl leading-relaxed">
              I'm always eager to embrace new challenges and opportunities,
              and I'm willing to relocate, with a particular interest in
              the <strong>Canary Islands</strong>.
            </p>
          </div>
        );
      case 'film':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Film Projects
            </h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title='"Proud Princess"'
                duration="PFX Studio | October 2023 - Present"
                description="Compositing work on the feature film."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">
                      Project Details:
                    </h4>
                    <p className="text-gray-700 leading-relaxed">
                      Collaborated with the VFX team to integrate visual
                      effects seamlessly into live-action footage, ensuring
                      high-quality deliverables under tight deadlines.
                    </p>
                  </div>
                }
                media={null} // Add media if available
              />
              <ProjectCard
                title='"Rosa & Dara a jejich velké letní dobrodružství"'
                duration="Incognito Studio | August 2021 - Present"
                description="Compositor and developer for a 3D feature film."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">
                      Key Responsibilities:
                    </h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
                      <li>
                        Lead compositor for character integration scenes.
                      </li>
                      <li>
                        Developed custom Nuke tools for pipeline
                        optimization.
                      </li>
                      <li>
                        Collaborated with 3D artists for seamless
                        integration.
                      </li>
                    </ul>
                  </div>
                }
                media={null} // Add media if available
              />
            </div>
          </div>
        );
      case 'experience':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Professional Experience
            </h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title="Mid Compositor & Technical Director"
                duration="Incognito Studio | August 2021 - Present"
                description="VFX compositing and development work for high-profile clients."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">Clients:</h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
                      <li>Panasonic Japan</li>
                      <li>Moneta Bank</li>
                      <li>McDonald's</li>
                      <li>
                        VFX Supervisor for the Creditas project
                      </li>
                    </ul>
                    <h4 className="font-semibold mb-2">Achievements:</h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
                      <li>
                        Implemented compositing workflows reducing render
                        times by 30%.
                      </li>
                      <li>
                        Developed custom scripts for pipeline optimization.
                      </li>
                    </ul>
                  </div>
                }
                media={null} // Add media if available
              />
              {/* Add more ProjectCards as needed */}
            </div>
          </div>
        );
      case 'skills':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Skills
            </h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title="Software Proficiency"
                description={`• Nuke
• After Effects
• DaVinci Resolve
• Adobe Creative Suite
• Resolume Arena
• TouchDesigner`}
              />
              <ProjectCard
                title="Pipeline & Workflow"
                description={`• OpenPype
• Ayon
• On-set VFX Supervision
• 2D and 3D Animation Integration
• VJing`}
              />
              <ProjectCard
                title="Technical Direction"
                description="Developed tailored scripts and tools for pipeline optimization in VFX production, enhancing efficiency and productivity."
              />
            </div>
          </div>
        );
      case 'personal':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Personal Projects
            </h1>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title="Experimental Animation & Glitch Art"
                description="Creating experimental animations and glitch art by integrating analog and digital media."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">Activities:</h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed">
                      <li>
                        Analog photography and alternative film development
                      </li>
                      <li>
                        VJing using Resolume Arena and TouchDesigner
                      </li>
                      <li>Mixed media art projects</li>
                    </ul>
                  </div>
                }
                media={placeholderVideos}
              />
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col md:flex-row">
      {/* Sidebar */}
      <nav className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r border-gray-200 p-6">
        <div className="flex flex-col md:h-full">
          {/* Logo */}
          <div className="mb-8">
            <h2 className="text-2xl font-bold text-gray-900">
              Martin Tomek
            </h2>
            <p className="text-sm text-gray-500 mt-1">
              Mid-Level Compositor & TD
            </p>
          </div>

          {/* Contact Info */}
          <div className="mb-6 space-y-2">
            <ContactInfo icon={MapPin} text="Prague, Czech Republic" />
            <ContactInfo
              icon={Mail}
              text="martintomek.vfx@gmail.com"
              href="mailto:martintomek.vfx@gmail.com"
            />
            <ContactInfo
              icon={Phone}
              text="+420 774 630 132"
              href="tel:+420774630132"
            />
          </div>

          {/* Navigation */}
          <div className="space-y-2">
            {sections.map((section) => (
              <NavItem
                key={section.id}
                id={section.id}
                icon={section.icon}
                label={section.label}
              />
            ))}
          </div>

          {/* Footer */}
          <div className="mt-auto pt-6 border-t border-gray-200 hidden md:block">
            <p className="text-sm text-gray-500">
              Available for new opportunities
            </p>
            <p className="text-sm text-gray-500 mt-1">
              Willing to relocate to Canary Islands
            </p>
            <a
              href="#"
              className="flex items-center mt-4 text-gray-600 hover:text-black transition-colors"
            >
              <Download size={16} className="mr-2" />
              <span className="text-sm">Download Resume</span>
            </a>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="flex-1 p-4 md:p-8 overflow-y-auto">
        {renderContent()}
      </main>
    </div>
  );
};

export default App;
