import React, { useState, useEffect } from 'react';
import { motion, useScroll } from 'framer-motion';
import { Link } from 'react-router-dom';

function Navbar() {
  const { scrollY } = useScroll();
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    return scrollY.onChange(() => setIsScrolled(scrollY.get() > 20));
  }, [scrollY]);

  return (
    <motion.nav
      className="fixed w-full z-50 py-4"
      initial={{ y: -100 }}
      animate={{ 
        y: 0,
        backgroundColor: isScrolled ? 'rgba(15, 15, 15, 0.8)' : 'transparent',
        backdropFilter: isScrolled ? 'blur(12px)' : 'none',
      }}
      transition={{ duration: 0.3 }}
    >
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between">
          <Link 
            to="/" 
            className="flex items-center gap-2 group"
          >
            <motion.div
              whileHover={{ scale: 1.05 }}
              className="bg-purple-600 p-2 rounded-lg"
            >
              <span className="text-xl font-bold">MT</span>
            </motion.div>
            <motion.span 
              initial={{ opacity: 0, x: -10 }}
              animate={{ opacity: 1, x: 0 }}
              className="text-sm font-medium text-gray-300 group-hover:text-white transition-colors"
            >
              Home
            </motion.span>
          </Link>

          <div className="flex items-center gap-4">
            <Link to="/shop" className="nav-link hover:text-purple-400 transition-colors">Shop</Link>
            <Link to="/blog" className="nav-link hover:text-purple-400 transition-colors">Blog</Link>
            <Link to="/about" className="nav-link hover:text-purple-400 transition-colors">About</Link>
          </div>
        </div>
      </div>
    </motion.nav>
  );
}

export default Navbar;
