import React, { useState, useRef } from "react";
import { motion, useAnimation } from "framer-motion";
import { useDrag } from "react-use-gesture";

export const BlobDemo = () => {
  const [isActive, setIsActive] = useState(false);
  const constraintsRef = useRef(null);
  const controls1 = useAnimation();
  const controls2 = useAnimation();
  const controls3 = useAnimation();

  // Create drag bindings for each blob
  const bind1 = useDrag(({ offset: [x, y] }) => {
    controls1.start({ x, y });
  });

  const bind2 = useDrag(({ offset: [x, y] }) => {
    controls2.start({ x, y });
  });

  const bind3 = useDrag(({ offset: [x, y] }) => {
    controls3.start({ x, y });
  });

  return (
    <div 
      ref={constraintsRef}
      className="min-h-screen flex flex-wrap gap-8 justify-center items-center p-8 bg-gradient-to-br from-purple-900 via-blue-900 to-purple-900 relative"
    >
      <motion.div 
        className="absolute inset-0"
        style={{ margin: "8rem" }}
      />

      {/* Purple Blob */}
      <motion.div
        {...bind1()}
        animate={controls1}
        className="w-64 h-64 relative cursor-grab rounded-full bg-gradient-to-r from-purple-500 to-pink-500 shadow-lg"
        whileTap={{ cursor: "grabbing", scale: 1.02 }}
        whileHover={{ scale: 1.05 }}
        drag
        dragConstraints={constraintsRef}
        dragElastic={0.2}
        dragMomentum={false}
      >
        <span className="absolute inset-0 flex items-center justify-center text-white font-semibold text-lg">
          Drag Me
        </span>
      </motion.div>

      {/* Blue Blob */}
      <motion.div
        {...bind2()}
        animate={controls2}
        className="w-64 h-64 cursor-grab rounded-full bg-gradient-to-tr from-teal-400 to-blue-500 shadow-lg flex items-center justify-center"
        whileTap={{ cursor: "grabbing", scale: 1.02 }}
        whileHover={{ scale: 1.05 }}
        drag
        dragConstraints={constraintsRef}
        dragElastic={0.2}
        dragMomentum={false}
      >
        <span className="text-white font-semibold text-lg">
          Also Draggable
        </span>
      </motion.div>

      {/* Green Blob */}
      <motion.div
        {...bind3()}
        animate={controls3}
        className="w-64 h-64 cursor-grab rounded-full bg-gradient-to-br from-green-400 to-yellow-500 shadow-lg flex items-center justify-center"
        whileTap={{ cursor: "grabbing", scale: 1.02 }}
        whileHover={{ scale: 1.05 }}
        onClick={() => setIsActive(!isActive)}
        drag
        dragConstraints={constraintsRef}
        dragElastic={0.2}
        dragMomentum={false}
      >
        <span className="text-white font-semibold text-lg">
          {isActive ? "Active" : "Drag or Click"}
        </span>
      </motion.div>
    </div>
  );
};
