import React from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import DownloadButton from '../components/blog/DownloadButton';

function BlogPost() {
  const { slug } = useParams();

  const blogPosts = {
    'nuke-advanced-grab-tool': {
      title: "ðŸš€ How I Built the Nuke Grab Tool (Without Being a Coding Expert)",
      date: "November 9, 2024",
      author: "Martin Tomek",
      tags: ["Nuke", "Python", "Workflow", "Technical Guide"],
      content: [
        {
          type: 'personal-intro',
          content: "You know that feeling when you're working in Nuke, and you keep thinking 'there has to be a better way'? That's exactly how this story begins. After spending countless hours in Blender's node editor and falling in love with its grab tool, I couldn't shake the thought: 'Why can't we have this in Nuke?'"
        },
        {
          type: 'journey-start',
          content: {
            title: "The 'Aha!' Moment ðŸ’¡",
            text: "I'm not a professional programmer - far from it. But one night, frustrated with moving nodes around in Nuke, I decided to try something different. I'd heard about this AI called Claude that was pretty good with Nuke's Python API. So I thought, 'What's the worst that could happen?'"
          }
        },
        {
          type: 'dialogue-section',
          content: {
            title: "My First Chat with Claude",
            conversation: [
              {
                me: "Hey Claude, I love how Blender's grab tool works with nodes. Could we make something similar for Nuke?",
                claude: "Absolutely! Let's break this down into simple steps. First, we'll need to capture the 'E' key press...",
                result: "20 minutes later, I had a working prototype. It wasn't perfect, but it WORKED! ðŸŽ‰"
              }
            ]
          }
        },
        {
          type: 'development-timeline',
          content: {
            title: "The Evolution ðŸ“ˆ",
            stages: [
              {
                date: "October 2",
                title: "The Birth",
                description: "First working version - basic but functional. I couldn't believe how quick it was!"
              },
              {
                date: "October 3",
                title: "Growing Pains",
                description: "Fixed scaling issues and made it work smoothly at different zoom levels (thanks to more chats with Claude)"
              },
              {
                date: "October 6",
                title: "The Game Changer",
                description: "Added Ctrl+Z support (because we all make mistakes!) and middle-click freeze"
              }
            ]
          }
        },
        {
          type: 'github-journey',
          content: {
            title: "Discovering GitHub ðŸŒŸ",
            text: "As I created more scripts for production, I realized I needed a better way to manage them. GitHub wasn't just for 'real programmers' - it became my digital toolbox, helping me track changes and share with others."
          }
        },
        {
          type: 'encouragement',
          content: {
            title: "Here's the Thing...",
            message: "If you're sitting there thinking 'I wish Nuke could do X' but you're not a coder - that's exactly where I was! With tools like Claude AI and a bit of curiosity, you might be surprised how quickly you can turn your ideas into reality.",
            tips: [
              "Start with a clear idea of what you want",
              "Break it down into simple steps",
              "Use Claude AI to help with the code",
              "Test in Nuke's Script Editor",
              "Don't be afraid to make mistakes"
            ]
          }
        },
        {
          type: 'try-it',
          content: {
            title: "Want to Try the Tool? ðŸŽ®",
            links: [
              {
                text: "Get it on Nukepedia",
                url: "https://www.nukepedia.com/python/nodegraph/nuke-grab-tool",
                description: "Ready to use version"
              },
              {
                text: "Check it out on GitHub",
                url: "https://github.com/Themolx/NukeScripts",
                description: "See how it evolved (and maybe contribute!)"
              }
            ]
          }
        },
        {
          type: 'closing-thoughts',
          content: "Remember: Every tool starts with someone thinking 'I wish this existed.' Sometimes that someone is you. So what tool do you wish existed? Maybe it's time to start building! ðŸ› ï¸"
        }
      ]
    }
  };

  const post = blogPosts[slug];

  const renderContent = (content) => {
    switch (content.type) {
      case 'paragraph':
        return <p className="text-gray-300 leading-relaxed mb-6">{content.content}</p>;

      case 'heading':
        return <h2 className="text-2xl font-bold text-white mt-12 mb-6">{content.content}</h2>;

      case 'download-section':
        return (
          <div className="flex flex-col sm:flex-row gap-4 my-8">
            <DownloadButton 
              url={content.content.nukepedia}
              text="Download from Nukepedia"
            />
            <DownloadButton 
              url={content.content.github}
              text="View on GitHub"
            />
          </div>
        );

      case 'video-demo':
        return (
          <div className="aspect-video w-full rounded-lg overflow-hidden my-8">
            <iframe
              src={`https://www.youtube.com/embed/${content.videoId}`}
              className="w-full h-full"
              allowFullScreen
            />
          </div>
        );

      case 'steps':
        return (
          <div className="space-y-6 my-8">
            {content.content.map((step, index) => (
              <div key={index} className="bg-white/5 rounded-lg p-6">
                <h3 className="text-xl font-semibold text-purple-400 mb-3">
                  {step.title}
                </h3>
                <p className="text-gray-300 whitespace-pre-line">
                  {step.description}
                </p>
              </div>
            ))}
          </div>
        );

      case 'code-example':
        return (
          <div className="my-8">
            <h3 className="text-xl font-semibold mb-4">{content.content.title}</h3>
            <pre className="bg-black/50 p-4 rounded-lg overflow-x-auto">
              <code className="text-sm text-gray-300">
                {content.content.code}
              </code>
            </pre>
          </div>
        );

      case 'try-yourself':
        return (
          <div className="bg-purple-900/20 rounded-lg p-6 my-8">
            <h3 className="text-xl font-semibold mb-4">{content.content.title}</h3>
            <ul className="list-decimal list-inside space-y-2 mb-6">
              {content.content.steps.map((step, index) => (
                <li key={index} className="text-gray-300">{step}</li>
              ))}
            </ul>
            <pre className="bg-black/50 p-4 rounded-lg overflow-x-auto">
              <code className="text-sm text-gray-300">
                {content.content.sampleCode}
              </code>
            </pre>
          </div>
        );

      default:
        return null;
    }
  };

  if (!post) {
    return <div className="container mx-auto px-4 py-24">Post not found</div>;
  }

  return (
    <motion.article
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="max-w-4xl mx-auto px-4 py-24"
    >
      <div className="mb-8">
        <span className="text-purple-400">{post.date}</span>
        <h1 className="text-4xl font-bold mt-2 mb-4">{post.title}</h1>
        <div className="flex items-center gap-2 text-gray-400">
          <span>By {post.author}</span>
          <span>â€¢</span>
          <div className="flex gap-2">
            {post.tags.map(tag => (
              <span key={tag} className="text-sm bg-white/10 px-3 py-1 rounded-full">
                {tag}
              </span>
            ))}
          </div>
        </div>
      </div>

      <div className="prose prose-invert max-w-none">
        {post.content.map((section, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            {renderContent(section)}
          </motion.div>
        ))}
      </div>
    </motion.article>
  );
}

export default BlogPost;
