import { useParams, useNavigate } from 'react-router-dom';
import { blogPosts } from '../../data/blogPosts';

function BlogPost() {
  const { slug } = useParams();
  const navigate = useNavigate();

  // Find the blog post by slug
  const post = blogPosts[slug];

  // If post doesn't exist, show error or redirect
  if (!post) {
    return (
      <div className="container mx-auto px-4 py-16">
        <h1 className="text-3xl text-red-400 mb-4">Post not found</h1>
        <p className="text-gray-400 mb-8">The blog post you're looking for doesn't exist.</p>
        <button 
          onClick={() => navigate('/blog')}
          className="text-purple-400 hover:text-purple-300"
        >
          ← Back to Blog
        </button>
      </div>
    );
  }

  return (
    <article className="container mx-auto px-4 py-16">
      <h1 className="text-4xl font-bold mb-4">{post.title}</h1>
      <div className="text-gray-400 mb-8">
        <time>{post.date}</time> • <span>{post.author}</span>
      </div>

      {post.content.map((section, index) => {
        switch (section.type) {
          case 'intro':
            return <p key={index} className="text-xl mb-8">{section.content}</p>;
          case 'section':
            return (
              <div key={index} className="mb-12">
                <h2 className="text-2xl font-bold mb-6">{section.title}</h2>
                {section.content.map((tool, toolIndex) => (
                  <div key={toolIndex} className="mb-8">
                    <h3 className="text-xl font-semibold mb-4">{tool.title}</h3>
                    <p className="mb-4">{tool.description}</p>
                    {tool.code && (
                      <pre className="bg-[#111111] p-4 rounded-lg overflow-x-auto">
                        <code>{tool.code}</code>
                      </pre>
                    )}
                  </div>
                ))}
              </div>
            );
          default:
            return null;
        }
      })}
    </article>
  );
}

export default BlogPost;
