import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';

function HeroSection() {
  const [glitchActive, setGlitchActive] = useState(false);

  useEffect(() => {
    const glitchInterval = setInterval(() => {
      setGlitchActive(true);
      setTimeout(() => setGlitchActive(false), 200);
    }, Math.random() * 4000 + 2000);

    return () => clearInterval(glitchInterval);
  }, []);

  const skills = [
    "VFX Artist",
    "Motion Designer",
    "Technical Director",
    "Creative Coder"
  ];

  return (
    <div className="relative min-h-screen flex items-center overflow-hidden">
      {/* Animated background grid */}
      <div className="absolute inset-0">
        <div className="grid grid-cols-12 gap-4 h-full opacity-20">
          {Array.from({ length: 48 }).map((_, i) => (
            <motion.div
              key={i}
              className="w-2 h-2 bg-purple-500 rounded-full"
              initial={{ opacity: 0 }}
              animate={{ 
                opacity: [0, 1, 0],
                scale: [1, 1.2, 1]
              }}
              transition={{
                duration: 4,
                repeat: Infinity,
                delay: i * 0.1,
              }}
            />
          ))}
        </div>
      </div>

      {/* Glitch effect overlay */}
      <AnimatePresence>
        {glitchActive && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 0.1 }}
            exit={{ opacity: 0 }}
            className="absolute inset-0 bg-gradient-to-r from-purple-500/20 to-cyan-500/20 
                     mix-blend-screen pointer-events-none"
            style={{
              clipPath: 'polygon(0 0, 100% 0, 100% 100%, 0 100%)',
              animation: 'glitch 0.2s infinite'
            }}
          />
        )}
      </AnimatePresence>

      <div className="relative container mx-auto px-4">
        <motion.div
          className="max-w-4xl mx-auto text-center"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8 }}
        >
          {/* Skills list */}
          <motion.div
            className="flex flex-wrap justify-center gap-4 mb-8 text-sm font-medium"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
          >
            {skills.map((skill, index) => (
              <span
                key={skill}
                className={`px-4 py-2 rounded-full ${
                  glitchActive 
                    ? 'bg-purple-500/20 text-white' 
                    : 'bg-white/5 text-purple-400'
                } transition-colors`}
              >
                {skill}
              </span>
            ))}
          </motion.div>

          {/* Main heading */}
          <motion.h1 
            className={`text-6xl md:text-7xl font-bold mb-6 ${
              glitchActive ? 'glitch-text' : ''
            }`}
            data-text="Digital Art & VFX"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.4 }}
          >
            Digital Art & VFX
          </motion.h1>

          <motion.p
            className="text-xl text-gray-300 mb-12 max-w-2xl mx-auto"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.6 }}
          >
            Creating immersive visual experiences through advanced compositing,
            creative coding, and artistic innovation
          </motion.p>

          <motion.div
            className="flex flex-wrap justify-center gap-6"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.8 }}
          >
            <Link 
              to="/portfolio" 
              className="px-8 py-4 bg-white text-black rounded-full font-medium
                       hover:bg-gray-100 transition-all transform hover:scale-105"
            >
              View Portfolio
            </Link>
            <Link 
              to="/shop" 
              className="px-8 py-4 border border-white/20 rounded-full
                       hover:bg-white/10 transition-all"
            >
              Shop Assets
            </Link>
          </motion.div>
        </motion.div>
      </div>
    </div>
  );
}

export default HeroSection;
