import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { blogPosts } from '../data/blogPosts';

function Blog() {
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="max-w-4xl mx-auto px-4 py-24"
    >
      <h1 className="text-4xl font-bold mb-12">Blog Posts</h1>

      <div className="space-y-8">
        {Object.values(blogPosts).map((post) => (
          <Link 
            key={post.id}
            to={`/blog/${post.id}`}
            className="block bg-[#1A1A1A] rounded-xl p-6 hover:bg-[#222] transition-colors"
          >
            <div className="mb-2 text-purple-400">{post.date}</div>
            <h2 className="text-2xl font-bold mb-3">{post.title}</h2>
            <div className="flex flex-wrap gap-2">
              {post.tags.map((tag, index) => (
                <span 
                  key={index}
                  className="text-sm text-gray-400"
                >
                  #{tag}
                </span>
              ))}
            </div>
          </Link>
        ))}
      </div>
    </motion.div>
  );
}

export default Blog;
