const ProjectCard = ({
  title,
  description,
  duration,
  details,
  technologies,
  media,
  mediaType,
  link,
  className = '',
  icon: Icon,
  variant = 'default',
  videoUrl,
  category
}) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [showGallery, setShowGallery] = useState(false);
  const [showVideo, setShowVideo] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [ref, isInView] = useInView({ threshold: 0.2 });
  const controls = useAnimation();

  useEffect(() => {
    if (isInView) {
      controls.start({ opacity: 1, y: 0 });
    }
  }, [isInView, controls]);

  const variants = {
    default: {
      background: 'bg-white',
      text: 'text-gray-900',
      hover: 'hover:shadow-lg hover:border-gray-300'
    },
    featured: {
      background: 'bg-gradient-to-br from-gray-900 to-gray-800',
      text: 'text-white',
      hover: 'hover:shadow-xl hover:shadow-gray-900/20'
    },
    outline: {
      background: 'bg-white',
      text: 'text-gray-900',
      hover: 'hover:border-black'
    }
  };

  const currentVariant = variants[variant];

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      animate={controls}
      whileHover={{ y: -5 }}
      transition={{ duration: 0.3 }}
      className={`
        rounded-xl overflow-hidden border transition-all duration-300
        ${currentVariant.background} ${currentVariant.hover} ${className}
      `}
    >
      <div className="p-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            {Icon && (
              <div className={`p-2 rounded-lg ${
                variant === 'featured' ? 'bg-white/10' : 'bg-gray-100'
              }`}>
                <Icon 
                  size={24} 
                  className={variant === 'featured' ? 'text-white' : 'text-gray-600'} 
                />
              </div>
            )}
            <div>
              <h3 className={`text-xl font-semibold ${currentVariant.text}`}>
                {title}
              </h3>
              {duration && (
                <p className={`text-sm mt-1 ${
                  variant === 'featured' ? 'text-white/60' : 'text-gray-500'
                }`}>
                  {duration}
                </p>
              )}
            </div>
          </div>

          <div className="flex items-center space-x-2">
            {category && (
              <span className={`px-3 py-1 rounded-full text-xs font-medium ${
                variant === 'featured' 
                  ? 'bg-white/10 text-white' 
                  : 'bg-gray-100 text-gray-600'
              }`}>
                {category}
              </span>
            )}
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={() => setIsExpanded(!isExpanded)}
              className={variant === 'featured' ? 'text-white/80' : 'text-gray-500'}
            >
              {isExpanded ? (
                <Minimize size={20} />
              ) : (
                <Maximize size={20} />
              )}
            </motion.button>
          </div>
        </div>

        {/* Main Content */}
        <div className={`mt-4 ${
          variant === 'featured' ? 'text-white/80' : 'text-gray-600'
        }`}>
          {description}
        </div>

        {/* Technologies */}
        {technologies && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.2 }}
            className="flex flex-wrap gap-2 mt-4"
          >
            {technologies.map((tech, index) => (
              <span
                key={index}
                className={`px-3 py-1 rounded-full text-sm ${
                  variant === 'featured'
                    ? 'bg-white/10 text-white'
                    : 'bg-gray-100 text-gray-700'
                }`}
              >
                {tech}
              </span>
            ))}
          </motion.div>
        )}

        {/* Expanded Content */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3 }}
              className="mt-6"
            >
              {/* Details */}
              {details && (
                <div className={`prose prose-sm max-w-none ${
                  variant === 'featured' ? 'text-white/80' : 'text-gray-700'
                }`}>
                  {details}
                </div>
              )}

              {/* Media Grid */}
              {media && mediaType === 'image' && (
                <motion.div 
                  variants={containerAnimation}
                  initial="hidden"
                  animate="show"
                  className="grid grid-cols-2 gap-4 mt-4"
                >
                  {media.map((src, index) => (
                    <motion.div
                      key={index}
                      variants={itemAnimation}
                      whileHover={{ scale: 1.05 }}
                      className="relative group cursor-pointer"
                      onClick={() => {
                        setCurrentImageIndex(index);
                        setShowGallery(true);
                      }}
                    >
                      <img
                        src={src}
                        alt={`${title} ${index + 1}`}
                        className="w-full h-48 object-cover rounded-lg"
                      />
