                  whileHover={{ x: 5 }}
                >
                  <span>View Project</span>
                  <ExternalLink size={16} />
                </motion.a>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </div>

      {/* Modals */}
      <AnimatePresence>
        {showGallery && (
          <ImageGalleryModal
            images={media}
            currentIndex={currentImageIndex}
            setCurrentIndex={setCurrentImageIndex}
            onClose={() => setShowGallery(false)}
            title={title}
            description={description}
          />
        )}
        {showVideo && (
          <VideoModal
            url={videoUrl}
            title={title}
            onClose={() => setShowVideo(false)}
          />
        )}
      </AnimatePresence>
    </motion.div>
  );
};

